import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  Image,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

const HomeScreen = ({ navigation }) => {
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');
  const { theme } = useTheme();
  const { t, language } = useLanguage();

  const [activeTab, setActiveTab] = useState('add');
  const [isFilterOpen, setIsFilterOpen] = useState(false);

  // Feed data with disaster-related content
  const [feedData] = useState([
    {
      id: 1,
      user: {
        name: language === 'tr' ? 'AFAD Türkiye' : 'AFAD Turkey',
        username: '@afadturkiye',
        avatar: 'https://via.placeholder.com/50x50/FF6B35/FFFFFF?text=AFAD',
        verified: true
      },
      content: {
        text: language === 'tr' 
          ? 'Deprem konusunda yapay zekanın sağlık alanındaki etik sonuçları nelerdir?' 
          : 'What are the ethical implications of AI in earthquake healthcare?',
        description: language === 'tr'
          ? 'Yapay zeka (AI), 21. yüzyılın en dönüştürücü teknolojilerinden biri haline geldi...'
          : 'Artificial Intelligence (AI) has become one of the most transformative technologies of the 21st century...',
        image: 'https://via.placeholder.com/400x200/FF6B35/FFFFFF?text=Deprem+AI',
        date: language === 'tr' ? 'Çarşamba, 1 Ocak 2025' : 'Wednesday, January 1, 2025'
      },
      stats: {
        likes: 730,
        views: '2.1k',
        comments: 875
      }
    },
    {
      id: 2,
      user: {
        name: language === 'tr' ? 'Meteoroloji Genel Müdürlüğü' : 'Turkish State Meteorological Service',
        username: '@meteoroloji',
        avatar: 'https://via.placeholder.com/50x50/4A90E2/FFFFFF?text=MET',
        verified: true
      },
      content: {
        text: language === 'tr' 
          ? 'Sel felaketlerinde yapay zekanın sağlık alanındaki etik sonuçları nelerdir?' 
          : 'What are the ethical implications of AI in flood healthcare?',
        description: language === 'tr'
          ? 'Yapay zeka (AI), 21. yüzyılın en dönüştürücü teknolojilerinden biri haline geldi...'
          : 'Artificial Intelligence (AI) has become one of the most transformative technologies of the 21st century...',
        image: 'https://via.placeholder.com/400x200/4A90E2/FFFFFF?text=Sel+AI',
        date: language === 'tr' ? 'Çarşamba, 1 Ocak 2025' : 'Wednesday, January 1, 2025'
      },
      stats: {
        likes: 730,
        views: '2.1k',
        comments: 876
      }
    },
    {
      id: 3,
      user: {
        name: language === 'tr' ? 'Orman Genel Müdürlüğü' : 'General Directorate of Forestry',
        username: '@ormangenel',
        avatar: 'https://via.placeholder.com/50x50/FF3B30/FFFFFF?text=OGM',
        verified: true
      },
      content: {
        text: language === 'tr' 
          ? 'Orman yangınlarında yapay zekanın sağlık alanındaki etik sonuçları nelerdir?' 
          : 'What are the ethical implications of AI in wildfire healthcare?',
        description: language === 'tr'
          ? 'Yapay zeka (AI), 21. yüzyılın en dönüştürücü teknolojilerinden biri haline geldi...'
          : 'Artificial Intelligence (AI) has become one of the most transformative technologies of the 21st century...',
        image: 'https://via.placeholder.com/400x200/FF3B30/FFFFFF?text=Yangın+AI',
        date: language === 'tr' ? 'Çarşamba, 1 Ocak 2025' : 'Wednesday, January 1, 2025'
      },
      stats: {
        likes: 730,
        views: '2.1k',
        comments: 876
      }
    },
    {
      id: 4,
      user: {
        name: language === 'tr' ? 'Jeoloji Mühendisleri Odası' : 'Chamber of Geological Engineers',
        username: '@jmo_turkiye',
        avatar: 'https://via.placeholder.com/50x50/8B4513/FFFFFF?text=JMO',
        verified: true
      },
      content: {
        text: language === 'tr' 
          ? 'Heyelan risklerinde yapay zekanın sağlık alanındaki etik sonuçları nelerdir?' 
          : 'What are the ethical implications of AI in landslide healthcare?',
        description: language === 'tr'
          ? 'Yapay zeka (AI), 21. yüzyılın en dönüştürücü teknolojilerinden biri haline geldi...'
          : 'Artificial Intelligence (AI) has become one of the most transformative technologies of the 21st century...',
        image: 'https://via.placeholder.com/400x200/8B4513/FFFFFF?text=Heyelan+AI',
        date: language === 'tr' ? 'Çarşamba, 1 Ocak 2025' : 'Wednesday, January 1, 2025'
      },
      stats: {
        likes: 730,
        views: '2.1k',
        comments: 876
      }
    }
  ]);

  const tabs = [
    { id: 'popular', title: language === 'tr' ? 'Popüler' : 'Popular', active: true },
    { id: 'trending', title: language === 'tr' ? 'Trend' : 'Trending' },
    { id: 'latest', title: language === 'tr' ? 'En Son' : 'Latest' },
    { id: 'following', title: language === 'tr' ? 'Takip' : 'Following' }
  ];

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: theme.colors.background,
    },
    header: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      paddingHorizontal: 16,
      paddingVertical: 12,
      backgroundColor: theme.colors.background,
      borderBottomWidth: 1,
      borderBottomColor: theme.colors.border,
    },
    headerTitle: {
      fontSize: 18,
      fontWeight: '600',
      color: theme.colors.text,
    },
    headerIcons: {
      flexDirection: 'row',
      alignItems: 'center',
    },
    headerIcon: {
      marginLeft: 16,
      padding: 4,
    },
    tabContainer: {
      flexDirection: 'row',
      paddingHorizontal: 16,
      paddingVertical: 12,
      backgroundColor: theme.colors.background,
      borderBottomWidth: 1,
      borderBottomColor: theme.colors.border,
    },
    tabButton: {
      paddingHorizontal: 16,
      paddingVertical: 8,
      marginRight: 8,
      borderRadius: 20,
      backgroundColor: 'transparent',
      flexDirection: 'row',
      alignItems: 'center',
    },
    activeTabButton: {
      backgroundColor: theme.colors.primary,
    },
    tabText: {
      fontSize: 14,
      fontWeight: '500',
      color: theme.colors.secondaryText,
      marginLeft: 4,
    },
    activeTabText: {
      color: '#FFFFFF',
    },
    scrollView: {
      flex: 1,
