import axios from 'axios';

const AFAD_API_BASE_URL = 'https://deprem.afad.gov.tr';
const AFAD_EVENT_FILTER_URL = 'https://deprem.afad.gov.tr/EventData/GetEventsByFilter';
const KANDILLI_API_URL = 'http://www.koeri.boun.edu.tr/scripts/lst0.asp';
const KANDILLI_JSON_URL = 'http://www.koeri.boun.edu.tr/scripts/lasteq.asp';
const TADAS_AFAD_URL = 'https://tadas.afad.gov.tr/api/earthquakes';

class EarthquakeService {
  constructor() {
    this.apiClient = axios.create({
      baseURL: AFAD_API_BASE_URL,
      timeout: 15000,
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
      },
    });
  }

  /**
   * AFAD API için tarih formatı: YYYY-MM-DD HH:mm:ss
   */
  formatDate(date) {
    return date.getFullYear() + '-' + 
           String(date.getMonth() + 1).padStart(2, '0') + '-' + 
           String(date.getDate()).padStart(2, '0') + ' ' + 
           String(date.getHours()).padStart(2, '0') + ':' + 
           String(date.getMinutes()).padStart(2, '0') + ':' + 
           String(date.getSeconds()).padStart(2, '0');
  }

  /**
   * Belirli tarih aralığındaki deprem verilerini getirir
   * @param {Date} startDate - Başlangıç tarihi
   * @param {Date} endDate - Bitiş tarihi
   * @param {number} skip - Atlanacak kayıt sayısı (pagination için)
   * @param {number} take - Alınacak kayıt sayısı
   * @param {number} minMagnitude - Minimum büyüklük
   * @returns {Promise<Array>} Deprem verileri dizisi
   */
  async getEarthquakesByDateRange(startDate, endDate, skip = 0, take = 100, minMagnitude = 0) {
    try {
      const eventFilter = {
        EventSearchFilterList: [
          { FilterType: 8, Value: startDate.toISOString() },
          { FilterType: 9, Value: endDate.toISOString() }
        ],
        Skip: skip,
        Take: take,
        SortDescriptor: { field: 'eventDate', dir: 'desc' }
      };

      const response = await axios.post(AFAD_EVENT_FILTER_URL, eventFilter, {
        timeout: 15000,
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
      });

      if (response.data && response.data.Data && Array.isArray(response.data.Data)) {
        console.log('AFAD API\'sinden başarıyla veri alındı:', response.data.Data.length, 'deprem');

        // Minimum büyüklük filtresi uygula
        const filteredData = response.data.Data.filter(event => 
          parseFloat(event.Magnitude) >= minMagnitude
        );

        return {
          data: this.formatNewEarthquakeData(filteredData),
          totalCount: response.data.TotalCount || filteredData.length,
          hasMore: (skip + take) < (response.data.TotalCount || 0)
        };
      }

      throw new Error('API\'den veri alınamadı');
    } catch (error) {
      console.error('AFAD API bağlantı hatası:', error.message);
      throw new Error('Veriler çekilemiyor. Lütfen internet bağlantınızı kontrol edin ve tekrar deneyin.');
    }
  }

  /**
   * Kandilli Rasathanesi'nden deprem verilerini çeker
   * @param {number} limit - Getirilecek deprem sayısı
   * @returns {Promise<Array>} Deprem verileri dizisi
   */
  async getKandilliEarthquakes(limit = 50) {
    try {
      console.log('Kandilli Rasathanesi API\'sinden veri çekiliyor...');

      // Kandilli API'sinden HTML formatında veri çek
      const response = await axios.get(KANDILLI_API_URL, {
        timeout: 10000,
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
          'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8'
        }
      });

      if (response.data) {
        const earthquakes = this.parseKandilliData(response.data, limit);
        console.log('Kandilli API\'sinden başarıyla veri alındı:', earthquakes.length, 'deprem');
        return earthquakes;
      }

      throw new Error('Kandilli API\'den veri alınamadı');
    } catch (error) {
      console.error('Kandilli API bağlantı hatası:', error.message);
      throw error;
    }
  }

  /**
   * Kandilli HTML verisini parse eder
   * @param {string} htmlData - HTML formatındaki veri
   * @param {number} limit - Maksimum deprem sayısı
   * @returns {Array} Formatlanmış deprem verileri
   */
  parseKandilliData(htmlData, limit = 50) {
    try {
      const earthquakes = [];
      const lines = htmlData.split('\n');
      let dataStarted = false;

      for (let i = 0; i < lines.length && earthquakes.length < limit; i++) {
        const line = lines[i].trim();

        // Veri başlangıcını bul
        if (line.includes('Tarih      Saat      Enlem(N)  Boylam(E)')) {
          dataStarted = true;
          continue;
        }

        if (!dataStarted || line.length < 50) continue;

        // Veri satırını parse et
        const match = line.match(/(\d{4}\.\d{2}\.\d{2})\s+(\d{2}:\d{2}:\d{2})\s+([\d\.]+)\s+([\d\.]+)\s+([\d\.]+)\s+([\d\.-]+)\s+([\d\.-]+)\s+([\d\.-]+)\s+(.+?)\s+(\w+)$/);

        if (match) {
          const [, date, time, latitude, longitude, depth, md, ml, mw, location, quality] = match;

          // Büyüklük değerini belirle (ML > MW > MD önceliği)
          let magnitude = 0;
          if (ml && ml !== '-.-' && parseFloat(ml) > 0) {
            magnitude = parseFloat(ml);
          } else if (mw && mw !== '-.-' && parseFloat(mw) > 0) {
            magnitude = parseFloat(mw);
          } else if (md && md !== '-.-' && parseFloat(md) > 0) {
            magnitude = parseFloat(md);
          }

          if (magnitude > 0) {
            const eventTime = new Date(`${date.replace(/\./g, '-')}T${time}`);

            earthquakes.push({
              id: `kandilli_${date}_${time}_${latitude}_${longitude}`,
              magnitude: magnitude,
              location: location.trim(),
              depth: parseFloat(depth) || 0,
              time: eventTime,
              coordinates: {
                latitude: parseFloat(latitude),
                longitude: parseFloat(longitude)
              },
              latitude: parseFloat(latitude),
              longitude: parseFloat(longitude),
              source: 'Kandilli Rasathanesi',
              severity: this.calculateSeverity(magnitude),
              distance: this.formatTimeAgo(eventTime),
              region: location.trim(),
              color: this.getSeverityColor(magnitude),
              isAftershock: magnitude < 4.0,
              type: magnitude < 4.0 ? 'Artçı Deprem' : 'Ana Deprem',
              quality: quality,
              formattedTime: eventTime.toLocaleString('tr-TR', {
                year: 'numeric',
                month: '2-digit',
                day: '2-digit',
                hour: '2-digit',
                minute: '2-digit',
                second: '2-digit'
              }),
              formattedDate: eventTime.toLocaleDateString('tr-TR', {
                year: 'numeric',
                month: 'long',
                day: 'numeric'
              })
            });
          }
        }
      }

      return earthquakes;
    } catch (error) {
      console.error('Kandilli veri parse hatası:', error);
      return [];
    }
  }

  /**
   * Son deprem verilerini getirir (Kandilli öncelikli, AFAD fallback)
   * @param {number} limit - Getirilecek deprem sayısı (varsayılan: 50)
   * @param {number} minMagnitude - Minimum büyüklük (varsayılan: 0)
   * @returns {Promise<Array>} Deprem verileri dizisi
   */
  async getLatestEarthquakes(limit = 50, minMagnitude = 0) {
    try {
      // Önce Kandilli Rasathanesi'nden dene
      const kandilliData = await this.getKandilliEarthquakes(limit);

      if (kandilliData && kandilliData.length > 0) {
        // Minimum büyüklük filtresi uygula
        const filteredData = kandilliData.filter(earthquake => 
          earthquake.magnitude >= minMagnitude
        );

        if (filteredData.length > 0) {
          console.log('Kandilli Rasathanesi verisi kullanılıyor:', filteredData.length, 'deprem');
          return filteredData;
        }
      }

      console.log('Kandilli verisi yetersiz, AFAD API\'sine geçiliyor...');

      // Kandilli başarısız olursa AFAD API'sini kullan
      const endDate = new Date();
      const startDate = new Date(endDate.getTime() - (24 * 60 * 60 * 1000)); // 1 gün öncesi

      const result = await this.getEarthquakesByDateRange(startDate, endDate, 0, limit, minMagnitude);
      console.log('AFAD API verisi kullanılıyor:', result.data.length, 'deprem');
      return result.data;

    } catch (error) {
      console.error('Tüm API\'ler başarısız:', error.message);

      // Gerçek API hatası durumunda hata fırlat
      throw new Error('Veriler çekilemiyor. Lütfen internet bağlantınızı kontrol edin ve tekrar deneyin.');
    }
  }

