import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  ScrollView,
  TextInput,
  TouchableOpacity,
  Alert,
  Dimensions,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useLanguage } from '../contexts/LanguageContext';

const RasathaneScreen = () => {
  const { t } = useLanguage();
  const [searchText, setSearchText] = useState('');
  const [selectedFilter, setSelectedFilter] = useState('all');
  const [isFilterPanelOpen, setIsFilterPanelOpen] = useState(false);
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');

  // Örnek deprem verileri
  const earthquakeData = [
    {
      id: 1,
      magnitude: 4.2,
      location: 'Şındırgı - Balıkesir',
      distance: '12.4 km',
      time: '17 dk önce',
      source: 'Kandilli',
      color: '#FFD60A',
      severity: 'Orta'
    },
    {
      id: 2,
      magnitude: 2.4,
      location: 'Merkez - Malatya',
      distance: '8.7 km',
      time: '32 dk önce',
      source: 'AFAD',
      color: '#40E0D0',
      severity: 'Düşük'
    },
    {
      id: 3,
      magnitude: 5.8,
      location: 'Biga - Çanakkale',
      distance: '15.2 km',
      time: '1 sa 15 dk önce',
      source: 'Kandilli',
      color: '#FF6B6B',
      severity: 'Büyük'
    },
    {
      id: 4,
      magnitude: 3.1,
      location: 'Manavgat - Antalya',
      distance: '2 sa 45 dk önce',
      time: '2 sa 45 dk önce',
      source: 'İtfaiye',
      color: '#98FB98',
      severity: 'Düşük'
    },
    {
      id: 5,
      magnitude: 3.1,
      location: 'Gönen - Balıkesir',
      distance: '9.8 km',
      time: '3 sa 12 dk önce',
      source: 'AFAD',
      color: '#98FB98',
      severity: 'Düşük'
    },
    {
      id: 6,
      magnitude: 2.8,
      location: 'Merkez - Samsun',
      distance: '4 sa 30 dk önce',
      time: '4 sa 30 dk önce',
      source: 'Meteoroloji',
      color: '#FFD60A',
      severity: 'Orta'
    }
  ];

  const filteredData = earthquakeData.filter(item => {
    const matchesSearch = item.location.toLowerCase().includes(searchText.toLowerCase());
    if (selectedFilter === 'all') return matchesSearch;
    if (selectedFilter === 'high') return matchesSearch && item.magnitude >= 5.0;
    if (selectedFilter === 'medium') return matchesSearch && item.magnitude >= 3.0 && item.magnitude < 5.0;
    if (selectedFilter === 'low') return matchesSearch && item.magnitude < 3.0;
    return matchesSearch;
  });

  const handleEarthquakePress = (earthquake) => {
    Alert.alert(
      `${earthquake.magnitude} ${t('magnitude')} ${t('earthquake')}`,
      `${t('location')}: ${earthquake.location}\n${t('distance')}: ${earthquake.distance}\n${t('time')}: ${earthquake.time}\n${t('source')}: ${earthquake.source}\n${t('severity')}: ${earthquake.severity}`,
      [
        { text: t('ok'), style: 'default' },
        { text: t('details'), style: 'default', onPress: () => showDetails(earthquake) }
      ]
    );
  };

  const showDetails = (earthquake) => {
    Alert.alert(
      'Deprem Detayları',
      `Büyüklük: ${earthquake.magnitude}\nKonum: ${earthquake.location}\nUzaklık: ${earthquake.distance}\nZaman: ${earthquake.time}\nVeri Kaynağı: ${earthquake.source}\nŞiddet Seviyesi: ${earthquake.severity}\n\nBu deprem hakkında daha fazla bilgi için yerel otoritelere başvurun.`,
      [{ text: 'Tamam' }]
    );
  };

  return (
    <View style={[styles.container, { paddingTop: insets.top }]}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity style={styles.backButton}>
          <Ionicons name="chevron-back" size={24} color="#000" />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>{t('rasathane')}</Text>
        <View style={styles.headerActions}>
          <TouchableOpacity 
            style={styles.filterButton}
            onPress={() => setIsFilterPanelOpen(!isFilterPanelOpen)}
          >
            <Ionicons name="options" size={24} color="#000" />
          </TouchableOpacity>
          <TouchableOpacity style={styles.searchButton}>
            <Ionicons name="search" size={24} color="#000" />
          </TouchableOpacity>
        </View>
      </View>

      {/* Search Bar */}
      <View style={styles.searchContainer}>
        <Ionicons name="search" size={20} color="#8E8E93" style={styles.searchIcon} />
        <TextInput
          style={styles.searchInput}
          placeholder={t('searchLocation')}
          value={searchText}
          onChangeText={setSearchText}
          placeholderTextColor="#8E8E93"
        />
      </View>

      {/* Compact Filter Panel */}
      {isFilterPanelOpen && (
        <View style={styles.compactFilterPanel}>
          <View style={styles.compactFilterHeader}>
            <Text style={styles.compactFilterTitle}>Filtreler</Text>
            <TouchableOpacity 
              onPress={() => setIsFilterPanelOpen(false)}
              style={styles.compactCloseButton}
            >
              <Ionicons name="close" size={18} color="#8E8E93" />
            </TouchableOpacity>
          </View>

          <View style={styles.compactFilterSection}>
            <Text style={styles.compactSectionTitle}>Büyüklük</Text>
            <View style={styles.compactChipContainer}>
              <TouchableOpacity 
                style={[styles.compactFilterChip, selectedFilter === 'all' && styles.compactFilterChipActive]}
                onPress={() => setSelectedFilter('all')}
              >
                <Text style={[styles.compactFilterText, selectedFilter === 'all' && styles.compactFilterTextActive]}>Tümü</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.compactFilterChip, selectedFilter === 'high' && styles.compactFilterChipActive]}
                onPress={() => setSelectedFilter('high')}
              >
                <Text style={[styles.compactFilterText, selectedFilter === 'high' && styles.compactFilterTextActive]}>5.0+</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.compactFilterChip, selectedFilter === 'medium' && styles.compactFilterChipActive]}
                onPress={() => setSelectedFilter('medium')}
              >
                <Text style={[styles.compactFilterText, selectedFilter === 'medium' && styles.compactFilterTextActive]}>3.0-4.9</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.compactFilterChip, selectedFilter === 'low' && styles.compactFilterChipActive]}
                onPress={() => setSelectedFilter('low')}
              >
                <Text style={[styles.compactFilterText, selectedFilter === 'low' && styles.compactFilterTextActive]}>&lt;3.0</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      )}

      {/* Earthquake List */}
      <ScrollView style={styles.listContainer} showsVerticalScrollIndicator={false}>
        {filteredData.map((earthquake) => (
          <TouchableOpacity 
            key={earthquake.id} 
            style={styles.earthquakeCard}
            onPress={() => handleEarthquakePress(earthquake)}
            activeOpacity={0.7}
          >
            <View style={styles.cardLeft}>
              <View style={[styles.magnitudeCircle, { backgroundColor: earthquake.color }]}>
                <Text style={styles.magnitudeText}>{earthquake.magnitude}</Text>
              </View>
              <View style={styles.cardInfo}>
                <Text style={styles.locationText}>{earthquake.location}</Text>
                <View style={styles.cardDetails}>
                  <Text style={styles.distanceText}>{earthquake.distance}</Text>
                  <Text style={styles.timeText}>{earthquake.time}</Text>
                  <Text style={styles.sourceText}>{earthquake.source}</Text>
                </View>
              </View>
            </View>
            <Ionicons name="chevron-forward" size={20} color="#C7C7CC" />
          </TouchableOpacity>
        ))}
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F2F2F7',
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    paddingHorizontal: 16,
    paddingVertical: 12,
    backgroundColor: '#FFFFFF',
    borderBottomWidth: 1,
    borderBottomColor: '#E5E5EA',
  },
  backButton: {
    padding: 8,
  },
  headerTitle: {
    fontSize: 18,
    fontWeight: '600',
    color: '#000000',
  },
  headerActions: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  filterButton: {
    padding: 8,
    marginRight: 8,
  },
  searchButton: {
    padding: 8,
  },
  searchContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#FFFFFF',
    marginHorizontal: 16,
    marginVertical: 12,
    paddingHorizontal: 12,
    paddingVertical: 10,
    borderRadius: 10,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.1,
    shadowRadius: 2,
    elevation: 2,
  },
  searchIcon: {
    marginRight: 8,
  },
