import React, { useState, useEffect, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  TouchableOpacity,
  ScrollView,
  Platform,
  ActivityIndicator,
  Alert,
  Animated,
  PanResponder,
  Dimensions,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useNavigation, useRoute } from '@react-navigation/native';
import { useLanguage } from '../contexts/LanguageContext';
import { useTheme } from '../contexts/ThemeContext';
import earthquakeService from '../services/earthquakeService';

// Web için Leaflet import'ları - sadece web platformunda
let MapContainer, TileLayer, Marker, Popup, L;
if (Platform.OS === 'web') {
  const leaflet = require('react-leaflet');
  MapContainer = leaflet.MapContainer;
  TileLayer = leaflet.TileLayer;
  Marker = leaflet.Marker;
  Popup = leaflet.Popup;
  L = require('leaflet');

  // CSS'i dinamik olarak yükle
  if (typeof document !== 'undefined') {
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = 'https://unpkg.com/leaflet@1.7.1/dist/leaflet.css';
    document.head.appendChild(link);
  }

  // Leaflet marker icon'larını düzelt
  if (L && L.Icon && L.Icon.Default) {
    delete L.Icon.Default.prototype._getIconUrl;
    L.Icon.Default.mergeOptions({
      iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
      iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
      shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
    });
  }
}

const { height: screenHeight } = Dimensions.get('window');

const EarthquakeDetailScreen = () => {
  const { t } = useLanguage();
  const { theme } = useTheme();
  const navigation = useNavigation();
  const route = useRoute();
  const insets = useSafeAreaInsets();

  const { earthquake } = route.params;
  const [loading, setLoading] = useState(false);
  const [mapCenter, setMapCenter] = useState([earthquake.latitude || 39.9334, earthquake.longitude || 32.8597]);

  // Drawer animation values
  const drawerHeight = useRef(new Animated.Value(screenHeight * 0.6)).current;
  const mapHeight = useRef(new Animated.Value(screenHeight * 0.4)).current;
  const [isDragging, setIsDragging] = useState(false);

  useEffect(() => {
    if (earthquake.latitude && earthquake.longitude) {
      setMapCenter([earthquake.latitude, earthquake.longitude]);
    }
  }, [earthquake]);

  // Pan responder for drawer
  const panResponder = useRef(
    PanResponder.create({
      onMoveShouldSetPanResponder: (evt, gestureState) => {
        return Math.abs(gestureState.dy) > 10;
      },
      onPanResponderGrant: () => {
        setIsDragging(true);
      },
      onPanResponderMove: (evt, gestureState) => {
        const newDrawerHeight = screenHeight * 0.6 - gestureState.dy;
        const newMapHeight = screenHeight * 0.4 + gestureState.dy;

        // Limit the drawer height between 20% and 80% of screen
        const minDrawerHeight = screenHeight * 0.2;
        const maxDrawerHeight = screenHeight * 0.8;

        if (newDrawerHeight >= minDrawerHeight && newDrawerHeight <= maxDrawerHeight) {
          drawerHeight.setValue(newDrawerHeight);
          mapHeight.setValue(newMapHeight);
        }
      },
      onPanResponderRelease: (evt, gestureState) => {
        setIsDragging(false);

        const velocity = gestureState.vy;
        const currentHeight = screenHeight * 0.6 - gestureState.dy;

        // Snap to positions based on velocity and current position
        let targetDrawerHeight, targetMapHeight;

        if (velocity > 0.5 || currentHeight < screenHeight * 0.4) {
          // Snap to minimized state
          targetDrawerHeight = screenHeight * 0.3;
          targetMapHeight = screenHeight * 0.7;
        } else if (velocity < -0.5 || currentHeight > screenHeight * 0.6) {
          // Snap to maximized state
          targetDrawerHeight = screenHeight * 0.7;
          targetMapHeight = screenHeight * 0.3;
        } else {
          // Snap to default state
          targetDrawerHeight = screenHeight * 0.6;
          targetMapHeight = screenHeight * 0.4;
        }

        Animated.parallel([
          Animated.spring(drawerHeight, {
            toValue: targetDrawerHeight,
            useNativeDriver: false,
            tension: 100,
            friction: 8,
          }),
          Animated.spring(mapHeight, {
            toValue: targetMapHeight,
            useNativeDriver: false,
            tension: 100,
            friction: 8,
          }),
        ]).start();
      },
    })
  ).current;

  const renderMap = () => {
    if (Platform.OS === 'web' && MapContainer) {
      return (
        <MapContainer
          center={mapCenter}
          zoom={10}
          style={{ height: '100%', width: '100%' }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          {earthquake.latitude && earthquake.longitude && (
            <Marker position={[earthquake.latitude, earthquake.longitude]}>
              <Popup>
                <div>
                  <strong>{earthquake.magnitude} Büyüklüğünde Deprem</strong><br/>
                  {earthquake.location}<br/>
                  Derinlik: {earthquake.depth} km
                </div>
              </Popup>
            </Marker>
          )}
        </MapContainer>
      );
    } else {
      // Mobile için placeholder
      return (
        <View style={styles.mapPlaceholder}>
          <Ionicons name="map-outline" size={64} color={theme.colors.secondaryText} />
          <Text style={[styles.mapPlaceholderText, { color: theme.colors.secondaryText }]}>
            Deprem konumu haritası
          </Text>
        </View>
      );
    }
  };

  const getSeverityColor = (magnitude) => {
    if (magnitude >= 7.0) return '#8B0000'; // Çok yüksek - koyu kırmızı
    if (magnitude >= 6.0) return '#FF0000'; // Yüksek - kırmızı
    if (magnitude >= 5.0) return '#FF4500'; // Orta-yüksek - turuncu kırmızı
    if (magnitude >= 4.0) return '#FF8C00'; // Orta - koyu turuncu
    if (magnitude >= 3.0) return '#FFA500'; // Düşük-orta - turuncu
    if (magnitude >= 2.0) return '#FFD700'; // Düşük - altın sarısı
    return '#90EE90'; // Çok düşük - açık yeşil
  };

  return (
    <SafeAreaView style={[styles.container, { backgroundColor: theme.colors.background }]}>
      {/* Header */}
      <View style={[styles.header, { backgroundColor: theme.colors.primary }]}>
        <TouchableOpacity 
          style={styles.backButton}
          onPress={() => navigation.goBack()}
        >
          <Ionicons name="arrow-back" size={24} color={theme.colors.onPrimary} />
        </TouchableOpacity>
        <Text style={[styles.headerTitle, { color: theme.colors.onPrimary }]}>Rasathane</Text>
        <TouchableOpacity style={styles.shareButton}>
          <Ionicons name="share-outline" size={24} color={theme.colors.onPrimary} />
        </TouchableOpacity>
      </View>

      <ScrollView style={styles.content} showsVerticalScrollIndicator={false}>
        {/* Map Section */}
        <Animated.View style={[styles.mapSection, { height: mapHeight }]}>
          <View style={styles.mapContainer}>
            {renderMap()}
          </View>
        </Animated.View>

        {/* Drawer Section */}
        <Animated.View 
          style={[
