import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  Image,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

const HomeScreen = ({ navigation }) => {
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');
  const { theme } = useTheme();
  const { t, language, forceUpdate } = useLanguage();

  // Force re-render when language changes
  useEffect(() => {
    // This effect will run whenever forceUpdate changes
    // causing the component to re-render with new language
  }, [forceUpdate]);

  // News data
  const [newsData] = useState([
    {
      id: 1,
      title: language === 'tr' ? 'Malatya\'da 5.2 büyüklüğünde deprem meydana geldi' : 'Magnitude 5.2 earthquake occurred in Malatya',
      source: 'AFAD',
      time: '2 saat önce',
      image: 'https://via.placeholder.com/300x200/FF6B35/FFFFFF?text=Deprem',
      category: language === 'tr' ? 'Deprem' : 'Earthquake'
    },
    {
      id: 2,
      title: language === 'tr' ? 'Antalya\'da sel felaketi: Birçok ev sular altında kaldı' : 'Flood disaster in Antalya: Many houses flooded',
      source: 'Meteoroloji',
      time: '4 saat önce',
      image: 'https://via.placeholder.com/300x200/4A90E2/FFFFFF?text=Sel',
      category: language === 'tr' ? 'Sel' : 'Flood'
    },
    {
      id: 3,
      title: language === 'tr' ? 'Muğla\'da orman yangını: Havadan müdahale sürüyor' : 'Forest fire in Muğla: Aerial intervention continues',
      source: 'Orman Bakanlığı',
      time: '6 saat önce',
      image: 'https://via.placeholder.com/300x200/FF3B30/FFFFFF?text=Yangın',
      category: language === 'tr' ? 'Yangın' : 'Fire'
    },
    {
      id: 4,
      title: language === 'tr' ? 'Rize\'de heyelan riski: Tahliye işlemleri başlatıldı' : 'Landslide risk in Rize: Evacuation operations started',
      source: 'Valilik',
      time: '8 saat önce',
      image: 'https://via.placeholder.com/300x200/8B4513/FFFFFF?text=Heyelan',
      category: language === 'tr' ? 'Heyelan' : 'Landslide'
    }
  ]);

  // For You data
  const [forYouData] = useState([
    {
      id: 1,
      title: language === 'tr' ? 'Deprem çantası nasıl hazırlanır? Uzmanlar açıklıyor' : 'How to prepare an earthquake bag? Experts explain',
      source: 'AFAD Eğitim',
      time: '1 gün önce',
      image: 'https://via.placeholder.com/80x80/FF6B35/FFFFFF?text=Eğitim',
      category: language === 'tr' ? 'Eğitim' : 'Education'
    },
    {
      id: 2,
      title: language === 'tr' ? 'Sel sularından korunma yöntemleri ve güvenlik önlemleri' : 'Flood protection methods and safety measures',
      source: 'Güvenlik',
      time: '2 gün önce',
      image: 'https://via.placeholder.com/80x80/4A90E2/FFFFFF?text=Güvenlik',
      category: language === 'tr' ? 'Güvenlik' : 'Safety'
    },
    {
      id: 3,
      title: language === 'tr' ? 'Yangın durumunda ilk yardım ve tahliye planları' : 'First aid and evacuation plans in case of fire',
      source: 'İtfaiye',
      time: '3 gün önce',
      image: 'https://via.placeholder.com/80x80/FF3B30/FFFFFF?text=İtfaiye',
      category: language === 'tr' ? 'İtfaiye' : 'Fire Department'
    },
    {
      id: 4,
      title: language === 'tr' ? 'Heyelan riskli bölgelerde yaşayanlar için önemli uyarılar' : 'Important warnings for residents in landslide-prone areas',
      source: 'Jeoloji',
      time: '4 gün önce',
      image: 'https://via.placeholder.com/80x80/8B4513/FFFFFF?text=Jeoloji',
      category: language === 'tr' ? 'Jeoloji' : 'Geology'
    },
    {
      id: 5,
      title: language === 'tr' ? 'Acil durum iletişim kanalları ve önemli telefon numaraları' : 'Emergency communication channels and important phone numbers',
      source: 'Acil Durum',
      time: '5 gün önce',
      image: 'https://via.placeholder.com/80x80/34C759/FFFFFF?text=Acil',
      category: language === 'tr' ? 'Acil Durum' : 'Emergency'
    }
  ]);

  // Logo brands data
  const logoData = [
    { id: 1, name: 'TEMPO', color: '#FF3B30' },
    { id: 2, name: 'd', color: '#007AFF', bgColor: '#E3F2FD' },
    { id: 3, name: 'detik', color: '#FFFFFF', bgColor: '#FF3B30' },
    { id: 4, name: 'beIN', color: '#007AFF', bgColor: '#E8F4FD' },
    { id: 5, name: 'CNN', color: '#FFFFFF', bgColor: '#FF3B30' },
    { id: 6, name: 'BBC', color: '#000000', bgColor: '#F0F0F0' }
  ];

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: theme.colors.background,
    },
    scrollView: {
      flex: 1,
    },
    header: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      paddingHorizontal: 16,
      paddingVertical: 12,
      backgroundColor: theme.colors.background,
    },
    headerTitle: {
      fontSize: 24,
      fontWeight: '700',
      color: theme.colors.text,
    },
    notificationButton: {
      padding: 8,
    },
    logoSection: {
      paddingHorizontal: 16,
      marginTop: 16,
    },
    logoContainer: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
    },
    logoItem: {
      width: 50,
      height: 50,
      borderRadius: 25,
      justifyContent: 'center',
      alignItems: 'center',
      borderWidth: 1,
      borderColor: theme.colors.border,
    },
    logoText: {
      fontSize: 12,
      fontWeight: '600',
    },
    section: {
      marginTop: 24,
      paddingHorizontal: 16,
    },
    sectionTitle: {
      fontSize: 18,
      fontWeight: '700',
      color: theme.colors.text,
      marginBottom: 16,
    },
    newsCard: {
      backgroundColor: theme.colors.cardBackground,
      borderRadius: 12,
      overflow: 'hidden',
      marginBottom: 16,
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 2,
      },
      shadowOpacity: 0.1,
      shadowRadius: 4,
      elevation: 3,
    },
    newsImage: {
      width: '100%',
      height: 200,
      backgroundColor: '#E0E0E0',
    },
    newsContent: {
      padding: 16,
    },
    newsTitle: {
      fontSize: 16,
      fontWeight: '600',
      color: theme.colors.text,
      lineHeight: 22,
      marginBottom: 8,
    },
    newsMeta: {
      flexDirection: 'row',
      alignItems: 'center',
    },
    newsSource: {
      fontSize: 12,
      color: theme.colors.secondaryText,
      marginRight: 8,
    },
    newsTime: {
      fontSize: 12,
      color: theme.colors.secondaryText,
    },
    forYouItem: {
      flexDirection: 'row',
      backgroundColor: theme.colors.cardBackground,
      borderRadius: 12,
      padding: 12,
      marginBottom: 12,
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 1,
      },
      shadowOpacity: 0.1,
      shadowRadius: 2,
      elevation: 2,
    },
    forYouImage: {
      width: 60,
      height: 60,
      borderRadius: 8,
      backgroundColor: '#E0E0E0',
      marginRight: 12,
    },
    forYouContent: {
      flex: 1,
      justifyContent: 'center',
    },
    forYouTitle: {
      fontSize: 14,
      fontWeight: '500',
      color: theme.colors.text,
      lineHeight: 18,
      marginBottom: 4,
    },
    forYouMeta: {
      flexDirection: 'row',
      alignItems: 'center',
    },
    forYouSource: {
      fontSize: 11,
      color: theme.colors.secondaryText,
      marginRight: 8,
    },
    forYouTime: {
      fontSize: 11,
      color: theme.colors.secondaryText,
    },
  });

  return (
    <View style={[styles.container, { paddingTop: insets.top }]}>
      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* Header */}
        <View style={styles.header}>
          <Text style={styles.headerTitle}>
            {language === 'tr' ? 'Anasayfa' : 'Home'}
          </Text>
          {/* Notification button hidden as requested */}
        </View>

        {/* Logo Section */}
        <View style={styles.logoSection}>
          <View style={styles.logoContainer}>
            {logoData.map((logo) => (
              <TouchableOpacity key={logo.id} style={[
                styles.logoItem,
                { backgroundColor: logo.bgColor || theme.colors.surface }
              ]}>
                <Text style={[
                  styles.logoText,
                  { color: logo.color || theme.colors.text }
                ]}>
                  {logo.name}
                </Text>
              </TouchableOpacity>
            ))}
          </View>
        </View>

        {/* Latest News Section */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>
            {language === 'tr' ? 'Haberler' : 'News'}
          </Text>

          {newsData.map((news) => (
            <TouchableOpacity key={news.id} style={styles.newsCard}>
              <View style={styles.newsImage}>
                <Image 
                  source={{ uri: news.image }}
                  style={styles.newsImage}
                  resizeMode="cover"
                />
              </View>
              <View style={styles.newsContent}>
                <Text style={styles.newsTitle}>{news.title}</Text>
                <View style={styles.newsMeta}>
                  <Text style={styles.newsSource}>{news.source}</Text>
                  <Text style={styles.newsTime}>• {news.time}</Text>
                </View>
              </View>
            </TouchableOpacity>
          ))}
        </View>

        {/* For You Section */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>
            {language === 'tr' ? 'Sizin İçin' : 'For You'}
          </Text>

          {forYouData.map((item) => (
            <TouchableOpacity key={item.id} style={styles.forYouItem}>
              <View style={styles.forYouImage}>
                <Image 
                  source={{ uri: item.image }}
                  style={styles.forYouImage}
                  resizeMode="cover"
                />
              </View>
              <View style={styles.forYouContent}>
                <Text style={styles.forYouTitle}>{item.title}</Text>
                <View style={styles.forYouMeta}>
                  <Text style={styles.forYouSource}>{item.source}</Text>
                  <Text style={styles.forYouTime}>• {item.time}</Text>
                </View>
              </View>
            </TouchableOpacity>
          ))}
        </View>

        {/* Bottom spacing */}
        <View style={{ height: 100 }} />
      </ScrollView>
    </View>
  );
};

export default HomeScreen;
