import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  ScrollView,
  TextInput,
  TouchableOpacity,
  Alert,
  Dimensions,
  ActivityIndicator,
  RefreshControl,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useLanguage } from '../contexts/LanguageContext';
import { useTheme } from '../contexts/ThemeContext';
import earthquakeService from '../services/earthquakeService';

const RasathaneScreen = () => {
  const { t } = useLanguage();
  const { theme } = useTheme();
  const [searchText, setSearchText] = useState('');
  const [selectedFilter, setSelectedFilter] = useState('all');
  const [isFilterPanelOpen, setIsFilterPanelOpen] = useState(false);
  const [earthquakeData, setEarthquakeData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [lastUpdateTime, setLastUpdateTime] = useState(null);
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');

  // Canlı deprem verilerini yükle
  const loadEarthquakeData = async () => {
    try {
      setIsLoading(true);
      const data = await earthquakeService.getLatestEarthquakes(100, 0);

      // Veriyi formatla - artçı deprem bilgilerini de dahil et
      const formattedData = data.map(earthquake => ({
        ...earthquake,
        displayTime: earthquake.time.toLocaleTimeString('tr-TR', { 
          hour: '2-digit', 
          minute: '2-digit' 
        }),
        fullDateTime: earthquake.formattedTime,
        dateOnly: earthquake.formattedDate
      }));

      setEarthquakeData(formattedData);
      setLastUpdateTime(new Date());
    } catch (error) {
      console.error('Deprem verileri yüklenirken hata:', error);
      Alert.alert('Hata', 'Deprem verileri yüklenemedi. Lütfen tekrar deneyin.');
    } finally {
      setIsLoading(false);
    }
  };

  // Verileri yenile
  const handleRefresh = async () => {
    setIsRefreshing(true);
    await loadEarthquakeData();
    setIsRefreshing(false);
  };

  // getSeverityColor artık earthquakeService'den geliyor

  // Component mount edildiğinde veri yükle
  useEffect(() => {
    loadEarthquakeData();

    // Her 1 dakikada bir otomatik güncelleme (anlık veri için)
    const interval = setInterval(() => {
      loadEarthquakeData();
    }, 60000); // 1 dakika

    return () => clearInterval(interval);
  }, []);

  const filteredData = earthquakeData.filter(item => {
    const matchesSearch = item.location.toLowerCase().includes(searchText.toLowerCase());
    if (selectedFilter === 'all') return matchesSearch;
    if (selectedFilter === 'high') return matchesSearch && item.magnitude >= 5.0;
    if (selectedFilter === 'medium') return matchesSearch && item.magnitude >= 3.0 && item.magnitude < 5.0;
    if (selectedFilter === 'low') return matchesSearch && item.magnitude < 3.0;
    return matchesSearch;
  });

  const handleEarthquakePress = (earthquake) => {
    const typeText = earthquake.isAftershock ? 'Artçı Deprem' : 'Ana Deprem';
    Alert.alert(
      `${earthquake.magnitude} Büyüklüğünde ${typeText}`,
      `Konum: ${earthquake.location}\nTarih: ${earthquake.fullDateTime}\nDerinlik: ${earthquake.depth} km\nKaynak: ${earthquake.source}\nŞiddet: ${earthquake.severity}\nTür: ${earthquake.type}`,
      [
        { text: 'Tamam', style: 'default' },
        { text: 'Detaylar', style: 'default', onPress: () => showDetails(earthquake) }
      ]
    );
  };

  const showDetails = (earthquake) => {
    const detailText = `Büyüklük: ${earthquake.magnitude}\nKonum: ${earthquake.location}\nTarih: ${earthquake.dateOnly}\nSaat: ${earthquake.displayTime}\nDerinlik: ${earthquake.depth} km\nEnlem: ${earthquake.latitude?.toFixed(4)}\nBoylam: ${earthquake.longitude?.toFixed(4)}\nVeri Kaynağı: ${earthquake.source}\nŞiddet Seviyesi: ${earthquake.severity}\nDeprem Türü: ${earthquake.type}\n\nBu veriler AFAD API'sinden anlık olarak çekilmektedir.`;

    Alert.alert(
      'Detaylı Deprem Bilgileri',
      detailText,
      [{ text: 'Tamam' }]
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={[styles.header, { paddingTop: insets.top }]}>
        <TouchableOpacity style={styles.menuButton}>
          <Ionicons name="menu" size={24} color="#FFFFFF" />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Deprem Bilgi Sistemi</Text>
        <View style={styles.headerActions}>
          <TouchableOpacity style={styles.searchButton}>
            <Ionicons name="search" size={24} color="#FFFFFF" />
          </TouchableOpacity>
          <TouchableOpacity style={styles.refreshButton}>
            <Ionicons name="refresh" size={24} color="#FFFFFF" />
          </TouchableOpacity>
          <TouchableOpacity style={styles.loginButton}>
            <Ionicons name="log-in" size={24} color="#FFFFFF" />
          </TouchableOpacity>
        </View>
      </View>

      {/* Filter Buttons */}
      <View style={styles.filterContainer}>
        <ScrollView horizontal showsHorizontalScrollIndicator={false}>
          <TouchableOpacity 
            style={[styles.filterChip, selectedFilter === 'all' && styles.filterChipActive]}
            onPress={() => setSelectedFilter('all')}
          >
            <Text style={[styles.filterText, selectedFilter === 'all' && styles.filterTextActive]}>Tüm</Text>
          </TouchableOpacity>
          <TouchableOpacity 
            style={[styles.filterChip, selectedFilter === 'low' && styles.filterChipActive]}
            onPress={() => setSelectedFilter('low')}
          >
            <Text style={[styles.filterText, selectedFilter === 'low' && styles.filterTextActive]}>0-3</Text>
          </TouchableOpacity>
          <TouchableOpacity 
            style={[styles.filterChip, selectedFilter === 'medium' && styles.filterChipActive]}
            onPress={() => setSelectedFilter('medium')}
          >
            <Text style={[styles.filterText, selectedFilter === 'medium' && styles.filterTextActive]}>3-4</Text>
          </TouchableOpacity>
          <TouchableOpacity 
            style={[styles.filterChip, selectedFilter === 'high' && styles.filterChipActive]}
            onPress={() => setSelectedFilter('high')}
          >
            <Text style={[styles.filterText, selectedFilter === 'high' && styles.filterTextActive]}>4&lt;</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.filterChip}>
            <Ionicons name="link" size={16} color="#8E8E93" />
          </TouchableOpacity>
        </ScrollView>
      </View>

      {/* Earthquake List */}
      <ScrollView 
        style={styles.listContainer} 
        showsVerticalScrollIndicator={false}
        refreshControl={
          <RefreshControl
            refreshing={isRefreshing}
            onRefresh={handleRefresh}
            colors={['#007AFF']}
            tintColor="#007AFF"
          />
        }
      >
        {/* Loading Indicator */}
        {isLoading && earthquakeData.length === 0 && (
          <View style={styles.loadingContainer}>
            <ActivityIndicator size="large" color={theme.colors.primary} />
            <Text style={[styles.loadingText, { color: theme.colors.text }]}>Deprem verileri yükleniyor...</Text>
          </View>
        )}

        {/* Last Update Time */}
        {lastUpdateTime && (
          <View style={[styles.updateTimeContainer, { backgroundColor: theme.colors.cardBackground }]}>
            <Text style={[styles.updateTimeText, { color: theme.colors.secondaryText }]}>
              Son Güncelleme: {lastUpdateTime.toLocaleDateString('tr-TR')} {lastUpdateTime.toLocaleTimeString('tr-TR')} (AFAD API - Anlık Veri)
            </Text>
          </View>
        )}

        {/* No Data Message */}
        {!isLoading && earthquakeData.length === 0 && (
          <View style={styles.noDataContainer}>
            <Ionicons name="earth" size={48} color={theme.colors.secondaryText} />
            <Text style={[styles.noDataText, { color: theme.colors.text }]}>Henüz deprem verisi bulunmuyor</Text>
            <TouchableOpacity style={[styles.retryButton, { backgroundColor: theme.colors.primary }]} onPress={loadEarthquakeData}>
              <Text style={[styles.retryButtonText, { color: theme.colors.onPrimary }]}>Yeniden Dene</Text>
            </TouchableOpacity>
          </View>
        )}
        {filteredData.map((earthquake) => (
          <TouchableOpacity 
            key={earthquake.id} 
            style={[
              styles.earthquakeCard,
              { backgroundColor: '#FFFFFF', borderBottomColor: '#E5E5EA' },
              earthquake.isAftershock && styles.aftershockCard
            ]}
            onPress={() => handleEarthquakePress(earthquake)}
            activeOpacity={0.7}
          >
            <View style={styles.cardLeft}>
              <View style={[styles.magnitudeCircle, { backgroundColor: '#2D5016' }]}>
                <Text style={styles.magnitudeText}>{earthquake.magnitude}</Text>
              </View>
              <View style={styles.cardInfo}>
                <Text style={[styles.locationText, { color: '#000000' }]}>{earthquake.location}</Text>
                <View style={styles.cardDetails}>
                  <Ionicons name="calendar-outline" size={12} color="#666666" style={{ marginRight: 4 }} />
                  <Text style={[styles.dateText, { color: '#666666' }]}>{earthquake.dateOnly}</Text>
                  <Ionicons name="time-outline" size={12} color="#666666" style={{ marginLeft: 8, marginRight: 4 }} />
                  <Text style={[styles.timeText, { color: '#666666' }]}>{earthquake.displayTime}</Text>
                  <Ionicons name="location-outline" size={12} color="#666666" style={{ marginLeft: 8, marginRight: 4 }} />
                  <Text style={[styles.distanceText, { color: '#666666' }]}>{earthquake.distance}</Text>
                  {earthquake.isAftershock && (
                    <Text style={[styles.aftershockLabel, { color: '#FF9500' }]}>Artçı Deprem</Text>
                  )}
                </View>
              </View>
            </View>
            <View style={styles.cardRight}>
              <View style={styles.afadLogo}>
                <Text style={styles.afadText}>AFAD</Text>
              </View>
              <View style={styles.csemLogo}>
                <Text style={styles.csemText}>CSEM</Text>
                <Text style={styles.emscText}>EMSC</Text>
              </View>
            </View>
          </TouchableOpacity>
        ))}))}
      </ScrollView>
    </SafeAreaView>
  );
};

