import React from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import Header from '../components/Header';

const PrivacyPolicyScreen = ({ navigation }) => {
  const { theme } = useTheme();
  const { t } = useLanguage();

  const privacySections = [
    {
      title: t('privacy.dataCollection.title'),
      content: t('privacy.dataCollection.content')
    },
    {
      title: t('privacy.dataUsage.title'),
      content: t('privacy.dataUsage.content')
    },
    {
      title: t('privacy.dataSharing.title'),
      content: t('privacy.dataSharing.content')
    },
    {
      title: t('privacy.dataSecurity.title'),
      content: t('privacy.dataSecurity.content')
    },
    {
      title: t('privacy.locationData.title'),
      content: t('privacy.locationData.content')
    },
    {
      title: t('privacy.cookies.title'),
      content: t('privacy.cookies.content')
    },
    {
      title: t('privacy.userRights.title'),
      content: t('privacy.userRights.content')
    },
    {
      title: t('privacy.dataRetention.title'),
      content: t('privacy.dataRetention.content')
    },
    {
      title: t('privacy.thirdParty.title'),
      content: t('privacy.thirdParty.content')
    },
    {
      title: t('privacy.contact.title'),
      content: t('privacy.contact.content')
    }
  ];

  const renderSection = (section, index) => (
    <View key={index} style={styles.section}>
      <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>
        {section.title}
      </Text>
      <Text style={[styles.sectionContent, { color: theme.colors.secondaryText }]}>
        {section.content}
      </Text>
    </View>
  );

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <Header
        title={t('privacy.title')}
        showBackButton={true}
        onBackPress={() => navigation.goBack()}
      />
      
      <ScrollView style={styles.content} showsVerticalScrollIndicator={false}>
        <View style={styles.header}>
          <Text style={[styles.lastUpdated, { color: theme.colors.secondaryText }]}>
            {t('privacy.lastUpdated')}: 15 Aralık 2024
          </Text>
          <Text style={[styles.introduction, { color: theme.colors.text }]}>
            {t('privacy.introduction')}
          </Text>
        </View>

        {privacySections.map(renderSection)}

        <View style={styles.footer}>
          <Text style={[styles.footerText, { color: theme.colors.tertiaryText }]}>
            {t('privacy.footer')}
          </Text>
        </View>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  content: {
    flex: 1,
    paddingHorizontal: 20,
  },
  header: {
    paddingVertical: 20,
  },
  lastUpdated: {
    fontSize: 14,
    marginBottom: 16,
    fontStyle: 'italic',
  },
  introduction: {
    fontSize: 16,
    lineHeight: 24,
    marginBottom: 20,
  },
  section: {
    marginBottom: 24,
  },
  sectionTitle: {
    fontSize: 18,
    fontWeight: '600',
    marginBottom: 12,
  },
  sectionContent: {
    fontSize: 15,
    lineHeight: 22,
    textAlign: 'justify',
  },
  footer: {
    paddingVertical: 30,
    alignItems: 'center',
  },
  footerText: {
    fontSize: 12,
    textAlign: 'center',
    lineHeight: 18,
  },
});

export default PrivacyPolicyScreen;
