import React from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity, Linking } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import Header from '../components/Header';

const HelpCenterScreen = ({ navigation }) => {
  const { theme } = useTheme();
  const { t } = useLanguage();

  const faqData = [
    {
      question: 'Afet.net nedir?',
      answer: 'Afet.net, afet durumlarında hızlı bilgi paylaşımı ve koordinasyon sağlayan bir mobil uygulamadır. Deprem, sel, yangın gibi doğal afetlerde kullanıcıların güvenli kalmasına yardımcı olur.'
    },
    {
      question: 'Uygulama nasıl kullanılır?',
      answer: 'Ana sayfadan güncel afet bilgilerini takip edebilir, harita üzerinden afet bölgelerini görebilir, acil durum butonunu kullanabilir ve profil sayfasından kişisel bilgilerinizi yönetebilirsiniz.'
    },
    {
      question: 'Acil durum butonu ne işe yarar?',
      answer: 'Acil durum butonu, tehlike anında hızlı bir şekilde yardım çağırmanızı sağlar. Bu buton ilgili mercilere konumunuz ve durumunuz hakkında bilgi gönderir.'
    },
    {
      question: 'Konum bilgilerim güvenli mi?',
      answer: 'Evet, konum bilgileriniz şifrelenerek saklanır ve sadece acil durumlarda yetkili mercilerle paylaşılır. Gizlilik politikamızda detaylı bilgi bulabilirsiniz.'
    },
    {
      question: 'Bildirimler nasıl çalışır?',
      answer: 'Uygulama, bulunduğunuz bölgedeki afet uyarılarını, aile üyelerinizin durumunu ve topluluk güncellemelerini bildirim olarak gönderir. Ayarlar sayfasından bildirim tercihlerinizi düzenleyebilirsiniz.'
    },
    {
      question: 'Offline kullanım mümkün mü?',
      answer: 'Temel özellikler internet bağlantısı olmadan da kullanılabilir. Ancak güncel bilgiler ve harita verileri için internet bağlantısı gereklidir.'
    }
  ];

  const contactInfo = [
    {
      title: 'Acil Durum Hattı',
      value: '112',
      icon: 'call',
      action: () => Linking.openURL('tel:112')
    },
    {
      title: 'AFAD İletişim',
      value: '122',
      icon: 'call',
      action: () => Linking.openURL('tel:122')
    },
    {
      title: 'E-posta Desteği',
      value: 'destek@afet.net',
      icon: 'mail',
      action: () => Linking.openURL('mailto:destek@afet.net')
    },
    {
      title: 'Web Sitesi',
      value: 'www.afet.net',
      icon: 'globe',
      action: () => Linking.openURL('https://www.afet.net')
    }
  ];

  const guideSteps = [
    {
      step: '1',
      title: 'Kayıt Olun',
      description: 'Uygulamaya kayıt olarak kişisel bilgilerinizi ve acil durum kişilerinizi ekleyin.'
    },
    {
      step: '2',
      title: 'Konum İzni Verin',
      description: 'Uygulamanın size en yakın afet bilgilerini sunabilmesi için konum izni verin.'
    },
    {
      step: '3',
      title: 'Bildirimleri Aktifleştirin',
      description: 'Önemli afet uyarılarını kaçırmamak için bildirim izinlerini açın.'
    },
    {
      step: '4',
      title: 'Aile Üyelerini Ekleyin',
      description: 'Profil sayfasından aile üyelerinizi ekleyerek onların durumunu takip edin.'
    },
    {
      step: '5',
      title: 'Acil Durum Planı Yapın',
      description: 'Ailenizle birlikte acil durum planınızı yapın ve buluşma noktalarını belirleyin.'
    }
  ];

  const renderFAQItem = (item, index) => (
    <View key={index} style={[styles.faqItem, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}>
      <Text style={[styles.question, { color: theme.colors.text }]}>{item.question}</Text>
      <Text style={[styles.answer, { color: theme.colors.secondaryText }]}>{item.answer}</Text>
    </View>
  );

  const renderContactItem = (item, index) => (
    <TouchableOpacity
      key={index}
      style={[styles.contactItem, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}
      onPress={item.action}
    >
      <View style={styles.contactLeft}>
        <Ionicons name={item.icon} size={24} color={theme.colors.primary} />
        <View style={styles.contactText}>
          <Text style={[styles.contactTitle, { color: theme.colors.text }]}>{item.title}</Text>
          <Text style={[styles.contactValue, { color: theme.colors.secondaryText }]}>{item.value}</Text>
        </View>
      </View>
      <Ionicons name="chevron-forward" size={20} color={theme.colors.secondaryText} />
    </TouchableOpacity>
  );

  const renderGuideStep = (item, index) => (
    <View key={index} style={[styles.guideStep, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}>
      <View style={[styles.stepNumber, { backgroundColor: theme.colors.primary }]}>
        <Text style={[styles.stepNumberText, { color: theme.colors.onPrimary }]}>{item.step}</Text>
      </View>
      <View style={styles.stepContent}>
        <Text style={[styles.stepTitle, { color: theme.colors.text }]}>{item.title}</Text>
        <Text style={[styles.stepDescription, { color: theme.colors.secondaryText }]}>{item.description}</Text>
      </View>
    </View>
  );

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <Header title="Yardım Merkezi" navigation={navigation} />

      <ScrollView style={styles.content} showsVerticalScrollIndicator={false}>
        {/* Sık Sorulan Sorular */}
        <View style={styles.section}>
          <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>Sık Sorulan Sorular</Text>
          {faqData.map(renderFAQItem)}
        </View>

        {/* İletişim Bilgileri */}
        <View style={styles.section}>
          <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>İletişim Bilgileri</Text>
          {contactInfo.map(renderContactItem)}
        </View>

        {/* Kullanım Kılavuzu */}
        <View style={styles.section}>
          <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>Kullanım Kılavuzu</Text>
          <Text style={[styles.sectionDescription, { color: theme.colors.secondaryText }]}>
            Afet.net uygulamasını etkili bir şekilde kullanmak için aşağıdaki adımları takip edin:
          </Text>
          {guideSteps.map(renderGuideStep)}
        </View>

        {/* Ek Bilgiler */}
        <View style={[styles.section, styles.lastSection]}>
          <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>Önemli Bilgiler</Text>
          <View style={[styles.infoBox, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.warning }]}>
            <Ionicons name="warning" size={24} color={theme.colors.warning} />
            <Text style={[styles.infoText, { color: theme.colors.text }]}>
              Gerçek bir acil durum yaşıyorsanız, öncelikle 112 Acil Çağrı Merkezi'ni arayın.
            </Text>
          </View>
        </View>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  content: {
    flex: 1,
    padding: 16,
  },
  section: {
    marginBottom: 32,
  },
  lastSection: {
    marginBottom: 16,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  sectionDescription: {
    fontSize: 14,
    lineHeight: 20,
    marginBottom: 16,
  },
  faqItem: {
    padding: 16,
    borderRadius: 12,
    borderWidth: 1,
    marginBottom: 12,
  },
  question: {
    fontSize: 16,
    fontWeight: '600',
    marginBottom: 8,
  },
  answer: {
    fontSize: 14,
    lineHeight: 20,
  },
  contactItem: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 16,
    borderRadius: 12,
    borderWidth: 1,
    marginBottom: 12,
  },
  contactLeft: {
    flexDirection: 'row',
    alignItems: 'center',
    flex: 1,
  },
  contactText: {
    marginLeft: 12,
    flex: 1,
  },
  contactTitle: {
    fontSize: 16,
    fontWeight: '600',
  },
  contactValue: {
    fontSize: 14,
    marginTop: 2,
  },
  guideStep: {
    flexDirection: 'row',
    padding: 16,
    borderRadius: 12,
    borderWidth: 1,
    marginBottom: 12,
  },
  stepNumber: {
    width: 32,
    height: 32,
    borderRadius: 16,
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: 12,
  },
  stepNumberText: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  stepContent: {
    flex: 1,
  },
  stepTitle: {
    fontSize: 16,
    fontWeight: '600',
    marginBottom: 4,
  },
  stepDescription: {
    fontSize: 14,
    lineHeight: 20,
  },
  infoBox: {
    flexDirection: 'row',
    padding: 16,
    borderRadius: 12,
    borderWidth: 2,
    alignItems: 'flex-start',
  },
  infoText: {
    fontSize: 14,
    lineHeight: 20,
    marginLeft: 12,
    flex: 1,
  },
});

export default HelpCenterScreen;
