import React, { useEffect, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Animated,
  Dimensions,
  ActivityIndicator,
} from 'react-native';
import { useTheme } from '../contexts/ThemeContext';

const { width, height } = Dimensions.get('window');

const SplashScreen = ({ onFinish }) => {
  const { theme } = useTheme();
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const scaleAnim = useRef(new Animated.Value(0.8)).current;
  const slideAnim = useRef(new Animated.Value(50)).current;
  const rotateAnim = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    // Ana animasyon sekansı
    Animated.sequence([
      // Logo ve başlık animasyonu
      Animated.parallel([
        Animated.timing(fadeAnim, {
          toValue: 1,
          duration: 1000,
          useNativeDriver: true,
        }),
        Animated.timing(scaleAnim, {
          toValue: 1,
          duration: 1000,
          useNativeDriver: true,
        }),
        Animated.timing(slideAnim, {
          toValue: 0,
          duration: 1000,
          useNativeDriver: true,
        }),
      ]),
      // Dönen animasyon
      Animated.timing(rotateAnim, {
        toValue: 1,
        duration: 1500,
        useNativeDriver: true,
      }),
    ]).start(() => {
      // Animasyon tamamlandıktan sonra ana ekrana geç
      setTimeout(() => {
        onFinish();
      }, 500);
    });
  }, []);

  const spin = rotateAnim.interpolate({
    inputRange: [0, 1],
    outputRange: ['0deg', '360deg'],
  });

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.primary }]}>
      {/* Top Section - Ministry Logo and Text */}
      <Animated.View
        style={[
          styles.topSection,
          {
            opacity: fadeAnim,
            transform: [{ translateY: slideAnim }],
          },
        ]}
      >
        {/* Ministry Logo */}
        <View style={styles.ministryLogoContainer}>
          <View style={[styles.ministryCircle, { backgroundColor: theme.colors.surface }]}>
            <View style={styles.crescent}>
              <Ionicons name="moon" size={20} color={theme.colors.primary} />
            </View>
            <View style={styles.star}>
              <Ionicons name="star" size={16} color={theme.colors.primary} />
            </View>
          </View>
        </View>

        {/* Ministry Text */}
        <Text style={[styles.ministryText, { color: theme.colors.onPrimary }]}>TÜRKİYE CUMHURİYETİ</Text>
        <Text style={[styles.ministrySubText, { color: theme.colors.onPrimary }]}>İÇİŞLERİ BAKANLIĞI</Text>
      </Animated.View>

      {/* Center Section - AFAD Logo */}
      <Animated.View
        style={[
          styles.centerSection,
          {
            opacity: fadeAnim,
            transform: [{ scale: scaleAnim }],
          },
        ]}
      >
        <View style={styles.afadContainer}>
          <Text style={[styles.afadTitle, { color: theme.colors.onPrimary }]}>AFAD</Text>
          <Text style={[styles.afadSubtitle, { color: theme.colors.onPrimary }]}>acil</Text>
          <Text style={[styles.afadDescription, { color: theme.colors.onPrimary }]}>AFET VE ACİL DURUM</Text>
          <Text style={[styles.afadDescriptionSub, { color: theme.colors.onPrimary }]}>YÖNETİMİ BAŞKANLIĞI</Text>
        </View>
      </Animated.View>

      {/* Bottom Loading */}
      <Animated.View
        style={[
          styles.loadingContainer,
          {
            opacity: fadeAnim,
          },
        ]}
      >
        <ActivityIndicator size="small" color={theme.colors.onPrimary} />
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E53E3E',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingVertical: 60,
    paddingHorizontal: 20,
  },
  topSection: {
    alignItems: 'center',
    marginTop: 40,
  },
  ministryLogoContainer: {
    marginBottom: 20,
  },
  ministryCircle: {
    width: 80,
    height: 80,
    borderRadius: 40,
    backgroundColor: '#FFFFFF',
    justifyContent: 'center',
    alignItems: 'center',
    position: 'relative',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  crescent: {
    position: 'absolute',
    left: 20,
    top: 15,
  },
  star: {
    position: 'absolute',
    right: 18,
    top: 12,
  },
  ministryText: {
    fontSize: 16,
    fontWeight: '700',
    color: '#FFFFFF',
    textAlign: 'center',
    letterSpacing: 1.2,
    marginBottom: 4,
  },
  ministrySubText: {
    fontSize: 14,
    fontWeight: '600',
    color: '#FFFFFF',
    textAlign: 'center',
    letterSpacing: 1,
  },
  centerSection: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  afadContainer: {
    backgroundColor: '#FFFFFF',
    paddingVertical: 40,
    paddingHorizontal: 60,
    borderRadius: 0,
    alignItems: 'center',
    width: width * 0.85,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 4,
    },
    shadowOpacity: 0.3,
    shadowRadius: 4.65,
    elevation: 8,
  },
  afadTitle: {
    fontSize: 64,
    fontWeight: '900',
    color: '#1E3A8A',
    textAlign: 'center',
    marginBottom: -5,
    letterSpacing: 2,
  },
  afadSubtitle: {
    fontSize: 36,
    fontWeight: '400',
    color: '#E53E3E',
    textAlign: 'center',
    fontStyle: 'italic',
    marginBottom: 25,
    letterSpacing: 1,
  },
  afadDescription: {
    fontSize: 12,
    fontWeight: '700',
    color: '#374151',
    textAlign: 'center',
    letterSpacing: 1.5,
    marginBottom: 2,
  },
  afadDescriptionSub: {
    fontSize: 12,
    fontWeight: '700',
    color: '#374151',
    textAlign: 'center',
    letterSpacing: 1.5,
  },
  loadingContainer: {
    alignItems: 'center',
    marginBottom: 20,
  },
});

export default SplashScreen;
