import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  FlatList,
  TouchableOpacity,
  StyleSheet,
  Alert,
  ActivityIndicator,
  Modal,
  ScrollView
} from 'react-native';
import { useAuth } from '../../contexts/AuthContext';

const AuthorApplications = () => {
  const { user, API_BASE_URL } = useAuth();
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedApplication, setSelectedApplication] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [actionLoading, setActionLoading] = useState(false);

  useEffect(() => {
    fetchApplications();
  }, []);

  const fetchApplications = async () => {
    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`${API_BASE_URL}/admin/author-applications`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const data = await response.json();
      if (data.success) {
        setApplications(data.applications);
      } else {
        Alert.alert('Hata', data.error || 'Başvurular yüklenirken hata oluştu');
      }
    } catch (error) {
      console.error('Fetch applications error:', error);
      Alert.alert('Hata', 'Başvurular yüklenirken hata oluştu');
    } finally {
      setLoading(false);
    }
  };

  const handleApplicationAction = async (applicationId, action) => {
    setActionLoading(true);
    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`${API_BASE_URL}/admin/author-applications`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          applicationId,
          action
        })
      });

      const data = await response.json();
      if (data.success) {
        Alert.alert(
          'Başarılı', 
          action === 'approve' ? 'Başvuru onaylandı' : 'Başvuru reddedildi',
          [
            {
              text: 'Tamam',
              onPress: () => {
                fetchApplications(); // Refresh the list
                setModalVisible(false);
              }
            }
          ]
        );
      } else {
        Alert.alert('Hata', data.error || 'İşlem gerçekleştirilemedi');
      }
    } catch (error) {
      console.error('Application action error:', error);
      Alert.alert('Hata', 'İşlem gerçekleştirilemedi');
    } finally {
      setActionLoading(false);
    }
  };

  const showApplicationDetails = (application) => {
    setSelectedApplication(application);
    setModalVisible(true);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('tr-TR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const renderApplicationItem = ({ item }) => (
    <TouchableOpacity
      style={styles.applicationCard}
      onPress={() => showApplicationDetails(item)}
    >
      <View style={styles.applicationHeader}>
        <Text style={styles.applicantName}>{item.full_name}</Text>
        <View style={styles.statusBadge}>
          <Text style={styles.statusText}>BEKLEMEDE</Text>
        </View>
      </View>

      <Text style={styles.applicantEmail}>{item.email}</Text>
      <Text style={styles.organization}>📍 {item.organization}</Text>

      <Text style={styles.bioPreview} numberOfLines={2}>
        {item.bio}
      </Text>

      <View style={styles.applicationFooter}>
        <Text style={styles.applicationDate}>
          Başvuru Tarihi: {formatDate(item.created_at)}
        </Text>
        <Text style={styles.viewDetails}>Detayları Görüntüle →</Text>
      </View>
    </TouchableOpacity>
  );

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#3498db" />
        <Text style={styles.loadingText}>Başvurular yükleniyor...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Yazar Başvuruları</Text>
        <Text style={styles.subtitle}>
          {applications.length} bekleyen başvuru
        </Text>
      </View>

      {applications.length === 0 ? (
        <View style={styles.emptyContainer}>
          <Text style={styles.emptyText}>Bekleyen yazar başvurusu bulunmuyor</Text>
          <Text style={styles.emptySubtext}>
            Yeni başvurular geldiğinde burada görünecektir
          </Text>
        </View>
      ) : (
        <FlatList
          data={applications}
          renderItem={renderApplicationItem}
          keyExtractor={(item) => item.id}
          contentContainerStyle={styles.listContainer}
          showsVerticalScrollIndicator={false}
        />
      )}

      {/* Application Details Modal */}
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            {selectedApplication && (
              <ScrollView showsVerticalScrollIndicator={false}>
                <Text style={styles.modalTitle}>Yazar Başvurusu</Text>

                <View style={styles.detailSection}>
                  <Text style={styles.detailLabel}>Başvuran</Text>
                  <Text style={styles.detailValue}>{selectedApplication.full_name}</Text>
                </View>

                <View style={styles.detailSection}>
                  <Text style={styles.detailLabel}>E-posta</Text>
                  <Text style={styles.detailValue}>{selectedApplication.email}</Text>
                </View>

                <View style={styles.detailSection}>
                  <Text style={styles.detailLabel}>Kuruluş</Text>
                  <Text style={styles.detailValue}>{selectedApplication.organization}</Text>
                </View>

                <View style={styles.detailSection}>
                  <Text style={styles.detailLabel}>Biyografi</Text>
                  <Text style={styles.bioText}>{selectedApplication.bio}</Text>
                </View>

                <View style={styles.detailSection}>
                  <Text style={styles.detailLabel}>Başvuru Tarihi</Text>
                  <Text style={styles.detailValue}>
                    {formatDate(selectedApplication.created_at)}
                  </Text>
                </View>

                {selectedApplication.phone && (
                  <View style={styles.detailSection}>
                    <Text style={styles.detailLabel}>Telefon</Text>
                    <Text style={styles.detailValue}>{selectedApplication.phone}</Text>
                  </View>
                )}

                <View style={styles.actionButtons}>
                  <TouchableOpacity
                    style={[styles.actionButton, styles.rejectButton]}
                    onPress={() => handleApplicationAction(selectedApplication.id, 'reject')}
                    disabled={actionLoading}
                  >
                    <Text style={styles.rejectButtonText}>
                      {actionLoading ? 'İşleniyor...' : 'Reddet'}
                    </Text>
                  </TouchableOpacity>

                  <TouchableOpacity
                    style={[styles.actionButton, styles.approveButton]}
                    onPress={() => handleApplicationAction(selectedApplication.id, 'approve')}
                    disabled={actionLoading}
                  >
                    <Text style={styles.approveButtonText}>
                      {actionLoading ? 'İşleniyor...' : 'Onayla'}
                    </Text>
                  </TouchableOpacity>
                </View>

                <TouchableOpacity
                  style={styles.closeButton}
                  onPress={() => setModalVisible(false)}
                  disabled={actionLoading}
                >
                  <Text style={styles.closeButtonText}>Kapat</Text>
                </TouchableOpacity>

                {actionLoading && (
                  <View style={styles.actionLoading}>
                    <ActivityIndicator color="#3498db" />
                  </View>
                )}
              </ScrollView>
            )}
          </View>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5'
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center'
  },
  loadingText: {
    marginTop: 10,
    fontSize: 16,
    color: '#666'
  },
  header: {
    backgroundColor: '#fff',
    padding: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#e0e0e0'
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#333'
  },
  subtitle: {
    fontSize: 16,
    color: '#666',
    marginTop: 4
  },
  emptyContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 40
  },
  emptyText: {
    fontSize: 18,
    fontWeight: '600',
    color: '#666',
    textAlign: 'center',
    marginBottom: 8
  },
  emptySubtext: {
    fontSize: 14,
    color: '#999',
    textAlign: 'center'
  },
  listContainer: {
    padding: 16
  },
  applicationCard: {
    backgroundColor: '#fff',
    borderRadius: 12,
    padding: 16,
    marginBottom: 12,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3
  },
  applicationHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 8
  },
  applicantName: {
    fontSize: 18,
    fontWeight: '600',
    color: '#333',
    flex: 1
