import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  Image,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

const HomeScreen = ({ navigation }) => {
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');
  const { theme } = useTheme();
  const { t } = useLanguage();

  // News sources data
  const newsSources = [
    { id: 1, name: 'TEMPO', color: '#E74C3C', bgColor: '#FFEBEE' },
    { id: 2, name: 'detik', color: '#3498DB', bgColor: '#E3F2FD' },
    { id: 3, name: 'CNN', color: '#E74C3C', bgColor: '#FFEBEE' },
    { id: 4, name: 'beIN', color: '#9B59B6', bgColor: '#F3E5F5' },
    { id: 5, name: 'CNN', color: '#E74C3C', bgColor: '#FFEBEE' },
  ];

  // Latest news data
  const latestNews = {
    id: 1,
    title: 'Threads mencatat 100 juta pengguna kurang dari satu pekan',
    source: 'BBC News',
    time: '21 Mar 2024',
    category: 'Teknologi',
    image: 'https://picsum.photos/400/250?random=1'
  };

  // For you news data
  const forYouNews = [
    {
      id: 1,
      category: 'Olahraga',
      title: 'Haaland Pakai Jersey Baru Timnas Norwegia... Itu Thailand?',
      source: 'Detik Sport',
      time: '21 Mar 2024',
      image: 'https://picsum.photos/80/80?random=2'
    },
    {
      id: 2,
      category: 'Politik',
      title: 'Hasil final rekapitulasi suara Pilpres dan Pileg 2024',
      source: 'BBC News',
      time: '21 Mar 2024',
      image: 'https://picsum.photos/80/80?random=3'
    },
    {
      id: 3,
      category: 'Otomotif',
      title: 'Mudik Lebaran: Mengenal Jalur Tol Trans Jawa',
      source: 'Kompas',
      time: '21 Mar 2024',
      image: 'https://picsum.photos/80/80?random=4'
    }
  ];

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: theme.colors.background,
    },
    header: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      paddingHorizontal: 20,
      paddingVertical: 16,
      backgroundColor: theme.colors.background,
    },
    headerTitle: {
      fontSize: 24,
      fontWeight: 'bold',
      color: theme.colors.text,
    },
    notificationButton: {
      padding: 8,
    },
    scrollView: {
      flex: 1,
    },
    newsSourcesContainer: {
      paddingHorizontal: 20,
      marginBottom: 24,
    },
    newsSourcesScroll: {
      flexDirection: 'row',
    },
    newsSourceItem: {
      alignItems: 'center',
      justifyContent: 'center',
      width: 60,
      height: 60,
      borderRadius: 30,
      marginRight: 16,
      borderWidth: 2,
      borderColor: '#E0E0E0',
    },
    newsSourceText: {
      fontSize: 10,
      fontWeight: '600',
      textAlign: 'center',
    },
    section: {
      paddingHorizontal: 20,
      marginBottom: 24,
    },
    sectionTitle: {
      fontSize: 18,
      fontWeight: 'bold',
      color: theme.colors.text,
      marginBottom: 16,
    },
    latestNewsCard: {
      backgroundColor: theme.colors.cardBackground,
      borderRadius: 12,
      overflow: 'hidden',
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 2,
      },
      shadowOpacity: 0.1,
      shadowRadius: 4,
      elevation: 3,
    },
    latestNewsImage: {
      width: '100%',
      height: 200,
      backgroundColor: '#F0F0F0',
    },
    latestNewsContent: {
      padding: 16,
    },
    latestNewsCategory: {
      position: 'absolute',
      top: 12,
      right: 12,
      backgroundColor: '#007AFF',
      paddingHorizontal: 8,
      paddingVertical: 4,
      borderRadius: 12,
    },
    latestNewsCategoryText: {
      color: '#FFFFFF',
      fontSize: 12,
      fontWeight: '600',
    },
    latestNewsTitle: {
      fontSize: 16,
      fontWeight: '600',
      color: theme.colors.text,
      lineHeight: 22,
      marginBottom: 8,
    },
    latestNewsMeta: {
      flexDirection: 'row',
      alignItems: 'center',
    },
    latestNewsSource: {
      fontSize: 12,
      color: theme.colors.secondaryText,
      marginRight: 8,
    },
    latestNewsTime: {
      fontSize: 12,
      color: theme.colors.secondaryText,
    },
    forYouNewsItem: {
      flexDirection: 'row',
      backgroundColor: theme.colors.cardBackground,
      borderRadius: 12,
      padding: 12,
      marginBottom: 12,
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 1,
      },
      shadowOpacity: 0.1,
      shadowRadius: 2,
      elevation: 2,
    },
    forYouNewsImage: {
      width: 60,
      height: 60,
      borderRadius: 8,
      backgroundColor: '#F0F0F0',
      marginRight: 12,
    },
    forYouNewsContent: {
      flex: 1,
      justifyContent: 'space-between',
    },
    forYouNewsCategory: {
      fontSize: 12,
      color: '#007AFF',
      fontWeight: '600',
      marginBottom: 4,
    },
    forYouNewsTitle: {
      fontSize: 14,
      fontWeight: '600',
      color: theme.colors.text,
      lineHeight: 18,
      marginBottom: 4,
    },
    forYouNewsMeta: {
      flexDirection: 'row',
      alignItems: 'center',
    },
    forYouNewsSource: {
      fontSize: 11,
      color: theme.colors.secondaryText,
      marginRight: 8,
    },
    forYouNewsTime: {
      fontSize: 11,
      color: theme.colors.secondaryText,
    },
  });

  return (
    <View style={[styles.container, { paddingTop: insets.top }]}>
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.headerTitle}>Afetnet.com</Text>
        <TouchableOpacity 
          style={styles.notificationButton}
          onPress={() => navigation.navigate('Notifications')}
        >
          <Ionicons name="notifications" size={24} color={theme.colors.primary} />
        </TouchableOpacity>
      </View>

      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* News Sources */}
        <View style={styles.newsSourcesContainer}>
          <ScrollView 
            horizontal 
            showsHorizontalScrollIndicator={false}
            contentContainerStyle={styles.newsSourcesScroll}
          >
            {newsSources.map((source) => (
              <TouchableOpacity
                key={source.id}
                style={[
                  styles.newsSourceItem,
                  { backgroundColor: source.bgColor, borderColor: source.color }
                ]}
              >
                <Text style={[styles.newsSourceText, { color: source.color }]}>
                  {source.name}
                </Text>
              </TouchableOpacity>
            ))}
          </ScrollView>
        </View>

        {/* Latest News Section */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Berita terbaru</Text>
          <TouchableOpacity style={styles.latestNewsCard}>
            <View style={{ position: 'relative' }}>
              <Image 
                source={{ uri: latestNews.image }}
                style={styles.latestNewsImage}
                resizeMode="cover"
              />
              <View style={styles.latestNewsCategory}>
                <Text style={styles.latestNewsCategoryText}>{latestNews.category}</Text>
              </View>
            </View>
            <View style={styles.latestNewsContent}>
              <Text style={styles.latestNewsTitle}>{latestNews.title}</Text>
              <View style={styles.latestNewsMeta}>
                <Text style={styles.latestNewsSource}>{latestNews.source}</Text>
                <Text style={styles.latestNewsTime}>• {latestNews.time}</Text>
              </View>
            </View>
          </TouchableOpacity>
        </View>

        {/* For You Section */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Untukmu</Text>
          {forYouNews.map((news) => (
            <TouchableOpacity key={news.id} style={styles.forYouNewsItem}>
              <Image 
                source={{ uri: news.image }}
                style={styles.forYouNewsImage}
                resizeMode="cover"
              />
              <View style={styles.forYouNewsContent}>
                <Text style={styles.forYouNewsCategory}>{news.category}</Text>
                <Text style={styles.forYouNewsTitle}>{news.title}</Text>
                <View style={styles.forYouNewsMeta}>
                  <Text style={styles.forYouNewsSource}>{news.source}</Text>
                  <Text style={styles.forYouNewsTime}>• {news.time}</Text>
                </View>
              </View>
            </TouchableOpacity>
          ))}
        </View>
      </ScrollView>
    </View>
  );
};

export default HomeScreen;
