import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  Image,
  StatusBar,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useTheme } from '../contexts/ThemeContext';

const HomeScreen = ({ navigation }) => {
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');
  const { theme } = useTheme();

  const [selectedCategory, setSelectedCategory] = useState('Tech');

  const categories = ['Tech', 'Science', 'Education', 'Business'];

  const featuredArticle = {
    id: 1,
    title: 'A month with DJI Mini 3 Pro',
    date: 'Jul 10, 2023',
    image: 'https://images.unsplash.com/photo-1473968512647-3e447244af8f?w=400&h=250&fit=crop',
    category: 'Tech'
  };

  const articles = [
    {
      id: 2,
      title: 'Now Google\'s Bard AI can talk & respond to visual prompts',
      source: 'The Verge',
      category: 'Google AI',
      date: 'Jul 13, 2023',
      readTime: '2 min read',
      image: 'https://images.unsplash.com/photo-1677442136019-21780ecad995?w=80&h=80&fit=crop'
    },
    {
      id: 3,
      title: 'WatchOS 10 preview: widgets all the way down',
      source: 'Gizmodo',
      category: 'Watch',
      date: 'Jul 10, 2023',
      readTime: '4 min read',
      image: 'https://images.unsplash.com/photo-1434494878577-86c23bcb06b9?w=80&h=80&fit=crop'
    },
    {
      id: 4,
      title: 'How Gen Z are disrupting the definition of \'prestigious\' jobs',
      source: 'Work Life',
      category: 'Jobs',
      date: 'Jul 8, 2023',
      readTime: '3 min read',
      image: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=80&h=80&fit=crop'
    }
  ];

  return (
    <View style={styles.container}>
      <StatusBar barStyle="light-content" backgroundColor="#1C1C1E" />

      {/* Header */}
      <View style={[styles.header, { paddingTop: insets.top }]}>
        <View style={styles.leftHeader}>
          <TouchableOpacity style={styles.headerButton}>
            <Ionicons name="menu" size={24} color="#FFFFFF" />
          </TouchableOpacity>
        </View>

        <View style={styles.rightHeader}>
          <TouchableOpacity style={styles.headerButton}>
            <Ionicons name="search" size={24} color="#FFFFFF" />
          </TouchableOpacity>

          <TouchableOpacity style={styles.headerButton}>
            <Ionicons name="notifications" size={24} color="#FFFFFF" />
            <View style={styles.notificationBadge} />
          </TouchableOpacity>

          <Image 
            source={{ uri: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=32&h=32&fit=crop&crop=face' }}
            style={styles.profileImage}
          />
        </View>
      </View>

      {/* Categories */}
      <View style={styles.categoriesContainer}>
        <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.categoriesScroll}>
          {categories.map((category, index) => (
            <TouchableOpacity 
              key={index}
              style={[
                styles.categoryButton,
                selectedCategory === category && styles.categoryButtonActive
              ]}
              onPress={() => setSelectedCategory(category)}
            >
              <Text style={[
                styles.categoryText,
                selectedCategory === category && styles.categoryTextActive
              ]}>
                {category}
              </Text>
            </TouchableOpacity>
          ))}
        </ScrollView>
      </View>

      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        <View style={styles.content}>
          {/* Featured Article */}
          <TouchableOpacity style={styles.featuredCard}>
            <Image source={{ uri: featuredArticle.image }} style={styles.featuredImage} />
            <View style={styles.featuredOverlay}>
              <Text style={styles.featuredDate}>{featuredArticle.date}</Text>
              <Text style={styles.featuredTitle}>{featuredArticle.title}</Text>
              <TouchableOpacity style={styles.expandButton}>
                <Ionicons name="arrow-up-right" size={20} color="#FFFFFF" />
              </TouchableOpacity>
            </View>
          </TouchableOpacity>

          {/* Top Stories Section */}
          <View style={styles.sectionHeader}>
            <Text style={styles.sectionTitle}>Top Stories</Text>
            <TouchableOpacity>
              <Text style={styles.seeAllText}>See all</Text>
            </TouchableOpacity>
          </View>

          {/* News Articles */}
          <View style={styles.articlesContainer}>
            {articles.map((article, index) => (
              <TouchableOpacity key={index} style={styles.articleCard}>
                <Image source={{ uri: article.image }} style={styles.articleImage} />
                <View style={styles.articleContent}>
                  <View style={styles.articleMeta}>
                    <Text style={styles.articleSource}>{article.source}</Text>
                    <Text style={styles.articleDot}>•</Text>
                    <Text style={styles.articleSource}>{article.category}</Text>
                  </View>
                  <Text style={styles.articleTitle}>{article.title}</Text>
                  <View style={styles.articleFooter}>
                    <Text style={styles.articleDate}>{article.date}</Text>
                    <Text style={styles.articleReadTime}>{article.readTime}</Text>
                    <TouchableOpacity style={styles.bookmarkButton}>
                      <Ionicons name="bookmark-outline" size={16} color="#8E8E93" />
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.moreButton}>
                      <Ionicons name="ellipsis-horizontal" size={16} color="#8E8E93" />
                    </TouchableOpacity>
                  </View>
                </View>
              </TouchableOpacity>
            ))}
          </View>
        </View>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#1C1C1E',
  },
  scrollView: {
    flex: 1,
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    paddingHorizontal: 16,
    paddingVertical: 12,
    backgroundColor: '#1C1C1E',
  },
  leftHeader: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  rightHeader: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  headerButton: {
    padding: 8,
    marginHorizontal: 4,
  },
  notificationBadge: {
    position: 'absolute',
    top: 6,
    right: 6,
    width: 8,
    height: 8,
    borderRadius: 4,
    backgroundColor: '#FF3B30',
  },
  profileImage: {
    width: 32,
    height: 32,
    borderRadius: 16,
    marginLeft: 8,
  },
  categoriesContainer: {
    paddingHorizontal: 16,
    paddingVertical: 12,
  },
  categoriesScroll: {
    flexDirection: 'row',
  },
  categoryButton: {
    paddingHorizontal: 16,
    paddingVertical: 8,
    borderRadius: 20,
    marginRight: 12,
    backgroundColor: '#2C2C2E',
  },
  categoryButtonActive: {
    backgroundColor: '#FF3B30',
  },
  categoryText: {
    fontSize: 14,
    fontWeight: '500',
    color: '#FFFFFF',
  },
  categoryTextActive: {
    color: '#FFFFFF',
  },
  content: {
    paddingHorizontal: 16,
  },
  featuredCard: {
    borderRadius: 12,
    overflow: 'hidden',
    marginBottom: 24,
    position: 'relative',
  },
  featuredImage: {
    width: '100%',
    height: 200,
  },
  featuredOverlay: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    backgroundColor: 'rgba(0,0,0,0.6)',
    padding: 16,
  },
  featuredDate: {
    fontSize: 12,
    color: '#FFFFFF',
    opacity: 0.8,
    marginBottom: 4,
  },
  featuredTitle: {
    fontSize: 18,
    fontWeight: '600',
    color: '#FFFFFF',
    lineHeight: 24,
  },
  expandButton: {
    position: 'absolute',
    top: 16,
    right: 16,
    width: 32,
    height: 32,
    borderRadius: 16,
    backgroundColor: 'rgba(255,255,255,0.2)',
    justifyContent: 'center',
    alignItems: 'center',
  },
  sectionHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 16,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: '600',
    color: '#FFFFFF',
  },
  seeAllText: {
    fontSize: 14,
    color: '#007AFF',
    fontWeight: '500',
  },
  articlesContainer: {
    gap: 16,
  },
  articleCard: {
    flexDirection: 'row',
    backgroundColor: '#2C2C2E',
    borderRadius: 12,
    overflow: 'hidden',
  },
  articleImage: {
    width: 80,
    height: 80,
  },
  articleContent: {
    flex: 1,
    padding: 12,
  },
  articleMeta: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 4,
  },
  articleSource: {
    fontSize: 12,
    color: '#8E8E93',
    fontWeight: '500',
  },
  articleDot: {
    fontSize: 12,
    color: '#8E8E93',
    marginHorizontal: 4,
  },
  articleTitle: {
    fontSize: 14,
    fontWeight: '600',
    color: '#FFFFFF',
    lineHeight: 18,
    marginBottom: 8,
  },
  articleFooter: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  articleDate: {
    fontSize: 12,
    color: '#8E8E93',
    flex: 1,
  },
  articleReadTime: {
    fontSize: 12,
    color: '#8E8E93',
    marginRight: 8,
  },
  bookmarkButton: {
    padding: 4,
    marginRight: 4,
  },
  moreButton: {
    padding: 4,
  },
});

export default HomeScreen;
