import React from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity, Linking } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import Header from '../components/Header';

const AboutScreen = ({ navigation }) => {
  const { theme } = useTheme();
  const { t } = useLanguage();

  const teamMembers = [
    {
      id: 1,
      name: 'Dr. Ahmet Yılmaz',
      role: t('about.roles.projectManager'),
      expertise: t('about.expertise.disasterManagement')
    },
    {
      id: 2,
      name: 'Eng. Fatma Kaya',
      role: t('about.roles.techLead'),
      expertise: t('about.expertise.mobileDevelopment')
    },
    {
      id: 3,
      name: 'Dr. Mehmet Demir',
      role: t('about.roles.dataScientist'),
      expertise: t('about.expertise.earthquakeAnalysis')
    }
  ];

  const features = [
    {
      icon: 'warning',
      title: t('about.features.realTimeAlerts'),
      description: t('about.features.realTimeAlertsDesc')
    },
    {
      icon: 'people',
      title: t('about.features.familyTracking'),
      description: t('about.features.familyTrackingDesc')
    },
    {
      icon: 'map',
      title: t('about.features.interactiveMap'),
      description: t('about.features.interactiveMapDesc')
    },
    {
      icon: 'chatbubbles',
      title: t('about.features.community'),
      description: t('about.features.communityDesc')
    }
  ];

  const handleWebsitePress = () => {
    Linking.openURL('https://afet.net');
  };

  const handleEmailPress = () => {
    Linking.openURL('mailto:info@afet.net');
  };

  const renderFeature = (feature, index) => (
    <View key={index} style={[styles.featureCard, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}>
      <Ionicons
        name={feature.icon}
        size={32}
        color={theme.colors.primary}
        style={styles.featureIcon}
      />
      <View style={styles.featureContent}>
        <Text style={[styles.featureTitle, { color: theme.colors.text }]}>
          {feature.title}
        </Text>
        <Text style={[styles.featureDescription, { color: theme.colors.secondaryText }]}>
          {feature.description}
        </Text>
      </View>
    </View>
  );

  const renderTeamMember = (member) => (
    <View key={member.id} style={[styles.teamCard, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}>
      <View style={[styles.avatar, { backgroundColor: theme.colors.primary }]}>
        <Text style={styles.avatarText}>
          {member.name.split(' ').map(n => n[0]).join('')}
        </Text>
      </View>
      <View style={styles.memberInfo}>
        <Text style={[styles.memberName, { color: theme.colors.text }]}>
          {member.name}
        </Text>
        <Text style={[styles.memberRole, { color: theme.colors.primary }]}>
          {member.role}
        </Text>
        <Text style={[styles.memberExpertise, { color: theme.colors.secondaryText }]}>
          {member.expertise}
        </Text>
      </View>
    </View>
  );

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <Header
        title={t('about.title')}
        showBackButton={true}
        onBackPress={() => navigation.goBack()}
      />
      
      <ScrollView style={styles.content} showsVerticalScrollIndicator={false}>
        {/* Logo ve Başlık */}
        <View style={styles.logoSection}>
          <View style={[styles.logo, { backgroundColor: theme.colors.primary }]}>
            <Text style={styles.logoText}>A</Text>
            <Ionicons name="home" size={16} color="white" style={styles.logoIcon} />
          </View>
          <Text style={[styles.appName, { color: theme.colors.text }]}>Afet.net</Text>
          <Text style={[styles.version, { color: theme.colors.secondaryText }]}>v1.0.0</Text>
        </View>

        {/* Misyon */}
        <View style={styles.section}>
          <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>
            {t('about.mission')}
          </Text>
          <Text style={[styles.missionText, { color: theme.colors.secondaryText }]}>
            {t('about.missionDescription')}
          </Text>
        </View>

        {/* Özellikler */}
        <View style={styles.section}>
          <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>
            {t('about.features.title')}
          </Text>
          {features.map(renderFeature)}
        </View>

        {/* Ekip */}
        <View style={styles.section}>
          <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>
            {t('about.team')}
          </Text>
          {teamMembers.map(renderTeamMember)}
        </View>

        {/* İletişim */}
        <View style={styles.section}>
          <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>
            {t('about.contact')}
          </Text>
          
          <TouchableOpacity
            style={[styles.contactItem, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}
            onPress={handleWebsitePress}
          >
            <Ionicons name="globe" size={24} color={theme.colors.primary} />
            <Text style={[styles.contactText, { color: theme.colors.text }]}>www.afet.net</Text>
            <Ionicons name="open-outline" size={20} color={theme.colors.secondaryText} />
          </TouchableOpacity>

          <TouchableOpacity
            style={[styles.contactItem, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}
            onPress={handleEmailPress}
          >
            <Ionicons name="mail" size={24} color={theme.colors.primary} />
            <Text style={[styles.contactText, { color: theme.colors.text }]}>info@afet.net</Text>
            <Ionicons name="open-outline" size={20} color={theme.colors.secondaryText} />
          </TouchableOpacity>
        </View>

        {/* Telif Hakkı */}
        <View style={styles.footer}>
          <Text style={[styles.copyright, { color: theme.colors.tertiaryText }]}>
            © 2024 Afet.net. {t('about.allRightsReserved')}
          </Text>
        </View>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  content: {
    flex: 1,
    paddingHorizontal: 20,
  },
  logoSection: {
    alignItems: 'center',
    paddingVertical: 30,
  },
  logo: {
    width: 80,
    height: 80,
    borderRadius: 40,
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 16,
    position: 'relative',
  },
  logoText: {
    fontSize: 36,
    fontWeight: 'bold',
    color: 'white',
  },
  logoIcon: {
    position: 'absolute',
    bottom: 8,
    right: 8,
  },
  appName: {
    fontSize: 28,
    fontWeight: 'bold',
    marginBottom: 4,
  },
  version: {
    fontSize: 16,
  },
  section: {
    marginBottom: 30,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: '600',
    marginBottom: 16,
  },
  missionText: {
    fontSize: 16,
    lineHeight: 24,
    textAlign: 'justify',
  },
  featureCard: {
    flexDirection: 'row',
    padding: 16,
    borderRadius: 12,
    borderWidth: 1,
    marginBottom: 12,
  },
  featureIcon: {
    marginRight: 16,
    marginTop: 4,
  },
  featureContent: {
    flex: 1,
  },
  featureTitle: {
    fontSize: 16,
    fontWeight: '600',
    marginBottom: 4,
  },
  featureDescription: {
    fontSize: 14,
    lineHeight: 20,
  },
  teamCard: {
    flexDirection: 'row',
    padding: 16,
    borderRadius: 12,
    borderWidth: 1,
    marginBottom: 12,
  },
  avatar: {
    width: 50,
    height: 50,
    borderRadius: 25,
    justifyContent: 'center',
    alignItems: 'center',
    marginRight: 16,
  },
  avatarText: {
    color: 'white',
    fontSize: 16,
    fontWeight: 'bold',
  },
  memberInfo: {
    flex: 1,
  },
  memberName: {
    fontSize: 16,
    fontWeight: '600',
    marginBottom: 2,
  },
  memberRole: {
    fontSize: 14,
    fontWeight: '500',
    marginBottom: 2,
  },
  memberExpertise: {
    fontSize: 12,
  },
  contactItem: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 16,
    borderRadius: 12,
    borderWidth: 1,
    marginBottom: 12,
  },
  contactText: {
    flex: 1,
    fontSize: 16,
    marginLeft: 12,
  },
  footer: {
    alignItems: 'center',
    paddingVertical: 20,
  },
  copyright: {
    fontSize: 12,
    textAlign: 'center',
  },
});

export default AboutScreen;
