import React from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  Dimensions,
  Image,
} from 'react-native';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { Ionicons } from '@expo/vector-icons';
import { LinearGradient } from 'expo-linear-gradient';

const { width, height } = Dimensions.get('window');

const WelcomeScreen = ({ navigation }) => {
  const insets = useSafeAreaInsets();

  const handleLogin = () => {
    navigation.navigate('Login');
  };

  const handleRegister = () => {
    navigation.navigate('Register');
  };

  return (
    <View style={[styles.container, { paddingTop: insets.top }]}>
      {/* Hero Section */}
      <View style={styles.heroContainer}>
        {/* Logo ve Uygulama Adı */}
        <View style={styles.logoContainer}>
          <View style={styles.logoWrapper}>
            <LinearGradient
              colors={['#FF6B35', '#FF8E53']}
              style={styles.logoGradient}
            >
              <Ionicons name="shield-checkmark" size={48} color="#FFFFFF" />
            </LinearGradient>
          </View>
          <Text style={styles.appName}>AfetNet</Text>
          <Text style={styles.appTagline}>Güvenliğiniz Bizim Önceliğimiz</Text>
        </View>

        {/* Özellik İkonları */}
        <View style={styles.featuresContainer}>
          <View style={styles.featureItem}>
            <View style={[styles.featureIcon, { backgroundColor: '#FF6B35' }]}>
              <Ionicons name="warning" size={24} color="#FFFFFF" />
            </View>
            <Text style={styles.featureText}>Acil Durum{"\n"}Bildirimi</Text>
          </View>

          <View style={styles.featureItem}>
            <View style={[styles.featureIcon, { backgroundColor: '#FF8E53' }]}>
              <Ionicons name="people" size={24} color="#FFFFFF" />
            </View>
            <Text style={styles.featureText}>Aile{"\n"}Takibi</Text>
          </View>

          <View style={styles.featureItem}>
            <View style={[styles.featureIcon, { backgroundColor: '#FFA726' }]}>
              <Ionicons name="location" size={24} color="#FFFFFF" />
            </View>
            <Text style={styles.featureText}>Güvenli{"\n"}Bölgeler</Text>
          </View>
        </View>

        {/* Güvenlik İllüstrasyonu */}
        <View style={styles.illustrationContainer}>
          <View style={styles.safetyIllustration}>
            {/* Ana Kalkan */}
            <View style={styles.mainShield}>
              <Ionicons name="shield-checkmark" size={80} color="#FF6B35" />
            </View>

            {/* Çevredeki Güvenlik Elementleri */}
            <View style={[styles.securityElement, styles.element1]}>
              <Ionicons name="home" size={20} color="#FF8E53" />
            </View>
            <View style={[styles.securityElement, styles.element2]}>
              <Ionicons name="car" size={20} color="#FFA726" />
            </View>
            <View style={[styles.securityElement, styles.element3]}>
              <Ionicons name="medical" size={20} color="#FF6B35" />
            </View>
            <View style={[styles.securityElement, styles.element4]}>
              <Ionicons name="call" size={20} color="#FF8E53" />
            </View>
          </View>
        </View>
      </View>

      {/* Metin Alanı */}
      <View style={styles.textContainer}>
        <Text style={styles.title}>Afetlere Karşı{"\n"}Hazırlıklı Olun</Text>
        <Text style={styles.subtitle}>
          Ailenizin güvenliğini sağlayın, acil durumlarda{"\n"}hızlı müdahale edin ve güvenli bölgeleri keşfedin
        </Text>
      </View>

      {/* Buton Alanı */}
      <View style={styles.buttonContainer}>
        <TouchableOpacity style={styles.loginButton} onPress={handleLogin}>
          <LinearGradient
            colors={['#FF6B35', '#FF8E53']}
            style={styles.loginGradient}
          >
            <Text style={styles.loginButtonText}>Giriş Yap</Text>
          </LinearGradient>
        </TouchableOpacity>

        <TouchableOpacity style={styles.registerButton} onPress={handleRegister}>
          <Text style={styles.registerButtonText}>Kayıt Ol</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FFFFFF',
    paddingHorizontal: 24,
  },
  heroContainer: {
    flex: 1,
    justifyContent: 'space-between',
    paddingVertical: 40,
  },
  logoContainer: {
    alignItems: 'center',
    marginTop: 20,
  },
  logoWrapper: {
    marginBottom: 16,
  },
  logoGradient: {
    width: 100,
    height: 100,
    borderRadius: 50,
    justifyContent: 'center',
    alignItems: 'center',
    shadowColor: '#FF6B35',
    shadowOffset: {
      width: 0,
      height: 8,
    },
    shadowOpacity: 0.3,
    shadowRadius: 16,
    elevation: 12,
  },
  appName: {
    fontSize: 32,
    fontWeight: 'bold',
    color: '#FF6B35',
    marginBottom: 8,
    letterSpacing: 1,
  },
  appTagline: {
    fontSize: 16,
    color: '#666666',
    textAlign: 'center',
  },
  featuresContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    paddingHorizontal: 20,
    marginVertical: 30,
  },
  featureItem: {
    alignItems: 'center',
    flex: 1,
  },
  featureIcon: {
    width: 60,
    height: 60,
    borderRadius: 30,
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 12,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 4,
    },
    shadowOpacity: 0.2,
    shadowRadius: 8,
    elevation: 6,
  },
  featureText: {
    fontSize: 12,
    color: '#666666',
    textAlign: 'center',
    lineHeight: 16,
    fontWeight: '500',
  },
  illustrationContainer: {
    justifyContent: 'center',
    alignItems: 'center',
    paddingVertical: 20,
  },
  safetyIllustration: {
    width: 200,
    height: 200,
    position: 'relative',
    justifyContent: 'center',
    alignItems: 'center',
  },
  mainShield: {
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#FFF5F0',
    borderRadius: 60,
    width: 120,
    height: 120,
    shadowColor: '#FF6B35',
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.2,
    shadowRadius: 12,
    elevation: 8,
  },
  securityElement: {
    position: 'absolute',
    width: 40,
    height: 40,
    borderRadius: 20,
    backgroundColor: '#FFFFFF',
    justifyContent: 'center',
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
  },
  element1: {
    top: 20,
    left: 20,
  },
  element2: {
    top: 20,
    right: 20,
  },
  element3: {
    bottom: 20,
    left: 20,
  },
  element4: {
    bottom: 20,
    right: 20,
  },
  // Metin Stilleri
  textContainer: {
    paddingHorizontal: 24,
    paddingBottom: 40,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#1F2937',
    textAlign: 'center',
    marginBottom: 12,
    lineHeight: 36,
  },
  subtitle: {
    fontSize: 16,
    color: '#666666',
    textAlign: 'center',
    lineHeight: 24,
    marginBottom: 32,
    paddingHorizontal: 20,
  },
  // Buton Stilleri
  buttonContainer: {
    paddingHorizontal: 24,
    paddingBottom: 40,
    gap: 16,
  },
  loginButton: {
    borderRadius: 16,
    shadowColor: '#FF6B35',
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.3,
    shadowRadius: 12,
    elevation: 8,
  },
  loginGradient: {
    paddingVertical: 18,
    borderRadius: 16,
    alignItems: 'center',
  },
  loginButtonText: {
    color: '#FFFFFF',
    fontSize: 18,
    fontWeight: '700',
    letterSpacing: 0.5,
  },
  registerButton: {
    backgroundColor: 'transparent',
    paddingVertical: 18,
    borderRadius: 16,
    alignItems: 'center',
    borderWidth: 2,
    borderColor: '#FF6B35',
  },
  registerButtonText: {
    color: '#FF6B35',
    fontSize: 18,
    fontWeight: '600',
    letterSpacing: 0.5,
  },
});

export default WelcomeScreen;
