import React, { useState, useEffect, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  TouchableOpacity,
  ScrollView,
  Platform,
  ActivityIndicator,
  Alert,
  Animated,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useNavigation, useRoute } from '@react-navigation/native';
import { useLanguage } from '../contexts/LanguageContext';
import { useTheme } from '../contexts/ThemeContext';
import earthquakeService from '../services/earthquakeService';

// Web iÃ§in Leaflet import'larÄ± - sadece web platformunda
let MapContainer, TileLayer, Marker, Popup, L;
if (Platform.OS === 'web') {
  const leaflet = require('react-leaflet');
  MapContainer = leaflet.MapContainer;
  TileLayer = leaflet.TileLayer;
  Marker = leaflet.Marker;
  Popup = leaflet.Popup;
  L = require('leaflet');

  // CSS'i dinamik olarak yÃ¼kle
  if (typeof document !== 'undefined') {
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = 'https://unpkg.com/leaflet@1.7.1/dist/leaflet.css';
    document.head.appendChild(link);
  }

  // Leaflet marker icon'larÄ±nÄ± dÃ¼zelt
  if (L && L.Icon && L.Icon.Default) {
    delete L.Icon.Default.prototype._getIconUrl;
    L.Icon.Default.mergeOptions({
      iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
      iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
      shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
    });
  }
}

const EarthquakeDetailScreen = () => {
  const { t } = useLanguage();
  const { theme } = useTheme();
  const navigation = useNavigation();
  const route = useRoute();
  const insets = useSafeAreaInsets();

  const { earthquake } = route.params;
  const [loading, setLoading] = useState(false);
  const [mapCenter, setMapCenter] = useState([earthquake.latitude || 39.9334, earthquake.longitude || 32.8597]);
  const [showDetails, setShowDetails] = useState(true);
  const [isFullMapView, setIsFullMapView] = useState(false);
  const scrollY = useRef(new Animated.Value(0)).current;
  const mapHeight = useRef(new Animated.Value(300)).current;

  useEffect(() => {
    if (earthquake.latitude && earthquake.longitude) {
      setMapCenter([earthquake.latitude, earthquake.longitude]);
    }
  }, [earthquake]);

  const toggleDetails = () => {
    setShowDetails(!showDetails);
  };

  const toggleFullMapView = () => {
    setIsFullMapView(!isFullMapView);
    if (!isFullMapView) {
      // Tam harita moduna geÃ§erken harita yÃ¼ksekliÄini artÄ±r
      Animated.timing(mapHeight, {
        toValue: 600,
        duration: 300,
        useNativeDriver: false,
      }).start();
    } else {
      // Normal moda dÃ¶nerken harita yÃ¼ksekliÄini eski haline getir
      Animated.timing(mapHeight, {
        toValue: 300,
        duration: 300,
        useNativeDriver: false,
      }).start();
    }
  };

  const handleScroll = Animated.event(
    [{ nativeEvent: { contentOffset: { y: scrollY } } }],
    {
      useNativeDriver: false,
      listener: (event) => {
        const offsetY = event.nativeEvent.contentOffset.y;
        if (!isFullMapView) {
          // Scroll pozisyonuna gÃ¶re harita yÃ¼ksekliÄini ayarla
          const newHeight = Math.max(100, 300 - offsetY * 0.8);
          mapHeight.setValue(newHeight);
        }
      },
    }
  );

  const renderMap = () => {
    if (Platform.OS === 'web' && MapContainer) {
      return (
        <MapContainer
          center={mapCenter}
          zoom={10}
          style={{ height: '100%', width: '100%' }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          {earthquake.latitude && earthquake.longitude && (
            <Marker position={[earthquake.latitude, earthquake.longitude]}>
              <Popup>
                <div>
                  <strong>{earthquake.magnitude} BÃ¼yÃ¼klÃ¼ÄÃ¼nde Deprem</strong><br/>
                  {earthquake.location}<br/>
                  Derinlik: {earthquake.depth} km
                </div>
              </Popup>
            </Marker>
          )}
        </MapContainer>
      );
    } else {
      // Mobile iÃ§in placeholder
      return (
        <View style={styles.mapPlaceholder}>
          <Ionicons name="map-outline" size={64} color={theme.colors.secondaryText} />
          <Text style={[styles.mapPlaceholderText, { color: theme.colors.secondaryText }]}>
            Deprem konumu haritasÄ±
          </Text>
        </View>
      );
    }
  };

  const getSeverityColor = (magnitude) => {
    if (magnitude >= 7.0) return '#8B0000'; // Ãok yÃ¼ksek - koyu kÄ±rmÄ±zÄ±
    if (magnitude >= 6.0) return '#FF0000'; // YÃ¼ksek - kÄ±rmÄ±zÄ±
    if (magnitude >= 5.0) return '#FF4500'; // Orta-yÃ¼ksek - turuncu kÄ±rmÄ±zÄ±
    if (magnitude >= 4.0) return '#FF8C00'; // Orta - koyu turuncu
    if (magnitude >= 3.0) return '#FFA500'; // DÃ¼ÅÃ¼k-orta - turuncu
    if (magnitude >= 2.0) return '#FFD700'; // DÃ¼ÅÃ¼k - altÄ±n sarÄ±sÄ±
    return '#90EE90'; // Ãok dÃ¼ÅÃ¼k - aÃ§Ä±k yeÅil
  };

  return (
    <SafeAreaView style={[styles.container, { backgroundColor: theme.colors.background }]}>
      {/* Header */}
      <View style={[styles.header, { backgroundColor: theme.colors.primary }]}>
        <TouchableOpacity 
          style={styles.backButton}
          onPress={() => navigation.goBack()}
        >
          <Ionicons name="arrow-back" size={24} color={theme.colors.onPrimary} />
        </TouchableOpacity>
        <Text style={[styles.headerTitle, { color: theme.colors.onPrimary }]}>Rasathane</Text>
        <View style={styles.headerActions}>
          <TouchableOpacity style={styles.headerButton} onPress={toggleFullMapView}>
            <Ionicons name={isFullMapView ? "contract-outline" : "expand-outline"} size={24} color={theme.colors.onPrimary} />
          </TouchableOpacity>
          <TouchableOpacity style={styles.headerButton}>
            <Ionicons name="share-outline" size={24} color={theme.colors.onPrimary} />
          </TouchableOpacity>
        </View>
      </View>

      <ScrollView 
        style={styles.content} 
        showsVerticalScrollIndicator={false}
        onScroll={handleScroll}
        scrollEventThrottle={16}
      >
        {/* Map Section */}
        <Animated.View style={[
          styles.mapSection, 
          isFullMapView && styles.fullMapSection,
          !isFullMapView && { height: mapHeight }
        ]}>
          <View style={styles.mapContainer}>
            {renderMap()}
          </View>

          {/* Map Overlay with Earthquake Info */}
          <View style={styles.mapOverlay}>
            <View style={[styles.magnitudeCircle, { backgroundColor: getSeverityColor(earthquake.magnitude) }]}>
              <Text style={styles.magnitudeText}>{earthquake.magnitude}</Text>
            </View>
          </View>

          {/* Map Controls */}
          <View style={styles.mapControls}>
            <TouchableOpacity 
              style={[styles.mapControlButton, { backgroundColor: theme.colors.cardBackground }]}
              onPress={toggleDetails}
            >
              <Ionicons 
