import React, { useState, useEffect, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  TouchableOpacity,
  ScrollView,
  Platform,
  ActivityIndicator,
  Alert,
  Animated,
  Dimensions,
} from 'react-native';
import { PanGestureHandler, State } from 'react-native-gesture-handler';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useNavigation, useRoute } from '@react-navigation/native';
import { useLanguage } from '../contexts/LanguageContext';
import { useTheme } from '../contexts/ThemeContext';
import earthquakeService from '../services/earthquakeService';

// Platform-specific map imports
let MapContainer, TileLayer, Marker, Popup, L, MapView, MapMarker;

if (Platform.OS === 'web') {
  // Web için Leaflet
  const leaflet = require('react-leaflet');
  MapContainer = leaflet.MapContainer;
  TileLayer = leaflet.TileLayer;
  Marker = leaflet.Marker;
  Popup = leaflet.Popup;
  L = require('leaflet');

  // CSS'i dinamik olarak yükle
  if (typeof document !== 'undefined') {
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = 'https://unpkg.com/leaflet@1.7.1/dist/leaflet.css';
    document.head.appendChild(link);
  }

  // Leaflet marker icon'larını düzelt
  if (L && L.Icon && L.Icon.Default) {
    delete L.Icon.Default.prototype._getIconUrl;
    L.Icon.Default.mergeOptions({
      iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
      iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
      shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
    });
  }
} else {
  // Mobile için React Native Maps
  const maps = require('react-native-maps');
  MapView = maps.default;
  MapMarker = maps.Marker;
}

const EarthquakeDetailScreen = () => {
  const { t } = useLanguage();
  const { theme } = useTheme();
  const navigation = useNavigation();
  const route = useRoute();
  const insets = useSafeAreaInsets();

  const { earthquake } = route.params;
  const [loading, setLoading] = useState(false);
  const [mapCenter, setMapCenter] = useState([earthquake.latitude || 39.9334, earthquake.longitude || 32.8597]);

  // Slider mekanizması için state'ler
  const [sliderPosition, setSliderPosition] = useState('down');
  const slideAnim = useRef(new Animated.Value(0.6)).current;
  const mapSizeAnim = useRef(new Animated.Value(0)).current;
  const panY = useRef(new Animated.Value(0)).current;
  const { height: screenHeight } = Dimensions.get('window');
  const detailsHeight = screenHeight * 0.6;

  useEffect(() => {
    if (earthquake.latitude && earthquake.longitude) {
      setMapCenter([earthquake.latitude, earthquake.longitude]);
    }
  }, [earthquake]);

  const toggleSlider = () => {
    const newPosition = sliderPosition === 'up' ? 'down' : 'up';
    setSliderPosition(newPosition);

    Animated.parallel([
      Animated.spring(slideAnim, {
        toValue: newPosition === 'up' ? 0.1 : 0.6,
        tension: 100,
        friction: 8,
        useNativeDriver: false,
      }),
      Animated.spring(mapSizeAnim, {
        toValue: newPosition === 'up' ? 1 : 0,
        tension: 100,
        friction: 8,
        useNativeDriver: false,
      })
    ]).start();
  };

  // Scroll ile slider kontrolü
  const handleScroll = (event) => {
    const scrollY = event.nativeEvent.contentOffset.y;
    const threshold = 50;

    if (scrollY > threshold && sliderPosition === 0) {
      toggleSlider();
    } else if (scrollY < -threshold && sliderPosition === 1) {
      toggleSlider();
    }
  };

  // Pan gesture handler
  const onPanGestureEvent = Animated.event(
    [{ nativeEvent: { translationY: panY } }],
    { useNativeDriver: false }
  );

  const onPanHandlerStateChange = (event) => {
    if (event.nativeEvent.oldState === State.ACTIVE) {
      const { translationY, velocityY } = event.nativeEvent;
      const threshold = screenHeight * 0.2; // %20 threshold

      // Yukarı çekme (negatif değer) - haritayı büyüt
      if (translationY < -threshold || velocityY < -1000) {
        setSliderPosition('up');
        Animated.parallel([
          Animated.spring(slideAnim, {
            toValue: 0.1,
            tension: 100,
            friction: 8,
            useNativeDriver: false,
          }),
          Animated.spring(mapSizeAnim, {
            toValue: 1,
            tension: 100,
            friction: 8,
            useNativeDriver: false,
          })
        ]).start();
      }
      // Aşağı çekme (pozitif değer) - detayları göster
      else if (translationY > threshold || velocityY > 1000) {
        setSliderPosition('down');
        Animated.parallel([
          Animated.spring(slideAnim, {
            toValue: 0.6,
            tension: 100,
            friction: 8,
            useNativeDriver: false,
          }),
          Animated.spring(mapSizeAnim, {
            toValue: 0,
            tension: 100,
            friction: 8,
            useNativeDriver: false,
          })
        ]).start();
      } else {
        // Orta konuma geri dön
        Animated.parallel([
          Animated.spring(slideAnim, {
            toValue: sliderPosition === 'up' ? 0.1 : 0.6,
            tension: 100,
            friction: 8,
            useNativeDriver: false,
          }),
          Animated.spring(mapSizeAnim, {
            toValue: sliderPosition === 'up' ? 1 : 0,
            tension: 100,
            friction: 8,
            useNativeDriver: false,
          })
        ]).start();
      }

      // Reset pan value
      panY.setValue(0);
    }
  };

  const renderMap = () => {
    if (Platform.OS === 'web' && MapContainer) {
      return (
        <MapContainer
          center={mapCenter}
          zoom={10}
          style={{ height: '100%', width: '100%' }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          {earthquake.latitude && earthquake.longitude && (
            <Marker position={[earthquake.latitude, earthquake.longitude]}>
              <Popup>
                <div>
                  <strong>{earthquake.magnitude} Büyüklüğünde Deprem</strong><br/>
                  {earthquake.location}<br/>
                  Derinlik: {earthquake.depth} km
                </div>
              </Popup>
            </Marker>
          )}
        </MapContainer>
      );
    } else if (MapView) {
      // Mobile için React Native Maps
      return (
        <MapView
          style={{ flex: 1 }}
          initialRegion={{
            latitude: earthquake.latitude || 39.9334,
            longitude: earthquake.longitude || 32.8597,
            latitudeDelta: 0.5,
            longitudeDelta: 0.5,
          }}
          showsUserLocation={true}
          showsMyLocationButton={true}
        >
          {earthquake.latitude && earthquake.longitude && (
            <MapMarker
              coordinate={{
                latitude: earthquake.latitude,
                longitude: earthquake.longitude,
              }}
              title={`${earthquake.magnitude} Büyüklüğünde Deprem`}
              description={`${earthquake.location} - Derinlik: ${earthquake.depth} km`}
            />
          )}
        </MapView>
      );
    } else {
      // Fallback için placeholder
      return (
        <View style={styles.mapPlaceholder}>
          <Ionicons name="map-outline" size={64} color={theme.colors.secondaryText} />
          <Text style={[styles.mapPlaceholderText, { color: theme.colors.secondaryText }]}>
            Deprem konumu haritası
          </Text>
        </View>
      );
    }
  };

  const getSeverityColor = (magnitude) => {
    if (magnitude >= 7.0) return '#8B0000'; // Çok yüksek - koyu kırmızı
    if (magnitude >= 6.0) return '#FF0000'; // Yüksek - kırmızı
    if (magnitude >= 5.0) return '#FF4500'; // Orta-yüksek - turuncu kırmızı
    if (magnitude >= 4.0) return '#FF8C00'; // Orta - koyu turuncu
    if (magnitude >= 3.0) return '#FFA500'; // Düşük-orta - turuncu
    if (magnitude >= 2.0) return '#FFD700'; // Düşük - altın sarısı
    return '#90EE90'; // Çok düşük - açık yeşil
  };

  return (
    <SafeAreaView style={[styles.container, { backgroundColor: theme.colors.background }]}>
      {/* Header */}
      <View style={[styles.header, { backgroundColor: theme.colors.primary }]}>
        <TouchableOpacity 
          style={styles.backButton}
          onPress={() => navigation.goBack()}
        >
          <Ionicons name="arrow-back" size={24} color={theme.colors.onPrimary} />
        </TouchableOpacity>
        <Text style={[styles.headerTitle, { color: theme.colors.onPrimary }]}>Deprem Detayı</Text>
        <TouchableOpacity style={styles.shareButton}>
          <Ionicons name="share-outline" size={24} color={theme.colors.onPrimary} />
        </TouchableOpacity>
      </View>

      <View style={styles.content}>
        {/* Map Section - Dinamik boyutlandırma */}
        <Animated.View 
          style={[
            styles.mapSection,
            {
              height: mapSizeAnim.interpolate({
                inputRange: [0.5, 0.9],
                outputRange: ['50%', '90%'],
              })
            }
          ]}
        >
          <View style={styles.mapContainer}>
            {renderMap()}
          </View>

          {/* Slider Toggle Button */}
          <TouchableOpacity 
            style={[styles.sliderToggle, { backgroundColor: theme.colors.primary }]}
            onPress={toggleSlider}
          >
            <Ionicons 
              name={sliderPosition === 0 ? "chevron-up" : "chevron-down"} 
              size={24} 
              color={theme.colors.onPrimary} 
            />
          </TouchableOpacity>
        </Animated.View>

        {/* Sliding Details Panel */}
         <Animated.View 
           style={[
             styles.detailsPanel,
             {
               backgroundColor: theme.colors.background,
               height: mapSizeAnim.interpolate({
                 inputRange: [0.5, 0.9],
                 outputRange: ['50%', '10%'],
               }),
               transform: [{
                 translateY: slideAnim.interpolate({
                   inputRange: [0, 1],
                   outputRange: [0, detailsHeight - 100], // 100px görünür kalacak
                 })
               }]
             }
           ]}
         >
           {/* Panel Handle */}
           <View style={styles.panelHandle}>
             <View style={[styles.handleBar, { backgroundColor: theme.colors.border }]} />
           </View>

           <ScrollView 
             style={styles.detailsScrollView}
             showsVerticalScrollIndicator={true}
             onScroll={handleScroll}
             scrollEventThrottle={16}
             bounces={true}
             alwaysBounceVertical={true}
           >
            {/* Earthquake Details Card */}
            <View style={[styles.detailsCard, { backgroundColor: '#FEFEFE' }]}>
              <View style={styles.cardHeader}>
                <Text style={[styles.locationTitle, { color: theme.colors.text }]}>
                  {earthquake.location.replace('(BALIKESİR)', '').replace('(', '').replace(')', '').trim()}
                </Text>
                <Text style={[styles.locationSubtitle, { color: theme.colors.secondaryText }]}>
                  (BALIKESİR)
                </Text>
              </View>

              <View style={styles.detailsGrid}>
                <View style={styles.detailRow}>
                  <View style={styles.detailItem}>
                    <Ionicons name="calendar-outline" size={16} color={theme.colors.secondaryText} />
                    <Text style={[styles.detailLabel, { color: theme.colors.secondaryText }]}>Tarih</Text>
                    <Text style={[styles.detailValue, { color: theme.colors.text }]}>{earthquake.dateOnly || '18.08.2025'}</Text>
                  </View>
                  <View style={styles.detailItem}>
                    <Ionicons name="time-outline" size={16} color={theme.colors.secondaryText} />
                    <Text style={[styles.detailLabel, { color: theme.colors.secondaryText }]}>Saat</Text>
                    <Text style={[styles.detailValue, { color: theme.colors.text }]}>{earthquake.displayTime || '14:52:59'}</Text>
                  </View>
                </View>

                <View style={styles.detailRow}>
                  <View style={styles.detailItem}>
                    <Ionicons name="resize-outline" size={16} color={theme.colors.secondaryText} />
                    <Text style={[styles.detailLabel, { color: theme.colors.secondaryText }]}>Derinlik (km)</Text>
                    <Text style={[styles.detailValue, { color: theme.colors.text }]}>{earthquake.depth || '8.3'}</Text>
                  </View>
                  <View style={styles.detailItem}>
                    <Ionicons name="diamond-outline" size={16} color={theme.colors.secondaryText} />
                    <Text style={[styles.detailLabel, { color: theme.colors.secondaryText }]}>Mesafe (km)</Text>
                    <Text style={[styles.detailValue, { color: theme.colors.text }]}>212.46</Text>
                  </View>
                </View>

                <View style={styles.detailRow}>
                  <View style={styles.detailItem}>
                    <Ionicons name="globe-outline" size={16} color={theme.colors.secondaryText} />
                    <Text style={[styles.detailLabel, { color: theme.colors.secondaryText }]}>Enlem</Text>
                    <Text style={[styles.detailValue, { color: theme.colors.text }]}>{earthquake.latitude?.toFixed(4) || '39.2533'}</Text>
                  </View>
                  <View style={styles.detailItem}>
                    <Ionicons name="globe-outline" size={16} color={theme.colors.secondaryText} />
                    <Text style={[styles.detailLabel, { color: theme.colors.secondaryText }]}>Boylam</Text>
                    <Text style={[styles.detailValue, { color: theme.colors.text }]}>{earthquake.longitude?.toFixed(4) || '28.0837'}</Text>
                  </View>
                </View>
              </View>
            </View>

            {/* Ek bilgiler için boşluk */}
            <View style={{ height: 100 }} />
          </ScrollView>
        </Animated.View>
      </View>
    </SafeAreaView>
  );
};

