import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  Image,
  RefreshControl,
  ActivityIndicator,
  Alert,
  FlatList,
  TextInput,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import { useAuth } from '../contexts/AuthContext';

const HomeScreen = ({ navigation }) => {
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');
  const { theme } = useTheme();
  const { t, language } = useLanguage();
  const { user } = useAuth();

  const [posts, setPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('trending');
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearchVisible, setIsSearchVisible] = useState(false);

  const categories = [
    { id: 'deprem', name: 'Deprem', icon: 'deprem-outline' },
    { id: 'yangin', name: 'Yangın', icon: 'yangin-outline' },
    { id: 'sel', name: 'Sel', icon: 'sel-outline' },
    { id: 'heyelan', name: 'Heyelan', icon: 'heyelan-outline' },
  ];

  // Sample news posts matching the design
  const sampleNewsPosts = [
    {
      id: 1,
      title: "Deprem: afetnet.com'un yeni ücretsiz uygulaması, çocukları okumaya öğretir",
      content: 'Google, sadece afetnet adlı ücretsiz mobil uygulamasını beta etiketine açtı, bu uygulama, çocukların okumayı dinlemeden öğrenmesini sağlar.',
      category: 'deprem',
      image_url: 'https://fileapi.bereket.com.tr/api/v1/image/public/57fc57d41cbb4339b1ab352195333519.jpeg',
      created_at: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString(),
      views_count: 2450,
      comments_count: 89,
      likes_count: 156
    },
    {
      id: 2,
      title: 'Yangın: Google\'ın yeni ücretsiz uygulaması, çocukları okumaya öğretir',
      content: 'Google, sadece Rivet adlı ücretsiz mobil uygulamasını beta etiketine açtı, bu uygulama, çocukların okumayı dinlemeden öğrenmesini sağlar.',
      category: 'sel',
      image_url: 'https://ichef.bbci.co.uk/ace/ws/640/cpsprodpb/9482/live/92a48800-1f13-11ee-9c7b-1b16c0025d82.jpg.webp',
      created_at: new Date(Date.now() - 4 * 60 * 60 * 1000).toISOString(),
      views_count: 1890,
      comments_count: 67,
      likes_count: 234
    },
    {
      id: 3,
      title: 'Clickbait fashion! You won\'t believe what brands are doing now',
      content: 'The fashion industry continues to evolve with surprising new trends and marketing strategies.',
      category: 'fashion',
      image_url: 'https://images.unsplash.com/photo-1445205170230-053b83016050?w=400&h=200&fit=crop',
      created_at: new Date(Date.now() - 6 * 60 * 60 * 1000).toISOString(),
      views_count: 3567,
      comments_count: 145,
      likes_count: 456
    },
    {
      id: 4,
      title: 'Sel: Google\'ın yeni ücretsiz uygulaması, çocukları okumaya öğretir',
      content: 'Google, sadece Rivet adlı ücretsiz mobil uygulamasını beta etiketine açtı, bu uygulama, çocukların okumayı dinlemeden öğrenmesini sağlar.',
      category: 'deprem',
      image_url: 'https://static.euronews.com/articles/stories/03/27/33/38/1536x864_cmsv2_a4234112-b081-5994-898c-c84e9b4379a0-3273338.jpg',
      created_at: new Date(Date.now() - 8 * 60 * 60 * 1000).toISOString(),
      views_count: 1234,
      comments_count: 78,
      likes_count: 189
    },
    {
      id: 5,
      title: 'Championship Finals: Underdog Team Secures Victory',
      content: 'Against all odds, the rookie team defeats the defending champions in a nail-biting finale.',
      category: 'sports',
      image_url: 'https://images.unsplash.com/photo-1461896836934-ffe607ba8211?w=400&h=200&fit=crop',
      created_at: new Date(Date.now() - 12 * 60 * 60 * 1000).toISOString(),
      views_count: 4567,
      comments_count: 234,
      likes_count: 678
    },
    {
      id: 6,
      title: 'Market Analysis: Cryptocurrency Reaches New Milestone',
      content: 'Digital currencies show unprecedented growth as institutional adoption continues to rise.',
      category: 'business',
      image_url: 'https://images.unsplash.com/photo-1611974789855-9c2a0a7236a3?w=400&h=200&fit=crop',
      created_at: new Date(Date.now() - 16 * 60 * 60 * 1000).toISOString(),
      views_count: 2890,
      comments_count: 156,
      likes_count: 345
    }
  ];

  // Fetch posts from API
  const fetchPosts = async () => {
    try {
      setIsLoading(true);
      // Using sample news posts for now
      setTimeout(() => {
        setPosts(sampleNewsPosts);
        setIsLoading(false);
        setIsRefreshing(false);
      }, 1000);
    } catch (error) {
      console.error('Error fetching posts:', error);
      Alert.alert(
        'Error',
        'An error occurred while loading posts.'
      );
      setIsLoading(false);
      setIsRefreshing(false);
    }
  };

  // Handle refresh
  const handleRefresh = () => {
    setIsRefreshing(true);
    fetchPosts();
  };

  // Format date
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));

    if (diffInHours < 1) {
      return 'Just now';
    } else if (diffInHours < 24) {
      return `${diffInHours}h ago`;
    } else {
      const diffInDays = Math.floor(diffInHours / 24);
      return `${diffInDays}d ago`;
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#ffffff',
    },
    header: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      paddingHorizontal: 20,
      paddingVertical: 16,
      backgroundColor: '#ffffff',
    },
    headerTitle: {
      fontSize: 24,
      fontWeight: '700',
      color: '#000000',
    },
    headerButtons: {
      flexDirection: 'row',
      alignItems: 'center',
    },
    headerButton: {
      marginLeft: 16,
      padding: 8,
    },
    categoriesContainer: {
      paddingVertical: 20,
      paddingHorizontal: 20,
    },
    categoriesScrollView: {
      flexDirection: 'row',
    },
    categoryItem: {
      paddingHorizontal: 20,
      paddingVertical: 10,
      marginRight: 12,
      borderRadius: 20,
      backgroundColor: '#f5f5f5',
    },
    categoryItemActive: {
      backgroundColor: '#000000',
    },
    categoryText: {
      fontSize: 14,
      fontWeight: '500',
      color: '#666666',
    },
    categoryTextActive: {
      color: '#ffffff',
    },
    newsContainer: {
      paddingHorizontal: 20,
    },
    newsCard: {
      backgroundColor: '#ffffff',
      borderRadius: 12,
      marginBottom: 20,
      shadowColor: '#000',
      shadowOffset: { width: 0, height: 2 },
      shadowOpacity: 0.1,
      shadowRadius: 8,
      elevation: 3,
    },
    newsImage: {
      width: '100%',
      height: 200,
      borderTopLeftRadius: 12,
      borderTopRightRadius: 12,
    },
    newsContent: {
      padding: 16,
    },
    newsTitle: {
      fontSize: 16,
      fontWeight: '600',
      color: '#000000',
      lineHeight: 22,
      marginBottom: 8,
    },
    newsDescription: {
      fontSize: 14,
      color: '#666666',
      lineHeight: 20,
      marginBottom: 12,
    },
    newsFooter: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
    },
    newsTime: {
      fontSize: 12,
      color: '#999999',
    },
    newsStats: {
      flexDirection: 'row',
      alignItems: 'center',
    },
    newsStat: {
      flexDirection: 'row',
      alignItems: 'center',
      marginLeft: 16,
    },
    newsStatText: {
      fontSize: 12,
      color: '#999999',
      marginLeft: 4,
    },
    loadingContainer: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      paddingVertical: 50,
    },
    emptyContainer: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      paddingVertical: 50,
      paddingHorizontal: 20,
    },
    emptyText: {
      fontSize: 16,
      color: '#666666',
      textAlign: 'center',
      marginTop: 16,
    },
    searchContainer: {
      backgroundColor: '#ffffff',
      paddingHorizontal: 20,
      paddingVertical: 12,
      borderBottomWidth: 1,
      borderBottomColor: '#e0e0e0',
    },
    searchInputContainer: {
      flexDirection: 'row',
      alignItems: 'center',
      backgroundColor: '#f5f5f5',
      borderRadius: 25,
      paddingHorizontal: 16,
      paddingVertical: 10,
    },
    searchInput: {
      flex: 1,
      fontSize: 16,
      color: '#000000',
      marginLeft: 8,
    },
  });

  const filteredPosts = (() => {
    let filtered = selectedCategory === 'trending' 
      ? posts 
      : posts.filter(post => post.category === selectedCategory);

    if (searchQuery.trim()) {
      filtered = filtered.filter(post => 
        post.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        post.content.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    return filtered;
  })();

  const renderNewsItem = ({ item }) => (
    <TouchableOpacity 
      style={styles.newsCard}
      onPress={() => navigation.navigate('PostDetail', { postId: item.id })}
    >
      <Image 
        source={{ uri: item.image_url }}
        style={styles.newsImage}
        resizeMode="cover"
      />

      <View style={styles.newsContent}>
        <Text style={styles.newsTitle}>{item.title}</Text>
        <Text style={styles.newsDescription} numberOfLines={2}>
          {item.content}
        </Text>

        <View style={styles.newsFooter}>
          <Text style={styles.newsTime}>{formatDate(item.created_at)}</Text>
          <View style={styles.newsStats}>
            <View style={styles.newsStat}>
              <Ionicons name="eye-outline" size={14} color="#999999" />
              <Text style={styles.newsStatText}>{item.views_count || 0}</Text>
            </View>
            <View style={styles.newsStat}>
              <Ionicons name="chatbubble-outline" size={14} color="#999999" />
              <Text style={styles.newsStatText}>{item.comments_count || 0}</Text>
            </View>
          </View>
        </View>
      </View>
    </TouchableOpacity>
  );

  return (
    <View style={[styles.container, { paddingTop: insets.top }]}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}>Afetnet.com</Text>
        <View style={styles.headerButtons}>
          <TouchableOpacity 
            style={styles.headerButton}
            onPress={() => setIsSearchVisible(!isSearchVisible)}
          >
            <Ionicons name="search-outline" size={24} color="#000000" />
          </TouchableOpacity>
          <TouchableOpacity 
            style={styles.headerButton}
            onPress={() => navigation.navigate('Notifications')}
          >
            <Ionicons name="notifications-outline" size={24} color="#000000" />
          </TouchableOpacity>
        </View>
      </View>

      {/* Search Bar */}
      {isSearchVisible && (
        <View style={styles.searchContainer}>
          <View style={styles.searchInputContainer}>
            <Ionicons name="search-outline" size={20} color="#666666" />
            <TextInput
              style={styles.searchInput}
              placeholder="Search news..."
              placeholderTextColor="#999999"
              value={searchQuery}
              onChangeText={setSearchQuery}
              autoFocus={true}
            />
            {searchQuery.length > 0 && (
              <TouchableOpacity onPress={() => setSearchQuery('')}>
                <Ionicons name="close-circle" size={20} color="#666666" />
              </TouchableOpacity>
            )}
          </View>
          <TouchableOpacity 
            style={styles.searchCancelButton}
            onPress={() => {
              setIsSearchVisible(false);
              setSearchQuery('');
            }}
          >
          </TouchableOpacity>
        </View>
      )}

      <View style={styles.categoriesContainer}>
        <ScrollView 
          horizontal 
          showsHorizontalScrollIndicator={false}
          contentContainerStyle={styles.categoriesScrollView}
        >
          {categories.map((item) => {
            const isActive = selectedCategory === item.id;
            return (
              <TouchableOpacity
                key={item.id}
                style={[
                  styles.categoryItem,
                  isActive && styles.categoryItemActive
                ]}
                onPress={() => setSelectedCategory(item.id)}
              >
                <Text style={[
                  styles.categoryText,
                  isActive && styles.categoryTextActive
                ]}>
                  {item.name}
                </Text>
              </TouchableOpacity>
            );
          })}
        </ScrollView>
      </View>

      {isLoading ? (
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#000000" />
        </View>
      ) : filteredPosts.length === 0 ? (
        <View style={styles.emptyContainer}>
          <Ionicons name="document-text-outline" size={48} color="#999999" />
          <Text style={styles.emptyText}>No news available</Text>
        </View>
      ) : (
        <FlatList
          data={filteredPosts}
          renderItem={renderNewsItem}
          keyExtractor={(item) => item.id.toString()}
          contentContainerStyle={styles.newsContainer}
          refreshControl={
            <RefreshControl
              refreshing={isRefreshing}
              onRefresh={handleRefresh}
              colors={['#000000']}
              tintColor={'#000000'}
            />
          }
          showsVerticalScrollIndicator={false}
        />
      )}
    </View>
  );
};

export default HomeScreen;
