import React from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import Header from '../components/Header';

const PrivacyPolicyScreen = ({ navigation }) => {
  const { theme } = useTheme();
  const { t } = useLanguage();

  const lastUpdated = '15 Aralık 2024';

  const sections = [
    {
      title: '1. Giriş',
      content: `Bu Gizlilik Politikası, Afet.net mobil uygulamasını kullanırken kişisel verilerinizin nasıl toplandığını, kullanıldığını, saklandığını ve korunduğunu açıklamaktadır. Uygulamayı kullanarak bu politikayı kabul etmiş sayılırsınız.`
    },
    {
      title: '2. Toplanan Bilgiler',
      content: `Afet.net uygulaması aşağıdaki bilgi türlerini toplayabilir:

• Kişisel Bilgiler: Ad, soyad, telefon numarası, e-posta adresi
• Konum Bilgileri: GPS koordinatları, adres bilgileri
• Cihaz Bilgileri: Cihaz modeli, işletim sistemi, uygulama versiyonu
• Kullanım Verileri: Uygulama içi aktiviteler, özellik kullanımı
• Acil Durum Bilgileri: Acil durum çağrıları, durum raporları`
    },
    {
      title: '3. Bilgilerin Kullanım Amacı',
      content: `Toplanan bilgiler aşağıdaki amaçlarla kullanılır:

• Afet uyarıları ve bildirimler gönderme
• Acil durumlarda konum tespiti ve yardım koordinasyonu
• Aile üyeleri arasında güvenlik durumu paylaşımı
• Uygulama performansını iyileştirme
• Kullanıcı deneyimini kişiselleştirme
• Yasal yükümlülükleri yerine getirme`
    },
    {
      title: '4. Bilgi Paylaşımı',
      content: `Kişisel bilgileriniz aşağıdaki durumlarda paylaşılabilir:

• Acil Durumlarda: AFAD, 112 Acil Çağrı Merkezi ve diğer yetkili mercilerle
• Aile Üyeleri: Belirlediğiniz acil durum kişileriyle
• Yasal Zorunluluk: Mahkeme kararı veya yasal talep durumunda
• Hizmet Sağlayıcılar: Teknik destek ve altyapı hizmetleri için

Bilgileriniz hiçbir zaman ticari amaçlarla üçüncü taraflarla paylaşılmaz.`
    },
    {
      title: '5. Veri Güvenliği',
      content: `Verilerinizin güvenliği için aşağıdaki önlemler alınmıştır:

• SSL/TLS şifreleme ile veri iletimi
• Güvenli sunucularda veri saklama
• Düzenli güvenlik denetimleri
• Erişim kontrolü ve yetkilendirme
• Veri yedekleme ve kurtarma sistemleri
• Çalışan eğitimleri ve gizlilik anlaşmaları`
    },
    {
      title: '6. Konum Bilgileri',
      content: `Konum bilgileriniz özel olarak korunur:

• Sadece acil durumlarda ve izninizle paylaşılır
• Şifrelenmiş olarak saklanır
• Belirli süre sonra otomatik olarak silinir
• İstediğiniz zaman konum paylaşımını kapatabilirsiniz
• Geçmiş konum verileri anonim hale getirilir`
    },
    {
      title: '7. Çerezler ve Takip',
      content: `Uygulama deneyimini iyileştirmek için:

• Teknik çerezler kullanılır
• Anonim kullanım istatistikleri toplanır
• Kişisel reklam takibi yapılmaz
• Üçüncü taraf analitik araçları sınırlı kullanılır
• Çerez tercihlerinizi ayarlayabilirsiniz`
    },
    {
      title: '8. Kullanıcı Hakları',
      content: `KVKK kapsamında sahip olduğunuz haklar:

• Kişisel verilerinizin işlenip işlenmediğini öğrenme
• İşlenen verileriniz hakkında bilgi talep etme
• İşleme amacını ve bunların amacına uygun kullanılıp kullanılmadığını öğrenme
• Yurt içinde veya yurt dışında aktarıldığı üçüncü kişileri bilme
• Eksik veya yanlış işlenmiş verilerin düzeltilmesini isteme
• Verilerin silinmesini veya yok edilmesini isteme`
    },
    {
      title: '9. Veri Saklama Süresi',
      content: `Verileriniz aşağıdaki sürelerle saklanır:

• Hesap Bilgileri: Hesap aktif olduğu sürece
• Konum Verileri: 30 gün (acil durum kayıtları 1 yıl)
• İletişim Kayıtları: 3 yıl
• Teknik Loglar: 6 ay
• Yasal Zorunluluk: İlgili mevzuatta belirtilen süreler

Süresi dolan veriler güvenli bir şekilde silinir.`
    },
    {
      title: '10. Çocukların Gizliliği',
      content: `13 yaş altı çocukların gizliliği özel olarak korunur:

• Ebeveyn izni olmadan veri toplanmaz
• Çocuklara özel güvenlik önlemleri uygulanır
• Ebeveynler çocuklarının verilerini kontrol edebilir
• Çocuk verilerinin silinmesini talep edebilirler`
    },
    {
      title: '11. Politika Değişiklikleri',
      content: `Bu gizlilik politikası gerektiğinde güncellenebilir:

• Önemli değişiklikler bildirim ile duyurulur
• Güncel politika uygulamada yayınlanır
• Eski versiyonlara erişim sağlanır
• Değişiklikleri kabul etmeme durumunda hesap kapatabilirsiniz`
    },
    {
      title: '12. İletişim',
      content: `Gizlilik ile ilgili sorularınız için:

• E-posta: gizlilik@afet.net
• Telefon: +90 (312) 555-0123
• Adres: Afet.net Veri Koruma Sorumlusu
  Çankaya/Ankara
• Web: www.afet.net/gizlilik

Talebiniz 30 gün içinde yanıtlanacaktır.`
    }
  ];

  const renderSection = (section, index) => (
    <View key={index} style={[styles.section, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}>
      <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>{section.title}</Text>
      <Text style={[styles.sectionContent, { color: theme.colors.secondaryText }]}>{section.content}</Text>
    </View>
  );

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <Header title="Gizlilik Politikası" navigation={navigation} />

      <ScrollView style={styles.content} showsVerticalScrollIndicator={false}>
        {/* Başlık ve Güncelleme Tarihi */}
        <View style={[styles.header, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}>
          <View style={styles.headerIcon}>
            <Ionicons name="shield-checkmark" size={32} color={theme.colors.primary} />
          </View>
          <Text style={[styles.headerTitle, { color: theme.colors.text }]}>Gizlilik Politikası</Text>
          <Text style={[styles.lastUpdated, { color: theme.colors.secondaryText }]}>Son güncelleme: {lastUpdated}</Text>
        </View>

        {/* Önemli Bilgi */}
        <View style={[styles.importantNotice, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.primary }]}>
          <Ionicons name="information-circle" size={24} color={theme.colors.primary} />
          <Text style={[styles.noticeText, { color: theme.colors.text }]}>
            Bu politika, kişisel verilerinizin korunması konusundaki taahhütümüzü açıklar. 
            Lütfen dikkatle okuyunuz.
          </Text>
        </View>

        {/* Politika Bölümleri */}
        {sections.map(renderSection)}

        {/* Alt Bilgi */}
        <View style={[styles.footer, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}>
          <Text style={[styles.footerText, { color: theme.colors.secondaryText }]}>
            Bu gizlilik politikası Türkiye Cumhuriyeti Kişisel Verilerin Korunması Kanunu (KVKK) 
            ve ilgili mevzuata uygun olarak hazırlanmıştır.
          </Text>
        </View>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  content: {
    flex: 1,
    padding: 16,
  },
  header: {
    padding: 20,
    borderRadius: 16,
    borderWidth: 1,
    alignItems: 'center',
    marginBottom: 20,
  },
  headerIcon: {
    marginBottom: 12,
  },
  headerTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 8,
  },
  lastUpdated: {
    fontSize: 14,
    textAlign: 'center',
  },
  importantNotice: {
    flexDirection: 'row',
    padding: 16,
    borderRadius: 12,
    borderWidth: 2,
    marginBottom: 20,
    alignItems: 'flex-start',
  },
  noticeText: {
    fontSize: 14,
    lineHeight: 20,
    marginLeft: 12,
    flex: 1,
  },
  section: {
    padding: 16,
    borderRadius: 12,
    borderWidth: 1,
    marginBottom: 16,
  },
  sectionTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 12,
  },
  sectionContent: {
    fontSize: 14,
    lineHeight: 22,
    textAlign: 'justify',
  },
