import React, { useState, useEffect, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  TouchableOpacity,
  ScrollView,
  Platform,
  ActivityIndicator,
  Alert,
  Animated,
  Dimensions,
  Linking,
} from 'react-native';
import { PanGestureHandler, State } from 'react-native-gesture-handler';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useNavigation, useRoute } from '@react-navigation/native';
import { useLanguage } from '../contexts/LanguageContext';
import { useTheme } from '../contexts/ThemeContext';
import earthquakeService from '../services/earthquakeService';

// Web için Leaflet import'ları - sadece web platformunda
let MapContainer, TileLayer, Marker, Popup, L;
if (Platform.OS === 'web') {
  const leaflet = require('react-leaflet');
  MapContainer = leaflet.MapContainer;
  TileLayer = leaflet.TileLayer;
  Marker = leaflet.Marker;
  Popup = leaflet.Popup;
  L = require('leaflet');

  // CSS'i dinamik olarak yükle
  if (typeof document !== 'undefined') {
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = 'https://unpkg.com/leaflet@1.7.1/dist/leaflet.css';
    document.head.appendChild(link);
  }

  // Leaflet marker icon'larını düzelt
  if (L && L.Icon && L.Icon.Default) {
    delete L.Icon.Default.prototype._getIconUrl;
    L.Icon.Default.mergeOptions({
      iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
      iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
      shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
    });
  }
}

const EarthquakeDetailScreen = () => {
  const { t } = useLanguage();
  const { theme } = useTheme();
  const navigation = useNavigation();
  const route = useRoute();
  const insets = useSafeAreaInsets();

  const { earthquake } = route.params;
  const [loading, setLoading] = useState(false);
  const [mapCenter, setMapCenter] = useState([earthquake.latitude || 39.9334, earthquake.longitude || 32.8597]);

  // Çekmece slider için state'ler
  const [isExpanded, setIsExpanded] = useState(false);
  const slideAnim = useRef(new Animated.Value(0)).current;
  const { height: screenHeight } = Dimensions.get('window');
  const panelHeight = screenHeight * 0.7;

  useEffect(() => {
    if (earthquake.latitude && earthquake.longitude) {
      setMapCenter([earthquake.latitude, earthquake.longitude]);
    }
  }, [earthquake]);

  // Çekmece toggle fonksiyonu
  const toggleDrawer = () => {
    const toValue = isExpanded ? 0 : 1;
    setIsExpanded(!isExpanded);

    Animated.spring(slideAnim, {
      toValue,
      useNativeDriver: false,
      tension: 100,
      friction: 8,
    }).start();
  };

  // Pan gesture handler
  const onGestureEvent = Animated.event(
    [{ nativeEvent: { translationY: slideAnim } }],
    { useNativeDriver: false }
  );

  const onHandlerStateChange = (event) => {
    if (event.nativeEvent.oldState === State.ACTIVE) {
      const { translationY, velocityY } = event.nativeEvent;

      let shouldExpand = isExpanded;

      if (Math.abs(velocityY) > 500) {
        shouldExpand = velocityY < 0;
      } else {
        shouldExpand = translationY < -panelHeight / 4;
      }

      setIsExpanded(shouldExpand);

      Animated.spring(slideAnim, {
        toValue: shouldExpanded ? 1 : 0,
        useNativeDriver: false,
        tension: 100,
        friction: 8,
      }).start();
    }
  };

  // Mobil harita açma fonksiyonu
  const openMobileMap = () => {
    const lat = earthquake.latitude || 39.9334;
    const lng = earthquake.longitude || 32.8597;
    const label = encodeURIComponent(earthquake.location || 'Deprem Konumu');

    const url = Platform.select({
      ios: `maps:0,0?q=${lat},${lng}(${label})`,
      android: `geo:0,0?q=${lat},${lng}(${label})`,
      default: `https://www.google.com/maps/search/?api=1&query=${lat},${lng}`
    });

    Linking.openURL(url).catch(() => {
      // Fallback to Google Maps web
      Linking.openURL(`https://www.google.com/maps/search/?api=1&query=${lat},${lng}`);
    });
  };

  const renderMap = () => {
    if (Platform.OS === 'web' && MapContainer) {
      return (
        <MapContainer
          center={mapCenter}
          zoom={10}
          style={{ height: '100%', width: '100%' }}
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          {earthquake.latitude && earthquake.longitude && (
            <Marker position={[earthquake.latitude, earthquake.longitude]}>
              <Popup>
                <div>
                  <strong>{earthquake.magnitude} Büyüklüğünde Deprem</strong><br/>
                  {earthquake.location}<br/>
                  Derinlik: {earthquake.depth} km
                </div>
              </Popup>
            </Marker>
          )}
        </MapContainer>
      );
    } else {
      // Mobile için interaktif harita butonu
      return (
        <TouchableOpacity style={styles.mapPlaceholder} onPress={openMobileMap}>
          <View style={styles.mapIconContainer}>
            <Ionicons name="map" size={64} color={theme.colors.primary} />
            <View style={[styles.magnitudeOverlay, { backgroundColor: getSeverityColor(earthquake.magnitude) }]}>
              <Text style={styles.magnitudeText}>{earthquake.magnitude}</Text>
            </View>
          </View>
          <Text style={[styles.mapPlaceholderText, { color: theme.colors.text }]}>
            Haritada Görüntüle
          </Text>
          <Text style={[styles.mapSubText, { color: theme.colors.secondaryText }]}>
            {earthquake.location}
          </Text>
          <View style={styles.mapButton}>
