import React, { useEffect, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Animated,
  Dimensions,
  ActivityIndicator,
} from 'react-native';
import { useTheme } from '../contexts/ThemeContext';

const { width, height } = Dimensions.get('window');

const SplashScreen = ({ onFinish }) => {
  const { theme } = useTheme();
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const scaleAnim = useRef(new Animated.Value(0.8)).current;
  const slideAnim = useRef(new Animated.Value(50)).current;
  const rotateAnim = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    // Ana animasyon sekansı
    Animated.sequence([
      // Logo ve başlık animasyonu
      Animated.parallel([
        Animated.timing(fadeAnim, {
          toValue: 1,
          duration: 1000,
          useNativeDriver: true,
        }),
        Animated.timing(scaleAnim, {
          toValue: 1,
          duration: 1000,
          useNativeDriver: true,
        }),
        Animated.timing(slideAnim, {
          toValue: 0,
          duration: 1000,
          useNativeDriver: true,
        }),
      ]),
      // Dönen animasyon
      Animated.timing(rotateAnim, {
        toValue: 1,
        duration: 1500,
        useNativeDriver: true,
      }),
    ]).start(() => {
      // Animasyon tamamlandıktan sonra ana ekrana geç
      setTimeout(() => {
        onFinish();
      }, 500);
    });
  }, []);

  const spin = rotateAnim.interpolate({
    inputRange: [0, 1],
    outputRange: ['0deg', '360deg'],
  });

  return (
    <View style={styles.container}>
      {/* Center Section - Afet.net Logo */}
      <Animated.View
        style={[
          styles.centerSection,
          {
            opacity: fadeAnim,
            transform: [{ scale: scaleAnim }],
          },
        ]}
      >
        <View style={styles.logoContainer}>
          {/* Large Orange A */}
          <View style={styles.letterAContainer}>
            <Text style={styles.letterA}>A</Text>
            {/* Small house icon inside A */}
            <View style={styles.houseIcon}>
              <View style={styles.houseSquare} />
            </View>
          </View>

          {/* Afet.net text */}
          <Text style={styles.afetNetText}>AfetNET</Text>
        </View>
      </Animated.View>

      {/* Bottom Loading */}
      <Animated.View
        style={[
          styles.loadingContainer,
          {
            opacity: fadeAnim,
          },
        ]}
      >
        <ActivityIndicator size="large" color="#FF6B35" />
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5F5F5',
    justifyContent: 'center',
    alignItems: 'center',
    paddingVertical: 60,
    paddingHorizontal: 20,
  },
  centerSection: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  logoContainer: {
    alignItems: 'center',
    justifyContent: 'center',
  },
  letterAContainer: {
    position: 'relative',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 20,
  },
  letterA: {
    fontSize: 200,
    fontWeight: '900',
    color: '#FF6B35',
    textAlign: 'center',
    lineHeight: 200,
    fontFamily: 'System',
  },
  houseIcon: {
    position: 'absolute',
    bottom: 45,
    left: '50%',
    marginLeft: -8,
    alignItems: 'center',
    justifyContent: 'center',
  },
  houseSquare: {
    width: 16,
    height: 12,
    backgroundColor: '#FF6B35',
    borderRadius: 2,
  },
  afetNetText: {
    fontSize: 48,
    fontWeight: '400',
    color: '#FF6B35',
    textAlign: 'center',
    marginTop: -10,
    fontFamily: 'System',
  },
  loadingContainer: {
    alignItems: 'center',
    marginBottom: 40,
    position: 'absolute',
    bottom: 60,
  },
});

export default SplashScreen;
