import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  FlatList,
  TouchableOpacity,
  StyleSheet,
  Alert,
  ActivityIndicator,
  Modal,
  TextInput
} from 'react-native';
import { useAuth } from '../../contexts/AuthContext';
import { API_BASE_URL } from '../../contexts/AuthContext';

const UserManagement = () => {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedUser, setSelectedUser] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [actionLoading, setActionLoading] = useState(false);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`${API_BASE_URL}/admin/users`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const data = await response.json();
      if (data.success) {
        setUsers(data.users);
      } else {
        Alert.alert('Hata', data.error || 'Kullanıcılar yüklenirken hata oluştu');
      }
    } catch (error) {
      console.error('Fetch users error:', error);
      Alert.alert('Hata', 'Kullanıcılar yüklenirken hata oluştu');
    } finally {
      setLoading(false);
    }
  };

  const handleUserAction = async (userId, action, value) => {
    setActionLoading(true);
    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`${API_BASE_URL}/admin/users`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          userId,
          action,
          value
        })
      });

      const data = await response.json();
      if (data.success) {
        Alert.alert('Başarılı', data.message);
        fetchUsers(); // Refresh the list
        setModalVisible(false);
      } else {
        Alert.alert('Hata', data.error || 'İşlem gerçekleştirilemedi');
      }
    } catch (error) {
      console.error('User action error:', error);
      Alert.alert('Hata', 'İşlem gerçekleştirilemedi');
    } finally {
      setActionLoading(false);
    }
  };

  const showUserActions = (userItem) => {
    setSelectedUser(userItem);
    setModalVisible(true);
  };

  const getRoleColor = (role) => {
    switch (role) {
      case 'admin': return '#e74c3c';
      case 'author': return '#3498db';
      case 'user': return '#95a5a6';
      default: return '#95a5a6';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'active': return '#27ae60';
      case 'inactive': return '#f39c12';
      case 'banned': return '#e74c3c';
      case 'pending': return '#f39c12';
      default: return '#95a5a6';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('tr-TR', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const renderUserItem = ({ item }) => (
    <TouchableOpacity
      style={styles.userCard}
      onPress={() => showUserActions(item)}
    >
      <View style={styles.userHeader}>
        <Text style={styles.userName}>{item.full_name}</Text>
        <View style={styles.badges}>
          <View style={[styles.badge, { backgroundColor: getRoleColor(item.role) }]}>
            <Text style={styles.badgeText}>{item.role?.toUpperCase()}</Text>
          </View>
          <View style={[styles.badge, { backgroundColor: getStatusColor(item.status) }]}>
            <Text style={styles.badgeText}>{item.status?.toUpperCase()}</Text>
          </View>
        </View>
      </View>

      <Text style={styles.userEmail}>{item.email}</Text>

      {item.organization && (
        <Text style={styles.userOrganization}>📍 {item.organization}</Text>
      )}

      <View style={styles.userFooter}>
        <Text style={styles.userDate}>
          Kayıt: {formatDate(item.created_at)}
        </Text>
        {item.last_login_at && (
          <Text style={styles.userDate}>
            Son Giriş: {formatDate(item.last_login_at)}
          </Text>
        )}
      </View>
    </TouchableOpacity>
  );

  if (loading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color="#3498db" />
        <Text style={styles.loadingText}>Kullanıcılar yükleniyor...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Kullanıcı Yönetimi</Text>
        <Text style={styles.subtitle}>{users.length} kullanıcı</Text>
      </View>

      <FlatList
        data={users}
        renderItem={renderUserItem}
        keyExtractor={(item) => item.id}
        contentContainerStyle={styles.listContainer}
        showsVerticalScrollIndicator={false}
      />

      {/* User Actions Modal */}
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            {selectedUser && (
              <>
                <Text style={styles.modalTitle}>{selectedUser.full_name}</Text>
                <Text style={styles.modalSubtitle}>{selectedUser.email}</Text>

                <View style={styles.actionSection}>
                  <Text style={styles.sectionTitle}>Rol Değiştir</Text>
                  <View style={styles.actionButtons}>
                    {selectedUser.role !== 'admin' && (
                      <TouchableOpacity
                        style={[styles.actionButton, { backgroundColor: '#e74c3c' }]}
                        onPress={() => handleUserAction(selectedUser.id, 'changeRole', 'admin')}
                        disabled={actionLoading}
                      >
                        <Text style={styles.actionButtonText}>Admin Yap</Text>
                      </TouchableOpacity>
                    )}
                    {selectedUser.role !== 'author' && (
                      <TouchableOpacity
                        style={[styles.actionButton, { backgroundColor: '#3498db' }]}
                        onPress={() => handleUserAction(selectedUser.id, 'changeRole', 'author')}
                        disabled={actionLoading}
                      >
                        <Text style={styles.actionButtonText}>Yazar Yap</Text>
                      </TouchableOpacity>
                    )}
                    {selectedUser.role !== 'user' && (
                      <TouchableOpacity
                        style={[styles.actionButton, { backgroundColor: '#95a5a6' }]}
                        onPress={() => handleUserAction(selectedUser.id, 'changeRole', 'user')}
                        disabled={actionLoading}
                      >
                        <Text style={styles.actionButtonText}>Normal Kullanıcı</Text>
                      </TouchableOpacity>
                    )}
                  </View>
                </View>

                <View style={styles.actionSection}>
                  <Text style={styles.sectionTitle}>Durum Değiştir</Text>
                  <View style={styles.actionButtons}>
                    {selectedUser.status !== 'active' && (
                      <TouchableOpacity
                        style={[styles.actionButton, { backgroundColor: '#27ae60' }]}
                        onPress={() => handleUserAction(selectedUser.id, 'changeStatus', 'active')}
                        disabled={actionLoading}
                      >
                        <Text style={styles.actionButtonText}>Aktif</Text>
                      </TouchableOpacity>
                    )}
                    {selectedUser.status !== 'inactive' && (
                      <TouchableOpacity
                        style={[styles.actionButton, { backgroundColor: '#f39c12' }]}
                        onPress={() => handleUserAction(selectedUser.id, 'changeStatus', 'inactive')}
                        disabled={actionLoading}
                      >
                        <Text style={styles.actionButtonText}>Pasif</Text>
                      </TouchableOpacity>
                    )}
                    {selectedUser.status !== 'banned' && (
                      <TouchableOpacity
                        style={[styles.actionButton, { backgroundColor: '#e74c3c' }]}
                        onPress={() => handleUserAction(selectedUser.id, 'changeStatus', 'banned')}
                        disabled={actionLoading}
                      >
                        <Text style={styles.actionButtonText}>Yasakla</Text>
                      </TouchableOpacity>
                    )}
                  </View>
                </View>

                <View style={styles.modalButtons}>
                  <TouchableOpacity
                    style={styles.cancelButton}
                    onPress={() => setModalVisible(false)}
                    disabled={actionLoading}
                  >
                    <Text style={styles.cancelButtonText}>İptal</Text>
                  </TouchableOpacity>
                </View>

                {actionLoading && (
                  <View style={styles.actionLoading}>
                    <ActivityIndicator color="#3498db" />
                    <Text style={styles.actionLoadingText}>İşlem yapılıyor...</Text>
                  </View>
                )}
              </>
            )}
