import React from 'react';
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  Platform,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { SvgXml } from 'react-native-svg';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

const Header = ({ 
  title, 
  showNotification = true, 
  showBackButton = false, 
  onBackPress,
  onNotificationPress,
  customLeftComponent,
  customRightComponent 
}) => {
  const { theme } = useTheme();
  const { t } = useLanguage();

  const handleNotificationPress = () => {
    if (onNotificationPress) {
      onNotificationPress();
    } else {
      // Varsayılan bildirim davranışı
      console.log('Bildirimler açıldı');
    }
  };

  const renderLeftComponent = () => {
    if (customLeftComponent) {
      return customLeftComponent;
    }

    if (showBackButton) {
      return (
        <TouchableOpacity 
          style={styles.backButton}
          onPress={onBackPress}
        >
          <Ionicons name="arrow-back" size={24} color={theme.colors.icon} />
        </TouchableOpacity>
      );
    }

    const logoSvg = `
      <svg width="32" height="32" viewBox="0 0 40 40" fill="none" xmlns="http://www.w3.org/2000/svg">
        <circle cx="20" cy="20" r="20" fill="#E53E3E" stroke="#C53030" stroke-width="1"/>
        <path d="M12 30 L16 18 L20 30 M14.5 25 L17.5 25" stroke="white" stroke-width="2.5" stroke-linecap="round" stroke-linejoin="round" fill="none"/>
        <circle cx="22" cy="29" r="1.5" fill="white"/>
        <path d="M24 30 L24 18 M24 18 L28 30 M28 30 L28 18" stroke="white" stroke-width="2.5" stroke-linecap="round" stroke-linejoin="round" fill="none"/>
      </svg>
    `;

    return (
      <View style={styles.headerLeft}>
        <View style={styles.logoContainer}>
          {Platform.OS === 'web' ? (
            <div dangerouslySetInnerHTML={{ __html: logoSvg }} />
          ) : (
            <SvgXml xml={logoSvg} width="32" height="32" />
          )}
        </View>
        <Text style={[styles.appTitle, { color: theme.colors.text }]}>
          Afet.net
        </Text>
      </View>
    );
  };

  const renderRightComponent = () => {
    if (customRightComponent) {
      return customRightComponent;
    }

    if (showNotification) {
      return (
        <TouchableOpacity 
          style={styles.notificationButton}
          onPress={handleNotificationPress}
        >
          <Ionicons name="notifications" size={28} color="#FF6B35" />
        </TouchableOpacity>
      );
    }

    return null;
  };

  return (
    <View style={[
      styles.header, 
      { 
        backgroundColor: theme.colors.cardBackground, 
        borderBottomColor: theme.colors.border 
      }
    ]}>
      {renderLeftComponent()}
      {renderRightComponent()}
    </View>
  );
};

const styles = StyleSheet.create({
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingHorizontal: 16,
    paddingVertical: 12,
    borderBottomWidth: 1,
  },
  headerLeft: {
    flexDirection: 'row',
    alignItems: 'center',
    flex: 1,
  },
  logoContainer: {
    width: 32,
    height: 32,
    borderRadius: 16,
    backgroundColor: '#FFF0ED',
    justifyContent: 'center',
    alignItems: 'center',
    marginRight: 12,
  },
  appTitle: {
    fontSize: 20,
    fontWeight: '700',
  },
  backButton: {
    padding: 8,
    marginRight: 8,
  },
  notificationButton: {
    padding: 4,
  },
});

export default Header;
