      baseURL: AFAD_API_BASE_URL,
      timeout: 15000,
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
      },
    });
  }

  /**
   * AFAD API iÃ§in tarih formatÄ±: YYYY-MM-DD HH:mm:ss
   */
  formatDate(date) {
    return date.getFullYear() + '-' + 
           String(date.getMonth() + 1).padStart(2, '0') + '-' + 
           String(date.getDate()).padStart(2, '0') + ' ' + 
           String(date.getHours()).padStart(2, '0') + ':' + 
           String(date.getMinutes()).padStart(2, '0') + ':' + 
           String(date.getSeconds()).padStart(2, '0');
  }

  /**
   * Belirli tarih aralÄ±ÄŸÄ±ndaki deprem verilerini getirir
   * @param {Date} startDate - BaÅŸlangÄ±Ã§ tarihi
   * @param {Date} endDate - BitiÅŸ tarihi
   * @param {number} skip - Atlanacak kayÄ±t sayÄ±sÄ± (pagination iÃ§in)
   * @param {number} take - AlÄ±nacak kayÄ±t sayÄ±sÄ±
   * @param {number} minMagnitude - Minimum bÃ¼yÃ¼klÃ¼k
   * @returns {Promise<Array>} Deprem verileri dizisi
   */
  async getEarthquakesByDateRange(startDate, endDate, skip = 0, take = 100, minMagnitude = 0) {
    try {
      const eventFilter = {
        EventSearchFilterList: [
          { FilterType: 8, Value: startDate.toISOString() },
          { FilterType: 9, Value: endDate.toISOString() }
        ],
        Skip: skip,
        Take: take,
        SortDescriptor: { field: 'eventDate', dir: 'desc' }
      };

      const response = await axios.post(AFAD_EVENT_FILTER_URL, eventFilter, {
        timeout: 15000,
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
      });

      if (response.data && response.data.Data && Array.isArray(response.data.Data)) {
        console.log('AFAD API\'sinden baÅŸarÄ±yla veri alÄ±ndÄ±:', response.data.Data.length, 'deprem');

        // Minimum bÃ¼yÃ¼klÃ¼k filtresi uygula
        const filteredData = response.data.Data.filter(event => 
          parseFloat(event.Magnitude) >= minMagnitude
        );

        return {
          data: this.formatNewEarthquakeData(filteredData),
          totalCount: response.data.TotalCount || filteredData.length,
          hasMore: (skip + take) < (response.data.TotalCount || 0)
        };
      }

      throw new Error('API\'den veri alÄ±namadÄ±');
    } catch (error) {
      console.error('AFAD API baÄŸlantÄ± hatasÄ±:', error.message);
      throw new Error('Veriler Ã§ekilemiyor. LÃ¼tfen internet baÄŸlantÄ±nÄ±zÄ± kontrol edin ve tekrar deneyin.');
    }
  }

  /**
   * Kandilli Rasathanesi Live API'sinden canlÄ± deprem verilerini Ã§eker
   * @param {number} limit - Getirilecek deprem sayÄ±sÄ±
   * @returns {Promise<Array>} Deprem verileri dizisi
   */
  async getKandilliLiveEarthquakes(limit = 50) {
    try {
      console.log('Kandilli Rasathanesi Live API\'sinden veri Ã§ekiliyor...');

      const response = await axios.get(KANDILLI_LIVE_API, {
        timeout: 10000,
        headers: {
          'Accept': 'application/json',
          'User-Agent': 'AfetNet Mobile App'
        }
      });

      if (response.data && response.data.result && Array.isArray(response.data.result)) {
        console.log('Kandilli Live API\'sinden baÅŸarÄ±yla veri alÄ±ndÄ±:', response.data.result.length, 'deprem');

        const limitedData = response.data.result.slice(0, limit);
        return this.formatKandilliLiveData(limitedData);
      }

      throw new Error('Kandilli Live API\'den veri alÄ±namadÄ±');
    } catch (error) {
      console.error('Kandilli Live API baÄŸlantÄ± hatasÄ±:', error.message);
      // Fallback olarak eski Kandilli API'sini dene
      return this.getKandilliEarthquakesFallback(limit);
