      });

      if (response.data) {
        const earthquakes = this.parseKandilliData(response.data, limit);
        console.log('Kandilli API\'sinden baÅŸarÄ±yla veri alÄ±ndÄ±:', earthquakes.length, 'deprem');
        return earthquakes;
      }

      throw new Error('Kandilli API\'den veri alÄ±namadÄ±');
