    if (magnitude >= 6.0) return '#FF9500'; // Turuncu - YÃ¼ksek
    if (magnitude >= 5.0) return '#FFCC00'; // SarÄ± - Orta
    if (magnitude >= 4.0) return '#34C759'; // YeÅŸil - DÃ¼ÅŸÃ¼k
    return '#007AFF'; // Mavi - Ã‡ok DÃ¼ÅŸÃ¼k
  }
