'use strict';

import type {BlobData, BlobOptions} from './BlobTypes';

/**
 * Opaque JS representation of some binary data in native.
 *
 * The API is modeled after the W3C Blob API, with one caveat
 * regarding explicit deallocation. Refer to the `close()`
 * method for further details.
 *
 * Example usage in a React component:
 *
 *   class WebSocketImage extends React.Component {
 *      state = {blob: null};
 *      componentDidMount() {
 *        let ws = this.ws = new WebSocket(...);
 *        ws.binaryType = 'blob';
 *        ws.onmessage = (event) => {
 *          if (this.state.blob) {
 *            this.state.blob.close();
 *          }
 *          this.setState({blob: event.data});
 *        };
 *      }
 *      componentUnmount() {
 *        if (this.state.blob) {
 *          this.state.blob.close();
 *        }
 *        if (this.ws) {
 *          this.ws.close();
 *        }
 *      }
 *      render() {
 *        if (!this.state.blob) {
 *          return <Text>Loading...</Text>;
 *        }
 *        return <Image source={{uri: URL.createObjectURL(this.state.blob)}} />;
 *      }
 *   }
 *
 * Reference: https://developer.mozilla.org/en-US/docs/Web/API/Blob
 */
class Blob {
  data: BlobData;
  size: number;
  type: string;

  constructor(parts: Array<Blob | string> = [], options?: BlobOptions) {
    const blobId = require('./BlobManager').createFromParts(parts, options);
    this.data = {
      blobId,
      offset: 0,
      size: require('./BlobManager').getSize(blobId),
      name: options ? options.name : undefined,
      type: options ? options.type || '' : '',
      lastModified: options ? options.lastModified : Date.now(),
    };
  }

  /*
   * This method is used to create a new Blob object containing
   * the data in the specified range of bytes of the source Blob.
   */
  slice(start?: number, end?: number, contentType?: string): Blob {
    let {offset, size} = this.data;
    if (typeof start === 'number') {
      if (start > size) {
        start = size;
      }
      offset += start;
      size -= start;

      if (typeof end === 'number') {
        if (end < 0) {
          end = this.data.size + end;
        }
        size = end - start;
      }
    }
    return require('./BlobManager').createFromOptions({
      blobId: this.data.blobId,
      offset,
      size,
      name: this.data.name,
      type: contentType || this.data.type || '',
      lastModified: this.data.lastModified,
    });
  }

  /**
   * This method is used to release the resources held by the Blob object.
   */
  close(): void {
    require('./BlobManager').release(this.data.blobId);
  }

  /**
   * Size of the data contained in the Blob object, in bytes.
   */
  // $FlowFixMe[unsafe-getters-setters]
  get size(): number {
    return this.data.size;
  }

  /*
   * String indicating the MIME type of the data contained in the Blob.
   * If the type is unknown, this string is empty.
   */
  // $FlowFixMe[unsafe-getters-setters]
  get type(): string {
    return this.data.type || '';
  }
}

module.exports = Blob;
module.exports.default = Blob;

// Add ES6 export for compatibility
if (typeof exports === 'object' && typeof module !== 'undefined') {
  exports.default = Blob;
}

// Ensure default export is available
