import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Dimensions,
  ScrollView,
  TouchableOpacity,
  Alert,
  Modal,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

const ProfileScreen = () => {
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');
  const { theme } = useTheme();
  const { t } = useLanguage();

  const [userStats] = useState({
    totalShares: 12,
    familyMembers: 3
  });

  const [familyMembers, setFamilyMembers] = useState([
    {
      id: 1,
      name: 'Emine K.',
      relation: 'Eş',
      phone: '+90 532 XXX XX XX',
      status: 'Güvenli',
      lastSeen: '2 dakika önce',
      initial: 'E',
      statusColor: '#34C759'
    },
    {
      id: 2,
      name: 'Ali K.',
      relation: 'Oğul',
      phone: '+90 535 XXX XX XX',
      status: 'Güvenli',
      lastSeen: '1 saat önce',
      initial: 'A',
      statusColor: '#34C759'
    },
    {
      id: 3,
      name: 'Fatma A.',
      relation: 'Anne',
      phone: '+90 542 XXX XX XX',
      status: 'Bilinmiyor',
      lastSeen: '3 saat önce',
      initial: 'F',
      statusColor: '#FF9500'
    }
  ]);

  const [showAllMembers, setShowAllMembers] = useState(false);

  const handleEmergencyReport = () => {
    Alert.alert(
      t('profile.emergencyReportTitle'),
      t('profile.emergencyReportMessage'),
      [
        { text: t('profile.cancel'), style: 'cancel' },
        { text: t('profile.report'), style: 'destructive', onPress: () => {
          Alert.alert(t('profile.success'), t('profile.emergencyReportSuccess'));
        }}
      ]
    );
  };

  const handleFamilyMemberPress = (member) => {
    setShowAllMembers(true);
  };

  const handleViewAllMembers = () => {
    setShowAllMembers(true);
  };

  const handleCallMember = (phone) => {
    Alert.alert(t('profile.call'), `${phone} ${t('profile.calling')}`);
  };

  const handleLocationRequest = (memberName) => {
    Alert.alert(t('profile.locationRequest'), `${memberName} ${t('profile.locationRequestSent')}`);
  };

  const handleEditMember = (member) => {
    Alert.alert(t('profile.edit'), `${member.name} ${t('profile.editingInfo')}`);
  };

  const handleDeleteMember = (memberId) => {
    Alert.alert(
      t('profile.deleteMember'),
      t('profile.deleteMemberConfirm'),
      [
        { text: t('profile.cancel'), style: 'cancel' },
        { 
          text: t('profile.delete'), 
          style: 'destructive', 
          onPress: () => {
            setFamilyMembers(prev => prev.filter(member => member.id !== memberId));
            Alert.alert(t('profile.success'), t('profile.memberDeleted'));
          }
        }
      ]
    );
  };

  return (
    <View style={[styles.container, { paddingTop: insets.top, backgroundColor: theme.colors.background }]}>
      {/* Header */}
      <View style={[styles.header, { backgroundColor: theme.colors.cardBackground, borderBottomColor: theme.colors.border }]}>
        <Text style={[styles.headerTitle, { color: theme.colors.text }]}>{t('profile.title')}</Text>
        <TouchableOpacity style={styles.profileIcon}>
          <Ionicons name="person-circle-outline" size={24} color={theme.colors.primary} />
        </TouchableOpacity>
      </View>

      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* User Profile Card */}
        <View style={[styles.profileCard, { backgroundColor: theme.colors.cardBackground }]}>
          <View style={styles.centeredProfileContent}>
            <View style={styles.avatarContainer}>
              <View style={styles.avatar}>
                <Ionicons name="person" size={32} color={theme.colors.primary} />
              </View>
              <View style={styles.statusBadge}>
                <Text style={styles.statusText}>{t('profile.verified')}</Text>
              </View>
            </View>

            <Text style={[styles.userName, { color: theme.colors.text }]}>{t('profile.user')}</Text>
            <Text style={[styles.userLocation, { color: theme.colors.secondaryText }]}>📍 {t('profile.location')}</Text>

            {/* Stats */}
            <View style={[styles.statsContainer, { borderTopColor: theme.colors.border }]}>
              <View style={styles.statItem}>
                <Text style={[styles.statNumber, { color: theme.colors.text }]}>{userStats.totalShares}</Text>
                <Text style={[styles.statLabel, { color: theme.colors.secondaryText }]}>{t('profile.totalShares')}</Text>
              </View>
              <View style={styles.statItem}>
                <Text style={[styles.statNumber, { color: theme.colors.text }]}>{userStats.familyMembers}</Text>
                <Text style={[styles.statLabel, { color: theme.colors.secondaryText }]}>{t('profile.familyMembers')}</Text>
              </View>
            </View>
          </View>

          <TouchableOpacity style={[styles.editButton, { backgroundColor: theme.colors.background }]}>
            <Ionicons name="create-outline" size={20} color={theme.colors.secondaryText} />
          </TouchableOpacity>
        </View>

        {/* Family Members Section */}
        <View style={[styles.sectionContainer, { backgroundColor: theme.colors.cardBackground }]}>
          <View style={styles.sectionHeader}>
            <View style={styles.sectionTitleContainer}>
              <Ionicons name="people" size={20} color={theme.colors.text} />
              <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>{t('profile.familyMembersTitle')}</Text>
            </View>
            <TouchableOpacity onPress={handleViewAllMembers} style={[styles.addButton, { backgroundColor: theme.colors.primary }]}>
              <Text style={styles.addButtonText}>{t('profile.add')}</Text>
            </TouchableOpacity>
          </View>

          {familyMembers.map((member) => (
            <TouchableOpacity 
              key={member.id} 
              style={[styles.familyMemberItem, { borderBottomColor: theme.colors.border }]}
              onPress={() => handleFamilyMemberPress(member)}
            >
              <View style={styles.memberInfo}>
                <View style={[styles.memberAvatar, { backgroundColor: theme.colors.primary + '20' }]}>
                  <Text style={[styles.memberInitial, { color: theme.colors.primary }]}>{member.initial}</Text>
                </View>
                <View style={styles.memberDetails}>
                  <Text style={[styles.memberName, { color: theme.colors.text }]}>{member.name}</Text>
                  <Text style={[styles.memberLastSeen, { color: theme.colors.secondaryText }]}>⏰ {member.lastSeen}</Text>
                </View>
              </View>
              <View style={styles.memberStatus}>
                <View style={[styles.statusIndicator, { backgroundColor: member.statusColor }]} />
                <Text style={[styles.memberStatusText, { color: member.statusColor }]}>
                  {member.status}
                </Text>
              </View>
              <TouchableOpacity style={styles.memberOptions}>
                <Ionicons name="ellipsis-horizontal" size={16} color={theme.colors.secondaryText} />
              </TouchableOpacity>
            </TouchableOpacity>
          ))}

          <TouchableOpacity onPress={handleViewAllMembers} style={styles.viewAllButton}>
            <Text style={[styles.viewAllText, { color: theme.colors.primary }]}>{t('profile.viewAllMembers')}</Text>
          </TouchableOpacity>
        </View>

        {/* Safety Status */}
        <View style={[styles.safetyContainer, { backgroundColor: theme.colors.cardBackground }]}>
          <View style={styles.safetyHeader}>
            <View style={styles.safetyIcon}>
              <Ionicons name="shield-checkmark" size={24} color="#34C759" />
            </View>
            <View style={styles.safetyInfo}>
              <Text style={styles.safetyTitle}>{t('profile.safetyStatus')}</Text>
              <Text style={[styles.safetySubtitle, { color: theme.colors.secondaryText }]}>{t('profile.lastUpdate')}</Text>
            </View>
          </View>
        </View>

        {/* Emergency Report Button */}
        <TouchableOpacity style={styles.emergencyButton} onPress={handleEmergencyReport}>
          <Ionicons name="warning" size={20} color="#FFFFFF" />
          <Text style={styles.emergencyButtonText}>{t('profile.emergencyReport')}</Text>
        </TouchableOpacity>

        <View style={styles.bottomSpacing} />
      </ScrollView>

      {/* Family Members Detail Modal */}
      <Modal
        visible={showAllMembers}
        animationType="slide"
        presentationStyle="pageSheet"
      >
        <View style={[styles.modalContainer, { paddingTop: insets.top, backgroundColor: theme.colors.background }]}>
          {/* Modal Header */}
          <View style={[styles.modalHeader, { backgroundColor: theme.colors.cardBackground, borderBottomColor: theme.colors.border }]}>
            <Text style={[styles.modalTitle, { color: theme.colors.text }]}>{t('profile.familyMembersTitle')}</Text>
            <TouchableOpacity 
              style={styles.modalCloseButton}
              onPress={() => setShowAllMembers(false)}
            >
              <Ionicons name="close" size={24} color={theme.colors.secondaryText} />
            </TouchableOpacity>
          </View>

          <ScrollView style={styles.modalScrollView} showsVerticalScrollIndicator={false}>
            {familyMembers.map((member) => (
              <View key={member.id} style={[styles.detailedMemberCard, { backgroundColor: theme.colors.cardBackground }]}>
                <View style={styles.detailedMemberHeader}>
                  <View style={styles.detailedMemberInfo}>
                    <View style={[styles.detailedMemberAvatar, { backgroundColor: theme.colors.primary }]}>
                      <Text style={styles.detailedMemberInitial}>{member.initial}</Text>
                    </View>
                    <View style={styles.detailedMemberDetails}>
                      <Text style={[styles.detailedMemberName, { color: theme.colors.text }]}>{member.name}</Text>
                      <Text style={[styles.detailedMemberRelation, { color: theme.colors.secondaryText }]}>{member.relation}</Text>
                      <Text style={[styles.detailedMemberPhone, { color: theme.colors.primary }]}>{member.phone}</Text>
                      <Text style={[styles.detailedMemberLastSeen, { color: theme.colors.secondaryText }]}>{t('profile.lastSeen')}: {member.lastSeen}</Text>
                    </View>
                  </View>
                  <View style={styles.detailedMemberStatus}>
                    <View style={[styles.detailedStatusIndicator, { backgroundColor: member.statusColor }]} />
                    <Text style={[styles.detailedMemberStatusText, { color: member.statusColor }]}>
                      {member.status}
                    </Text>
                  </View>
                  <TouchableOpacity 
                    style={styles.detailedMemberOptions}
                    onPress={() => handleEditMember(member)}
                  >
                    <Ionicons name="create-outline" size={20} color={theme.colors.secondaryText} />
                  </TouchableOpacity>
                  <TouchableOpacity 
                    style={styles.detailedMemberDelete}
                    onPress={() => handleDeleteMember(member.id)}
                  >
                    <Ionicons name="trash-outline" size={20} color="#FF3B30" />
                  </TouchableOpacity>
                </View>

                {/* Action Buttons */}
                <View style={[styles.memberActionButtons, { borderTopColor: theme.colors.border }]}>
                  <TouchableOpacity 
                    style={[styles.actionButton, { backgroundColor: theme.colors.background }]}
                    onPress={() => handleCallMember(member.phone)}
                  >
                    <Ionicons name="call" size={16} color={theme.colors.primary} />
                    <Text style={[styles.actionButtonText, { color: theme.colors.primary }]}>{t('profile.call')}</Text>
                  </TouchableOpacity>
                  <TouchableOpacity 
                    style={[styles.actionButton, { backgroundColor: theme.colors.background }]}
                    onPress={() => handleLocationRequest(member.name)}
                  >
                    <Ionicons name="location" size={16} color={theme.colors.primary} />
                    <Text style={[styles.actionButtonText, { color: theme.colors.primary }]}>{t('profile.requestLocation')}</Text>
                  </TouchableOpacity>
                </View>
              </View>
            ))}

            <TouchableOpacity style={[styles.addMemberButton, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.primary }]}>
              <Ionicons name="add" size={20} color={theme.colors.primary} />
              <Text style={[styles.addMemberText, { color: theme.colors.primary }]}>{t('profile.addNewMember')}</Text>
            </TouchableOpacity>

            <View style={styles.modalBottomSpacing} />
          </ScrollView>
        </View>
      </Modal>
    </View>
  );
};

