import React, { useState, useRef, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  Animated,
  FlatList
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import { useNotification } from '../contexts/NotificationContext';
import Header from '../components/Header';
import DisasterVisualization from '../components/DisasterVisualization';
import NotificationBox from '../components/NotificationBox';

const { width: screenWidth } = Dimensions.get('window');

const NotificationsScreen = ({ navigation }) => {
  const insets = useSafeAreaInsets();
  const { theme } = useTheme();
  const { t } = useLanguage();
  const { 
    notifications, 
    unreadCount, 
    markAsRead, 
    markAllAsRead, 
    clearAllNotifications,
    removeNotification 
  } = useNotification();

  const [activeTab, setActiveTab] = useState('disasters'); // 'disasters' veya 'notifications'
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(50)).current;
  const tabIndicatorAnim = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 600,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 600,
        useNativeDriver: true,
      })
    ]).start();
  }, []);

  useEffect(() => {
    Animated.spring(tabIndicatorAnim, {
      toValue: activeTab === 'disasters' ? 0 : 1,
      useNativeDriver: true,
      tension: 100,
      friction: 8,
    }).start();
  }, [activeTab]);

  const handleNotificationPress = (notification) => {
    markAsRead(notification.id);

    // Bildirim türüne göre özel işlemler
    if (notification.onPress) {
      notification.onPress(notification);
    }
  };

  const handleNotificationClose = (notificationId) => {
    removeNotification(notificationId);
  };

  const renderTabButton = (tabKey, title, icon) => {
    const isActive = activeTab === tabKey;
    return (
      <TouchableOpacity
        style={[
          styles.tabButton,
          isActive && styles.activeTabButton
        ]}
        onPress={() => setActiveTab(tabKey)}
        activeOpacity={0.7}
      >
        <Animated.View style={[
          styles.tabContent,
          isActive && {
            transform: [{
              scale: fadeAnim.interpolate({
                inputRange: [0, 1],
                outputRange: [0.95, 1]
              })
            }]
          }
        ]}>
          <Ionicons
            name={icon}
            size={22}
            color={isActive ? '#FFFFFF' : theme.colors.textSecondary}
            style={styles.tabIcon}
          />
          <Text style={[
            styles.tabText,
            { color: isActive ? '#FFFFFF' : theme.colors.textSecondary }
          ]}>
            {title}
          </Text>
          {tabKey === 'notifications' && unreadCount > 0 && (
            <Animated.View style={[
              styles.badge,
              {
                transform: [{
                  scale: fadeAnim.interpolate({
                    inputRange: [0, 1],
                    outputRange: [0, 1]
                  })
                }]
              }
            ]}>
              <Text style={styles.badgeText}>
                {unreadCount > 99 ? '99+' : unreadCount}
              </Text>
            </Animated.View>
          )}
        </Animated.View>
      </TouchableOpacity>
    );
  };

  const renderNotificationsList = () => {
    if (notifications.length === 0) {
      return (
        <View style={styles.emptyContainer}>
          <Ionicons
            name="notifications-off"
            size={64}
            color={theme.colors.textSecondary}
            style={styles.emptyIcon}
          />
          <Text style={[styles.emptyText, { color: theme.colors.text }]}>
            Henüz bildirim yok
          </Text>
          <Text style={[styles.emptySubtext, { color: theme.colors.textSecondary }]}>
            Yeni bildirimler burada görünecek
          </Text>
        </View>
      );
    }

    const renderNotificationItem = ({ item: notification, index }) => {
      const itemAnim = useRef(new Animated.Value(0)).current;

      useEffect(() => {
        Animated.timing(itemAnim, {
          toValue: 1,
          duration: 400,
          delay: index * 100,
          useNativeDriver: true,
        }).start();
      }, []);

      return (
        <Animated.View
          style={[
            styles.notificationItem,
            {
              opacity: itemAnim,
              transform: [{
                translateY: itemAnim.interpolate({
                  inputRange: [0, 1],
                  outputRange: [30, 0]
                })
              }]
            }
          ]}
        >
          <TouchableOpacity
            style={[
              styles.notificationCard,
              { backgroundColor: theme.colors.cardBackground },
              !notification.isRead && styles.unreadNotification
            ]}
            onPress={() => handleNotificationPress(notification)}
            activeOpacity={0.8}
          >
            <View style={styles.notificationHeader}>
              <Animated.View style={[
                styles.notificationIconContainer,
                { backgroundColor: getNotificationColor(notification.type) + '15' }
              ]}>
                <Ionicons
                  name={getNotificationIcon(notification.type)}
                  size={22}
                  color={getNotificationColor(notification.type)}
                />
              </Animated.View>
              <View style={styles.notificationContent}>
                <Text style={[styles.notificationTitle, { color: theme.colors.text }]}>
                  {notification.title}
                </Text>
                <Text style={[styles.notificationMessage, { color: theme.colors.textSecondary }]}>
                  {notification.message}
                </Text>
                <Text style={[styles.notificationTime, { color: theme.colors.textSecondary }]}>
                  {formatTime(notification.timestamp)}
                </Text>
              </View>
              <TouchableOpacity
                style={styles.closeButton}
                onPress={() => handleNotificationClose(notification.id)}
                hitSlop={{ top: 10, bottom: 10, left: 10, right: 10 }}
              >
                <Ionicons
                  name="close-circle"
                  size={20}
                  color={theme.colors.textSecondary}
                />
              </TouchableOpacity>
            </View>
          </TouchableOpacity>
        </Animated.View>
      );
    };

    return (
      <View style={styles.notificationsContainer}>
        <Animated.View style={[
          styles.notificationsHeader,
          {
            opacity: fadeAnim,
            transform: [{ translateY: slideAnim }]
          }
        ]}>
          <Text style={[styles.notificationsTitle, { color: theme.colors.text }]}>
            Bildirimler
          </Text>
          <Text style={[styles.notificationsCount, { color: theme.colors.textSecondary }]}>
            {notifications.length} bildirim
          </Text>
          {(unreadCount > 0 || notifications.length > 0) && (
            <View style={styles.notificationsActions}>
              {unreadCount > 0 && (
                <TouchableOpacity
                  style={[styles.actionButton, styles.primaryActionButton]}
                  onPress={markAllAsRead}
                  activeOpacity={0.8}
                >
                  <Ionicons name="checkmark-done" size={16} color="#FFFFFF" style={styles.actionIcon} />
                  <Text style={styles.actionButtonText}>Okundu İşaretle</Text>
                </TouchableOpacity>
              )}
