import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  Image,
  StatusBar,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useTheme } from '../contexts/ThemeContext';

const HomeScreen = ({ navigation }) => {
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');
  const { theme } = useTheme();

  const [selectedCategory, setSelectedCategory] = useState('Tech');

  const categories = ['Tech', 'Science', 'Education', 'Business'];

  const featuredArticle = {
    id: 1,
    title: 'A month with DJI Mini 3 Pro',
    date: 'Jul 10, 2023',
    image: 'https://images.unsplash.com/photo-1473968512647-3e447244af8f?w=400&h=250&fit=crop',
    category: 'Tech'
  };

  const articles = [
    {
      id: 2,
      title: 'Now Google\'s Bard AI can talk & respond to visual prompts',
      source: 'The Verge • Google AI',
      date: 'Jul 13, 2023',
      readTime: '2 min read',
      image: 'https://images.unsplash.com/photo-1677442136019-21780ecad995?w=80&h=80&fit=crop'
    },
    {
      id: 3,
      title: 'WatchOS 10 preview: widgets all the way down',
      source: 'Gizmodo • Watch',
      date: 'Jul 10, 2023',
      readTime: '4 min read',
      image: 'https://images.unsplash.com/photo-1434494878577-86c23bcb06b9?w=80&h=80&fit=crop'
    },
    {
      id: 4,
      title: 'How Gen Z are disrupting the definition of \'prestigious\' jobs',
      source: 'Work Life • Jobs',
      date: 'Jul 8, 2023',
      readTime: '3 min read',
      image: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=80&h=80&fit=crop'
    }
  ];

  return (
    <View style={styles.container}>
      <StatusBar barStyle="light-content" backgroundColor="#1a1a1a" />

      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity style={styles.menuButton}>
          <Ionicons name="menu" size={24} color="#ffffff" />
        </TouchableOpacity>

        <TouchableOpacity style={styles.searchButton}>
          <Ionicons name="search" size={24} color="#ffffff" />
        </TouchableOpacity>

        <TouchableOpacity style={styles.notificationButton}>
          <Ionicons name="notifications" size={24} color="#ffffff" />
          <View style={styles.notificationBadge}>
            <Text style={styles.badgeText}>3</Text>
          </View>
        </TouchableOpacity>

        <TouchableOpacity style={styles.profileButton}>
          <View style={styles.profileAvatar}>
            <Ionicons name="person" size={20} color="#ffffff" />
          </View>
        </TouchableOpacity>
      </View>

      {/* Categories */}
      <View style={styles.categoriesContainer}>
        <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.categoriesScroll}>
          {categories.map((category) => (
            <TouchableOpacity
              key={category}
              style={[
                styles.categoryButton,
                selectedCategory === category && styles.selectedCategoryButton
              ]}
              onPress={() => setSelectedCategory(category)}
            >
              <Text style={[
                styles.categoryText,
                selectedCategory === category && styles.selectedCategoryText
              ]}>
                {category}
              </Text>
            </TouchableOpacity>
          ))}
        </ScrollView>
      </View>

      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* Featured Article */}
        <TouchableOpacity style={styles.featuredCard}>
          <Image source={{ uri: featuredArticle.image }} style={styles.featuredImage} />
          <View style={styles.featuredOverlay}>
            <View style={styles.featuredContent}>
              <Text style={styles.featuredDate}>{featuredArticle.date}</Text>
              <Text style={styles.featuredTitle}>{featuredArticle.title}</Text>
              <TouchableOpacity style={styles.expandButton}>
                <Ionicons name="arrow-up-right" size={20} color="#ffffff" />
              </TouchableOpacity>
            </View>
          </View>
        </TouchableOpacity>

        {/* Top Stories Section */}
        <View style={styles.topStoriesSection}>
          <View style={styles.sectionHeader}>
            <Text style={styles.sectionTitle}>Top Stories</Text>
            <TouchableOpacity>
              <Text style={styles.seeAllText}>See all</Text>
            </TouchableOpacity>
          </View>

          {/* Earthquake Alert */}
          <TouchableOpacity style={[styles.emergencyCard, { backgroundColor: theme.colors.cardBackground }]}>
            <View style={styles.emergencyIconContainer}>
              <Ionicons name="warning" size={20} color="#FF6B35" />
            </View>
            <View style={styles.emergencyContent}>
              <Text style={[styles.emergencyTitle, { color: theme.colors.text }]}>Malatya - 4.2 {t('earthquake')}</Text>
              <View style={styles.emergencyMeta}>
                <View style={styles.metaItem}>
                  <Ionicons name="location-outline" size={12} color={theme.colors.secondaryText} />
                  <Text style={[styles.metaText, { color: theme.colors.secondaryText }]}>Malatya Merkez</Text>
                </View>
                <View style={styles.metaItem}>
                  <Ionicons name="time-outline" size={12} color={theme.colors.secondaryText} />
                  <Text style={[styles.metaText, { color: theme.colors.secondaryText }]}>2 {t('minutesAgo')}</Text>
                </View>
              </View>
            </View>
          </TouchableOpacity>

          {/* Fire Alert */}
          <TouchableOpacity style={[styles.emergencyCard, { backgroundColor: theme.colors.cardBackground }]}>
            <View style={[styles.emergencyIconContainer, { backgroundColor: '#FFE5E5' }]}>
              <Ionicons name="flame" size={20} color="#FF3B30" />
            </View>
            <View style={styles.emergencyContent}>
              <Text style={[styles.emergencyTitle, { color: theme.colors.text }]}>{t('forestFire')}</Text>
              <View style={styles.emergencyMeta}>
                <View style={styles.metaItem}>
                  <Ionicons name="location-outline" size={12} color={theme.colors.secondaryText} />
                  <Text style={[styles.metaText, { color: theme.colors.secondaryText }]}>Antalya Manavgat</Text>
                </View>
                <View style={styles.metaItem}>
                  <Ionicons name="time-outline" size={12} color={theme.colors.secondaryText} />
                  <Text style={[styles.metaText, { color: theme.colors.secondaryText }]}>15 {t('minutesAgo')}</Text>
                </View>
              </View>
            </View>
          </TouchableOpacity>
        </View>

        {/* Share Information Button */}
        <TouchableOpacity 
          style={styles.shareButton}
          onPress={() => navigation.navigate('Emergency')}
        >
          <Ionicons name="add" size={20} color="#FFFFFF" />
          <Text style={styles.shareButtonText}>{t('shareInformationDesc')}</Text>
        </TouchableOpacity>

        {/* Community Posts Section */}
        <View style={styles.section}>
          <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>{t('communityUpdates')}</Text>

          {posts.map((post) => (
            <View key={post.id} style={[styles.postCard, { backgroundColor: theme.colors.cardBackground }]}>
              <View style={styles.postHeader}>
                <View style={styles.postAvatar}>
                  <Text style={styles.avatarText}>{post.author.charAt(0)}</Text>
                </View>
                <View style={styles.postUserInfo}>
                  <Text style={[styles.postUsername, { color: theme.colors.text }]}>{post.author}</Text>
                  {post.badge && (
                    <Text style={styles.postBadge}>{post.badge}</Text>
                  )}
                  <Text style={[styles.postTime, { color: theme.colors.secondaryText }]}>{post.time}</Text>
                  {post.location && (
                    <Text style={[styles.postLocation, { color: theme.colors.secondaryText }]}>{post.location}</Text>
                  )}
                </View>
                <TouchableOpacity style={[styles.followButton, { backgroundColor: post.badgeColor }]}>
                  <Text style={styles.followButtonText}>{post.badgeText}</Text>
                </TouchableOpacity>
              </View>
              <Text style={[styles.postContent, { color: theme.colors.text }]}>
                {post.content}
              </Text>
              <View style={styles.postActions}>
                <View style={styles.postStats}>
                  <TouchableOpacity 
                    style={styles.actionButton}
                    onPress={() => handleLike(post.id)}
                  >
                    <Ionicons 
                      name={post.isLiked ? "heart" : "heart-outline"} 
                      size={16} 
                      color={post.isLiked ? "#FF3B30" : theme.colors.secondaryText} 
                    />
                    <Text style={[styles.statText, { color: theme.colors.secondaryText }]}>{post.likes}</Text>
                  </TouchableOpacity>

                  <TouchableOpacity 
                    style={[styles.actionButton, { marginLeft: 16 }]}
                    onPress={() => handleComment(post.id)}
                  >
                    <Ionicons name="chatbubble-outline" size={16} color={theme.colors.secondaryText} />
                    <Text style={[styles.statText, { color: theme.colors.secondaryText }]}>{post.comments}</Text>
                  </TouchableOpacity>
                </View>

                <TouchableOpacity 
                  style={styles.shareButton}
                  onPress={() => handleShare(post)}
                >
                  <Ionicons name="share-outline" size={16} color={theme.colors.secondaryText} />
                </TouchableOpacity>
              </View>
            </View>
          ))}
        </View>
      </ScrollView>
    </View>
  );
};

