import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  Image,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

const HomeScreen = ({ navigation }) => {
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');
  const { theme } = useTheme();
  const { t } = useLanguage();

  // Sample news data based on the image
  const [trendingNews] = useState([
    {
      id: 1,
      category: 'Environment',
      categoryColor: '#00D4AA',
      title: 'Global Summit on Climate Change: Historic Agreement Reached',
      source: 'BBC News',
      verified: true,
      time: 'Jun 9, 2023',
      image: 'https://via.placeholder.com/300x200/4A90E2/FFFFFF?text=Climate+Summit',
    },
    {
      id: 2,
      category: 'Technology',
      categoryColor: '#007AFF',
      title: 'Tech Giant Unveils Revolutionary AI-powered Smartphone',
      source: 'The New York Times',
      verified: true,
      time: 'Jun 8, 2023',
      image: 'https://via.placeholder.com/300x200/FF6B35/FFFFFF?text=AI+Phone',
    },
  ]);

  const [recommendations] = useState([
    {
      id: 1,
      title: 'Tech Startup Secures $50 Million Funding for Expansion',
      category: 'Business',
      categoryColor: '#FF9500',
      source: 'Forbes',
      verified: true,
      time: 'Jun 11, 2023',
      image: 'https://via.placeholder.com/80x80/FF9500/FFFFFF?text=Startup',
    },
  ]);

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: theme.colors.background,
      paddingTop: insets.top,
    },
    header: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      paddingHorizontal: 16,
      paddingVertical: 12,
    },
    menuButton: {
      padding: 8,
    },
    notificationButton: {
      padding: 8,
    },
    welcomeSection: {
      paddingHorizontal: 16,
      paddingVertical: 20,
    },
    welcomeTitle: {
      fontSize: 28,
      fontWeight: '700',
      color: theme.colors.text,
      marginBottom: 8,
    },
    welcomeSubtitle: {
      fontSize: 16,
      color: theme.colors.secondaryText,
      lineHeight: 22,
    },
    sectionHeader: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      paddingHorizontal: 16,
      marginBottom: 16,
    },
    sectionTitle: {
      fontSize: 22,
      fontWeight: '700',
      color: theme.colors.text,
    },
    seeAllButton: {
      fontSize: 16,
      color: theme.colors.primary,
      fontWeight: '500',
    },
    trendingContainer: {
      paddingLeft: 16,
    },
    newsCard: {
      width: screenWidth * 0.75,
      backgroundColor: theme.colors.cardBackground,
      borderRadius: 16,
      marginRight: 16,
      overflow: 'hidden',
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 2,
      },
      shadowOpacity: 0.1,
      shadowRadius: 8,
      elevation: 4,
    },
    newsImage: {
      width: '100%',
      height: 160,
      backgroundColor: '#E5E5EA',
    },
    newsContent: {
      padding: 16,
    },
    categoryBadge: {
      alignSelf: 'flex-start',
      paddingHorizontal: 12,
      paddingVertical: 6,
      borderRadius: 20,
      marginBottom: 12,
    },
    categoryText: {
      fontSize: 12,
      fontWeight: '600',
      color: '#FFFFFF',
    },
    newsTitle: {
      fontSize: 16,
      fontWeight: '600',
      color: theme.colors.text,
      lineHeight: 22,
      marginBottom: 12,
    },
    newsFooter: {
      flexDirection: 'row',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
    sourceContainer: {
      flexDirection: 'row',
      alignItems: 'center',
      flex: 1,
    },
    sourceText: {
      fontSize: 14,
      color: theme.colors.text,
      fontWeight: '500',
      marginRight: 4,
    },
    timeText: {
      fontSize: 14,
      color: theme.colors.secondaryText,
    },
    recommendationSection: {
      paddingHorizontal: 16,
      marginTop: 32,
    },
    recommendationCard: {
      flexDirection: 'row',
      backgroundColor: theme.colors.cardBackground,
      borderRadius: 12,
      padding: 16,
      marginBottom: 12,
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 1,
      },
      shadowOpacity: 0.05,
      shadowRadius: 4,
      elevation: 2,
    },
    recommendationImage: {
      width: 60,
      height: 60,
      borderRadius: 8,
      backgroundColor: '#E5E5EA',
      marginRight: 12,
    },
    recommendationContent: {
      flex: 1,
      justifyContent: 'space-between',
    },
    recommendationTitle: {
      fontSize: 14,
      fontWeight: '600',
      color: theme.colors.text,
      lineHeight: 20,
      marginBottom: 8,
    },
    recommendationFooter: {
      flexDirection: 'row',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
    recommendationSource: {
      flexDirection: 'row',
      alignItems: 'center',
    },
    followButton: {
      backgroundColor: theme.colors.primary,
      paddingHorizontal: 16,
      paddingVertical: 6,
      borderRadius: 20,
    },
    followButtonText: {
      fontSize: 12,
      fontWeight: '600',
      color: '#FFFFFF',
    },
  });

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity style={styles.menuButton}>
          <Ionicons name="menu" size={24} color={theme.colors.text} />
        </TouchableOpacity>
        <TouchableOpacity 
          style={styles.notificationButton}
          onPress={() => navigation.navigate('Notifications')}
        >
          <Ionicons name="notifications-outline" size={24} color={theme.colors.text} />
        </TouchableOpacity>
      </View>

      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Welcome Section */}
        <View style={styles.welcomeSection}>
          <Text style={styles.welcomeTitle}>Welcome back, Tyler!</Text>
          <Text style={styles.welcomeSubtitle}>
            Discover a world of news that matters to you
          </Text>
        </View>

        {/* Trending News Section */}
        <View style={styles.sectionHeader}>
          <Text style={styles.sectionTitle}>Trending news</Text>
          <TouchableOpacity>
            <Text style={styles.seeAllButton}>See all</Text>
          </TouchableOpacity>
        </View>

        <ScrollView 
          horizontal 
          showsHorizontalScrollIndicator={false}
          style={styles.trendingContainer}
          contentContainerStyle={{ paddingRight: 16 }}
        >
          {trendingNews.map((news) => (
            <TouchableOpacity key={news.id} style={styles.newsCard}>
              <View style={styles.newsImage} />
              <View style={styles.newsContent}>
                <View style={[styles.categoryBadge, { backgroundColor: news.categoryColor }]}>
                  <Text style={styles.categoryText}>{news.category}</Text>
                </View>
                <Text style={styles.newsTitle}>{news.title}</Text>
                <View style={styles.newsFooter}>
                  <View style={styles.sourceContainer}>
                    <Text style={styles.sourceText}>{news.source}</Text>
                    {news.verified && (
                      <Ionicons name="checkmark-circle" size={16} color={theme.colors.primary} />
                    )}
                  </View>
                  <Text style={styles.timeText}>{news.time}</Text>
                </View>
              </View>
            </TouchableOpacity>
          ))}
        </ScrollView>

        {/* Recommendation Section */}
        <View style={styles.recommendationSection}>
          <View style={styles.sectionHeader}>
            <Text style={styles.sectionTitle}>Recommendation</Text>
          </View>

          {recommendations.map((item) => (
            <TouchableOpacity key={item.id} style={styles.recommendationCard}>
              <View style={styles.recommendationImage} />
              <View style={styles.recommendationContent}>
                <Text style={styles.recommendationTitle}>{item.title}</Text>
                <View style={styles.recommendationFooter}>
                  <View style={styles.recommendationSource}>
                    <Text style={styles.sourceText}>{item.source}</Text>
                    {item.verified && (
                      <Ionicons name="checkmark-circle" size={14} color={theme.colors.primary} style={{ marginLeft: 4 }} />
                    )}
                  </View>
                  <TouchableOpacity style={styles.followButton}>
                    <Text style={styles.followButtonText}>Follow</Text>
                  </TouchableOpacity>
                </View>
              </View>
            </TouchableOpacity>
          ))}
        </View>

        <View style={{ height: 100 }} />
      </ScrollView>
    </View>
  );
};

export default HomeScreen;
