import React, { useState, useEffect, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Dimensions,
  ScrollView,
  TouchableOpacity,
  Alert,
  Modal,
  Animated,
  TextInput,
  Image,
  Platform,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useLanguage } from '../contexts/LanguageContext';
import { useTheme } from '../contexts/ThemeContext';
import { useAuth } from '../contexts/AuthContext';
import { LinearGradient } from 'expo-linear-gradient';
// import * as ImagePicker from 'expo-image-picker'; // Temporarily commented out
import Header from '../components/Header';
import { supabase } from '../lib/supabase';

const ProfileScreen = ({ navigation }) => {
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');
  const { t } = useLanguage();
  const { theme } = useTheme();
  const { user, logout, updateUserProfile } = useAuth();

  // Animation values
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(50)).current;
  const scaleAnim = useRef(new Animated.Value(0.9)).current;

  const [userStats] = useState({
    totalShares: 12,
    familyMembers: 3
  });

  // User profile state
  const [userProfile, setUserProfile] = useState({
    firstName: user?.firstName || '',
    lastName: user?.lastName || '',
    email: user?.email || '',
    phone: user?.phone || '',
    location: user?.location || '',
    profileImage: user?.profileImage || null,
    isVerified: user?.isVerified || false,
    emergencyContact: user?.emergencyContact || ''
  });

  // Modal states
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [showAddMember, setShowAddMember] = useState(false);
  const [showEditMemberModal, setShowEditMemberModal] = useState(false);
  const [editingMember, setEditingMember] = useState(null);
  const [isSearchVisible, setIsSearchVisible] = useState(false);

  // Form states
  const [profileForm, setProfileForm] = useState({ 
    ...userProfile,
    age: user?.age || '',
    occupation: user?.occupation || '',
    bloodType: user?.bloodType || '',
    healthInfo: user?.healthInfo || ''
  });
  const [memberForm, setMemberForm] = useState({
    name: '',
    surname: '',
    relation: '',
    phone: '',
    emergencyContact: false
  });

  const startAnimations = () => {
    // Reset animation values
    fadeAnim.setValue(0);
    slideAnim.setValue(50);
    scaleAnim.setValue(0.9);

    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 800,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 600,
        useNativeDriver: true,
      }),
      Animated.timing(scaleAnim, {
        toValue: 1,
        duration: 500,
        useNativeDriver: true,
      }),
    ]).start();
  };

  useEffect(() => {
    startAnimations();
  }, [showAllMembers, theme.isDarkMode]);

  // Kullanıcı profil bilgilerini Supabase'den çek
  const loadUserProfile = async () => {
    if (!user?.id) return;

    try {
      const { data, error } = await supabase
        .from('profiles')
        .select('*')
        .eq('id', user.id)
        .single();

      if (error && error.code !== 'PGRST116') {
        throw error;
      }

      if (data) {
        setUserProfile({
          firstName: data.first_name || user.firstName || '',
          lastName: data.last_name || user.lastName || '',
          email: data.email || user.email || '',
          phone: data.phone || user.phone || '',
          location: data.location || '',
          profileImage: data.profile_image || null,
          isVerified: data.is_verified || false,
          emergencyContact: data.emergency_contact || ''
        });

        setProfileForm({
          firstName: data.first_name || user.firstName || '',
          lastName: data.last_name || user.lastName || '',
          email: data.email || user.email || '',
          phone: data.phone || user.phone || '',
          location: data.location || '',
          profileImage: data.profile_image || null,
          emergencyContact: data.emergency_contact || '',
          age: data.age || '',
          occupation: data.occupation || '',
          bloodType: data.blood_type || '',
          healthInfo: data.health_info || ''
        });
      }
    } catch (error) {
      console.error('Kullanıcı profili yüklenirken hata:', error);
      // Hata durumunda mevcut user bilgilerini kullan
      if (user) {
        setUserProfile({
          firstName: user.firstName || '',
          lastName: user.lastName || '',
          email: user.email || '',
          phone: user.phone || '',
          location: user.location || '',
          profileImage: user.profileImage || null,
          isVerified: user.isVerified || false,
          emergencyContact: user.emergencyContact || ''
        });
      }
