    if (!profileForm.firstName.trim() || !profileForm.lastName.trim()) {
      Alert.alert('Hata', 'Ad ve soyad alanları zorunludur.');
      return;
    }

    // E-posta format kontrolü
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(profileForm.email)) {
      Alert.alert('Hata', 'Geçerli bir e-posta adresi giriniz.');
      return;
    }

    // Telefon format kontrolü
    const phoneRegex = /^\+90\s?[0-9]{3}\s?[0-9]{3}\s?[0-9]{2}\s?[0-9]{2}$/;
    if (profileForm.phone && !phoneRegex.test(profileForm.phone)) {
      Alert.alert('Hata', 'Geçerli bir telefon numarası giriniz. (+90 5XX XXX XX XX)');
      return;
    }

    try {
      // AuthContext üzerinden profil güncelle
      const result = await updateUserProfile({
        firstName: profileForm.firstName,
        lastName: profileForm.lastName,
        phone: profileForm.phone,
        location: profileForm.location,
        emergencyContact: profileForm.emergencyContact,
        profileImage: profileForm.profileImage,
        age: profileForm.age,
        occupation: profileForm.occupation,
        bloodType: profileForm.bloodType,
        healthInfo: profileForm.healthInfo
