      } else {
        Alert.alert('Hata', result.error || 'Profil güncellenirken bir hata oluştu.');
      }
    } catch (error) {
      console.error('Profil güncelleme hatası:', error);
      Alert.alert('Hata', 'Profil güncellenirken bir hata oluştu.');
    }
