    const phoneRegex = /^\+90\s?[0-9]{3}\s?[0-9]{3}\s?[0-9]{2}\s?[0-9]{2}$/;
    if (!phoneRegex.test(memberForm.phone)) {
      Alert.alert('Hata', 'Geçerli bir telefon numarası giriniz. (+90 5XX XXX XX XX)');
      return;
