import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Switch,
  Modal,
  Alert,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

const SettingsScreen = () => {
  const insets = useSafeAreaInsets();
  const { theme, toggleTheme, isDarkMode } = useTheme();
  const { t, currentLanguage, changeLanguage, availableLanguages } = useLanguage();

  const [settings, setSettings] = useState({
    emergencyNotifications: true,
    familyNotifications: true,
    communityNotifications: false,
    soundNotifications: true,
    locationSharing: true,
    profileVisibility: false,
    dataCollection: true,
  });

  const [languageModalVisible, setLanguageModalVisible] = useState(false);

  const toggleSetting = (key) => {
    if (key === 'darkMode') {
      toggleTheme();
    } else {
      setSettings(prev => ({
        ...prev,
        [key]: !prev[key]
      }));
    }
  };

  const renderSettingItem = (title, subtitle, settingKey, icon) => {
    const value = settingKey === 'darkMode' ? isDarkMode : settings[settingKey];

    return (
      <View key={settingKey} style={[styles.settingItem, { backgroundColor: theme.colors.cardBackground }]}>
        <View style={styles.settingContent}>
          <View style={styles.settingInfo}>
            <Text style={[styles.settingTitle, { color: theme.colors.text }]}>{title}</Text>
            <Text style={[styles.settingSubtitle, { color: theme.colors.secondaryText }]}>{subtitle}</Text>
          </View>
          <Switch
            value={value}
            onValueChange={() => toggleSetting(settingKey)}
            trackColor={{ false: theme.colors.switchTrackOff, true: theme.colors.switchTrackOn }}
            thumbColor={theme.colors.switchThumb}
            ios_backgroundColor={theme.colors.switchTrackOff}
          />
        </View>
      </View>
    );
  };

  const renderMenuItemWithArrow = (title, subtitle, icon, onPress) => (
    <TouchableOpacity key={title} style={[styles.settingItem, { backgroundColor: theme.colors.cardBackground }]} onPress={onPress}>
      <View style={styles.settingContent}>
        <View style={styles.settingInfo}>
          <Text style={[styles.settingTitle, { color: theme.colors.text }]}>{title}</Text>
          {subtitle && <Text style={[styles.settingSubtitle, { color: theme.colors.secondaryText }]}>{subtitle}</Text>}
        </View>
        <Ionicons name="chevron-forward" size={20} color={theme.colors.tertiaryText} />
      </View>
    </TouchableOpacity>
  );

  const handleLanguagePress = () => {
    setLanguageModalVisible(true);
  };

  const handleLanguageSelect = (languageCode) => {
    changeLanguage(languageCode);
    setLanguageModalVisible(false);
  };

  const handleHelpPress = () => {
    console.log('Help center pressed');
  };

  const handleAboutPress = () => {
    console.log('About pressed');
  };

  const handlePrivacyPress = () => {
    console.log('Privacy policy pressed');
  };

  const handleTermsPress = () => {
    console.log('Terms of use pressed');
  };

  return (
    <View style={[styles.container, { paddingTop: insets.top }]}>
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.headerTitle}>AfetLink</Text>
        <TouchableOpacity style={styles.profileButton}>
          <Ionicons name="person-circle-outline" size={24} color="#007AFF" />
        </TouchableOpacity>
      </View>

      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* General Section */}
        <View style={styles.section}>
          <View style={styles.sectionHeader}>
            <Ionicons name="settings-outline" size={20} color="#8E8E93" />
            <Text style={styles.sectionTitle}>Genel</Text>
          </View>

          {renderSettingItem(
            'Karanlık Mod',
            'Koyu tema kullan',
            'darkMode'
          )}

          {renderMenuItemWithArrow(
            'Dil',
            settings.language,
            'language-outline',
            handleLanguagePress
          )}
        </View>

        {/* Notifications Section */}
        <View style={styles.section}>
          <View style={styles.sectionHeader}>
            <Ionicons name="notifications-outline" size={20} color="#8E8E93" />
            <Text style={styles.sectionTitle}>Bildirimler</Text>
          </View>

          {renderSettingItem(
            'Acil Durum Bildirimleri',
            'Afet ve acil durum uyarıları',
            'emergencyNotifications'
          )}

          {renderSettingItem(
            'Aile Bildirimleri',
            'Aile üyelerinden gelen bildirimler',
            'familyNotifications'
          )}

          {renderSettingItem(
            'Topluluk Bildirimleri',
            'Paylaşım ve yorumlar',
            'communityNotifications'
          )}

          {renderSettingItem(
            'Ses Bildirimleri',
            'Bildirim sesleri',
            'soundNotifications'
          )}
        </View>

        {/* Privacy & Security Section */}
        <View style={styles.section}>
          <View style={styles.sectionHeader}>
            <Ionicons name="shield-outline" size={20} color="#8E8E93" />
            <Text style={styles.sectionTitle}>Gizlilik ve Güvenlik</Text>
          </View>

          {renderSettingItem(
            'Konum Paylaşımı',
            'Aile üyeleriyle konum paylaş',
            'locationSharing'
          )}

          {renderSettingItem(
            'Profil Görünürlüğü',
            'Profilini diğer kullanıcılara göster',
            'profileVisibility'
          )}

          {renderSettingItem(
            'Veri Toplama',
            'Hizmet iyileştirme için veri kullanımı',
            'dataCollection'
          )}
        </View>

        {/* Support Section */}
        <View style={styles.section}>
          <View style={styles.sectionHeader}>
            <Ionicons name="help-circle-outline" size={20} color="#8E8E93" />
            <Text style={styles.sectionTitle}>Destek</Text>
          </View>

          {renderMenuItemWithArrow(
            'Yardım Merkezi',
            null,
            'help-circle-outline',
            handleHelpPress
          )}

          {renderMenuItemWithArrow(
            'Hakkında',
            null,
            'information-circle-outline',
            handleAboutPress
          )}

          {renderMenuItemWithArrow(
            'Gizlilik Politikası',
            null,
            'document-text-outline',
            handlePrivacyPress
          )}

          {renderMenuItemWithArrow(
            'Kullanım Şartları',
            null,
            'document-outline',
            handleTermsPress
          )}
        </View>

        {/* App Version */}
        <View style={styles.versionContainer}>
          <Text style={styles.versionText}>AfetLink v1.0.0</Text>
          <Text style={styles.copyrightText}>© 2024 AfetLink. Tüm hakları saklıdır.</Text>
        </View>

        <View style={styles.bottomSpacing} />
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F2F2F7',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    paddingHorizontal: 20,
    paddingVertical: 16,
    backgroundColor: '#FFFFFF',
    borderBottomWidth: 1,
    borderBottomColor: '#E5E5EA',
  },
  headerTitle: {
    fontSize: 20,
    fontWeight: '600',
    color: '#000000',
  },
  profileButton: {
    padding: 4,
  },
  scrollView: {
    flex: 1,
  },
  section: {
    marginTop: 24,
    paddingHorizontal: 16,
  },
  sectionHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 12,
    paddingHorizontal: 4,
  },
  sectionTitle: {
    fontSize: 16,
    fontWeight: '600',
    color: '#8E8E93',
    marginLeft: 8,
  },
  settingItem: {
    backgroundColor: '#FFFFFF',
    borderRadius: 12,
    marginBottom: 8,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.05,
    shadowRadius: 4,
    elevation: 2,
  },
  settingContent: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: 16,
  },
  settingInfo: {
    flex: 1,
    marginRight: 16,
  },
  settingTitle: {
    fontSize: 16,
    fontWeight: '500',
    color: '#000000',
    marginBottom: 4,
  },
  settingSubtitle: {
    fontSize: 14,
    color: '#8E8E93',
    lineHeight: 18,
  },
  bottomSpacing: {
    height: 50,
  },
  versionContainer: {
    alignItems: 'center',
    paddingVertical: 20,
    paddingHorizontal: 16,
  },
  versionText: {
    fontSize: 14,
    fontWeight: '600',
    color: '#8E8E93',
    marginBottom: 4,
  },
  copyrightText: {
    fontSize: 12,
    color: '#C7C7CC',
    textAlign: 'center',
  },
});

export default SettingsScreen;
