          <View style={styles.centeredProfileContent}>
            <View style={styles.avatarContainer}>
              <View style={styles.avatar}>
                <Ionicons name="person" size={32} color="#007AFF" />
              </View>
              <View style={styles.statusBadge}>
                <Text style={styles.statusText}>Doƒürulanmƒ±≈ü</Text>
              </View>
            </View>

            <Text style={[styles.userName, { color: '#FFFFFF' }]}>Kullanƒ±cƒ±</Text>
            <Text style={[styles.userLocation, { color: '#E8E8E8' }]}>üìç ƒ∞stanbul, T√ºrkiye</Text>

