import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Dimensions,
  ScrollView,
  TouchableOpacity,
  Alert,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';

const ProfileScreen = () => {
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');

  const [userStats] = useState({
    totalShares: 12,
    familyMembers: 3,
    securityScore: 85
  });

  const [familyMembers] = useState([
    {
      id: 1,
      name: 'Emine K.',
      status: 'Güvenli',
      lastSeen: '2 dakika önce',
      initial: 'E',
      statusColor: '#34C759'
    },
    {
      id: 2,
      name: 'Ali K.',
      status: 'Güvenli',
      lastSeen: '1 saat önce',
      initial: 'A',
      statusColor: '#34C759'
    },
    {
      id: 3,
      name: 'Fatma A.',
      status: 'Bilinmiyor',
      lastSeen: '3 saat önce',
      initial: 'F',
      statusColor: '#FF9500'
    }
  ]);

  const handleEmergencyReport = () => {
    Alert.alert(
      'Acil Durum Bildirimi',
      'Acil durum bildiriminiz yetkililere iletilecektir. Devam etmek istiyor musunuz?',
      [
        { text: 'İptal', style: 'cancel' },
        { text: 'Bildir', style: 'destructive', onPress: () => {
          Alert.alert('Başarılı', 'Acil durum bildiriminiz alındı.');
        }}
      ]
    );
  };

  const handleFamilyMemberPress = (member) => {
    Alert.alert(
      member.name,
      `Durum: ${member.status}\nSon görülme: ${member.lastSeen}`,
      [{ text: 'Tamam' }]
    );
  };

  const handleViewAllMembers = () => {
    Alert.alert('Tüm Aile Üyeleri', 'Aile üyeleri listesi genişletilecek.');
  };

  return (
    <View style={[styles.container, { paddingTop: insets.top }]}>
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.headerTitle}>AfetLink</Text>
        <TouchableOpacity style={styles.profileIcon}>
          <Ionicons name="person-circle-outline" size={24} color="#007AFF" />
        </TouchableOpacity>
      </View>

      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* User Profile Card */}
        <View style={styles.profileCard}>
          <View style={styles.profileHeader}>
            <View style={styles.avatarContainer}>
              <View style={styles.avatar}>
                <Ionicons name="person" size={32} color="#007AFF" />
              </View>
              <View style={styles.statusBadge}>
                <Text style={styles.statusText}>Doğrulanmış</Text>
              </View>
            </View>
            <TouchableOpacity style={styles.editButton}>
              <Ionicons name="create-outline" size={20} color="#8E8E93" />
            </TouchableOpacity>
          </View>

          <Text style={styles.userName}>Kullanıcı</Text>
          <Text style={styles.userLocation}>📍 İstanbul, Türkiye</Text>

          {/* Stats */}
          <View style={styles.statsContainer}>
            <View style={styles.statItem}>
              <Text style={styles.statNumber}>{userStats.totalShares}</Text>
              <Text style={styles.statLabel}>Toplam Paylaşım</Text>
            </View>
            <View style={styles.statItem}>
              <Text style={styles.statNumber}>{userStats.familyMembers}</Text>
              <Text style={styles.statLabel}>Aile Üyesi</Text>
            </View>
            <View style={styles.statItem}>
              <Text style={styles.statNumber}>{userStats.securityScore}</Text>
              <Text style={styles.statLabel}>Güvenlik Puanı</Text>
            </View>
          </View>
        </View>

        {/* Family Members Section */}
        <View style={styles.sectionContainer}>
          <View style={styles.sectionHeader}>
            <View style={styles.sectionTitleContainer}>
              <Ionicons name="people" size={20} color="#000000" />
              <Text style={styles.sectionTitle}>Aile Üyeleri</Text>
            </View>
            <TouchableOpacity onPress={handleViewAllMembers} style={styles.addButton}>
              <Text style={styles.addButtonText}>Ekle</Text>
            </TouchableOpacity>
          </View>

          {familyMembers.map((member) => (
            <TouchableOpacity 
              key={member.id} 
              style={styles.familyMemberItem}
              onPress={() => handleFamilyMemberPress(member)}
            >
              <View style={styles.memberInfo}>
                <View style={styles.memberAvatar}>
                  <Text style={styles.memberInitial}>{member.initial}</Text>
                </View>
                <View style={styles.memberDetails}>
                  <Text style={styles.memberName}>{member.name}</Text>
                  <Text style={styles.memberLastSeen}>⏰ {member.lastSeen}</Text>
                </View>
              </View>
              <View style={styles.memberStatus}>
                <View style={[styles.statusIndicator, { backgroundColor: member.statusColor }]} />
                <Text style={[styles.memberStatusText, { color: member.statusColor }]}>
                  {member.status}
                </Text>
              </View>
              <TouchableOpacity style={styles.memberOptions}>
                <Ionicons name="ellipsis-horizontal" size={16} color="#8E8E93" />
              </TouchableOpacity>
            </TouchableOpacity>
          ))}

          <TouchableOpacity onPress={handleViewAllMembers} style={styles.viewAllButton}>
            <Text style={styles.viewAllText}>Tüm Aile Üyelerini Görüntüle</Text>
          </TouchableOpacity>
        </View>

        {/* Safety Status */}
        <View style={styles.safetyContainer}>
          <View style={styles.safetyHeader}>
            <View style={styles.safetyIcon}>
              <Ionicons name="shield-checkmark" size={24} color="#34C759" />
            </View>
            <View style={styles.safetyInfo}>
              <Text style={styles.safetyTitle}>Durum: Güvenli</Text>
              <Text style={styles.safetySubtitle}>Son güncelleme: 5 dakika önce</Text>
            </View>
          </View>
        </View>

        {/* Emergency Report Button */}
        <TouchableOpacity style={styles.emergencyButton} onPress={handleEmergencyReport}>
          <Ionicons name="warning" size={20} color="#FFFFFF" />
          <Text style={styles.emergencyButtonText}>Acil Durum Bildir</Text>
        </TouchableOpacity>

        <View style={styles.bottomSpacing} />
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F2F2F7',
  },
  content: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 32,
  },
  title: {
    fontSize: 24,
    fontWeight: '600',
    color: '#000000',
    marginTop: 16,
    marginBottom: 8,
  },
  subtitle: {
    fontSize: 16,
    color: '#8E8E93',
    textAlign: 'center',
  },
});

export default ProfileScreen;
