import React, { useState, useEffect } from 'react';
import { View } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import { Ionicons } from '@expo/vector-icons';
import { StatusBar } from 'expo-status-bar';

import { ThemeProvider } from './src/contexts/ThemeContext';
import { LanguageProvider } from './src/contexts/LanguageContext';
import { NotificationProvider } from './src/contexts/NotificationContext';
import NotificationContainer from './src/components/NotificationContainer';
import HomeScreen from './src/screens/HomeScreen';
import RasathaneScreen from './src/screens/RasathaneScreen';
import MapScreen from './src/screens/MapScreen';
import ProfileScreen from './src/screens/ProfileScreen';
import SettingsScreen from './src/screens/SettingsScreen';
import EmergencyScreen from './src/screens/EmergencyScreen';
import SplashScreen from './src/screens/SplashScreen';
import HelpCenterScreen from './src/screens/HelpCenterScreen';
import AboutScreen from './src/screens/AboutScreen';
import PrivacyPolicyScreen from './src/screens/PrivacyPolicyScreen';
import TermsOfUseScreen from './src/screens/TermsOfUseScreen';
import NotificationsScreen from './src/screens/NotificationsScreen';
import PostDetailScreen from './src/screens/PostDetailScreen';

import { useTheme } from './src/contexts/ThemeContext';

const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

function TabNavigator() {
  const { theme } = useTheme();

  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;
          let iconSize = size;

          if (route.name === 'Ana Sayfa') {
            iconName = focused ? 'home' : 'home-outline';
          } else if (route.name === 'Rasathane') {
            iconName = focused ? 'analytics' : 'analytics-outline';
          } else if (route.name === 'Harita') {
            iconName = focused ? 'map' : 'map-outline';
            iconSize = route.name === 'Harita' ? 28 : size;

            // Ortadaki düğme için özel tasarım
            if (route.name === 'Harita') {
              return (
                <View style={{
                  width: 60,
                  height: 60,
                  borderRadius: 30,
                  backgroundColor: '#FF6B35',
                  justifyContent: 'center',
                  alignItems: 'center',
                  marginBottom: 15,
                  shadowColor: '#FF6B35',
                  shadowOffset: {
                    width: 0,
                    height: 6,
                  },
                  shadowOpacity: 0.4,
                  shadowRadius: 12,
                  elevation: 12,
                  borderWidth: 3,
                  borderColor: '#FFFFFF',
                }}>
                  <Ionicons name={iconName} size={iconSize} color="#FFFFFF" />
                </View>
              );
            }
          } else if (route.name === 'Aile') {
            iconName = focused ? 'people' : 'people-outline';
          } else if (route.name === 'Ayarlar') {
            iconName = focused ? 'settings' : 'settings-outline';
          }

          return <Ionicons name={iconName} size={iconSize} color={color} />;
        },
        tabBarActiveTintColor: '#FF6B35',
        tabBarInactiveTintColor: theme.colors.secondaryText,
        tabBarStyle: {
          backgroundColor: theme.colors.cardBackground,
          borderTopWidth: 0,
          height: 75,
          paddingBottom: 15,
          paddingTop: 8,
          paddingHorizontal: 15,
          borderTopLeftRadius: 25,
          borderTopRightRadius: 25,
          shadowColor: '#000',
          shadowOffset: {
            width: 0,
            height: -4,
          },
          shadowOpacity: 0.15,
          shadowRadius: 15,
          elevation: 15,
          position: 'absolute',
          borderTopColor: 'transparent',
        },
        tabBarLabelStyle: {
          fontSize: 10,
          fontWeight: '600',
          marginTop: 2,
          letterSpacing: 0.3,
        },
        tabBarItemStyle: {
          paddingVertical: 4,
          borderRadius: 8,
          marginHorizontal: 1,
        },
        headerShown: false,
      })}
    >
      <Tab.Screen 
        name="Ana Sayfa" 
        component={HomeScreen}
        options={{
          tabBarLabel: 'Ana Sayfa',
        }}
      />
      <Tab.Screen 
        name="Rasathane" 
        component={RasathaneScreen}
        options={{
          tabBarLabel: 'Rasathane',
        }}
      />
      <Tab.Screen 
        name="Harita" 
        component={MapScreen}
        options={{
          tabBarLabel: 'Haritalar',
        }}
      />
      <Tab.Screen 
        name="Aile" 
        component={ProfileScreen}
        options={{
          tabBarLabel: 'Aile',
        }}
      />
      <Tab.Screen 
        name="Ayarlar" 
        component={SettingsScreen}
        options={{
          tabBarLabel: 'Ayarlar',
        }}
      />
    </Tab.Navigator>
  );
}

function AppNavigator() {
  const { isDarkMode } = useTheme();

  return (
    <NavigationContainer>
      <StatusBar 
        style={isDarkMode ? "light" : "dark"} 
        backgroundColor={isDarkMode ? "#1a1a1a" : "#ffffff"}
        translucent={false}
      />
      <Stack.Navigator
        screenOptions={{
          headerShown: false,
        }}
      >
        <Stack.Screen name="Main" component={TabNavigator} />
        <Stack.Screen name="Emergency" component={EmergencyScreen} />
        <Stack.Screen name="Notifications" component={NotificationsScreen} />
        <Stack.Screen name="PostDetail" component={PostDetailScreen} />
        <Stack.Screen name="HelpCenter" component={HelpCenterScreen} />
        <Stack.Screen name="About" component={AboutScreen} />
        <Stack.Screen name="PrivacyPolicy" component={PrivacyPolicyScreen} />
        <Stack.Screen name="TermsOfUse" component={TermsOfUseScreen} />
      </Stack.Navigator>
      {/* <NotificationContainer /> */}
    </NavigationContainer>
  );
}

export default function App() {
  const [showSplash, setShowSplash] = useState(true);

  const handleSplashFinish = () => {
    setShowSplash(false);
  };

  return (
    <ThemeProvider>
      <LanguageProvider>
        <NotificationProvider>
          {showSplash ? (
            <SplashScreen onFinish={handleSplashFinish} />
          ) : (
            <AppNavigator />
          )}
        </NotificationProvider>
      </LanguageProvider>
    </ThemeProvider>
  );
}
