import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  SafeAreaView,
  ScrollView,
  TextInput,
  TouchableOpacity,
  Alert,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';

const RasathaneScreen = () => {
  const [searchText, setSearchText] = useState('');
  const [selectedFilter, setSelectedFilter] = useState('all');
  const [selectedTimeFilter, setSelectedTimeFilter] = useState('all');
  const [selectedSourceFilter, setSelectedSourceFilter] = useState('all');
  const [isFilterPanelOpen, setIsFilterPanelOpen] = useState(false);

  // Örnek deprem verileri
  const earthquakeData = [
    {
      id: 1,
      magnitude: 4.2,
      location: 'Şındırgı - Balıkesir',
      distance: '12.4 km',
      time: '17 dk önce',
      source: 'Kandilli',
      color: '#FFD60A',
      severity: 'Orta',
      minutesAgo: 17
    },
    {
      id: 2,
      magnitude: 2.4,
      location: 'Merkez - Malatya',
      distance: '8.7 km',
      time: '32 dk önce',
      source: 'AFAD',
      color: '#40E0D0',
      severity: 'Düşük',
      minutesAgo: 32
    },
    {
      id: 3,
      magnitude: 5.8,
      location: 'Biga - Çanakkale',
      distance: '15.2 km',
      time: '1 sa 15 dk önce',
      source: 'Kandilli',
      color: '#FF6B6B',
      severity: 'Büyük',
      minutesAgo: 75
    },
    {
      id: 4,
      magnitude: 3.1,
      location: 'Manavgat - Antalya',
      distance: '2 sa 45 dk önce',
      time: '2 sa 45 dk önce',
      source: 'İtfaiye',
      color: '#98FB98',
      severity: 'Düşük',
      minutesAgo: 165
    },
    {
      id: 5,
      magnitude: 3.1,
      location: 'Gönen - Balıkesir',
      distance: '9.8 km',
      time: '3 sa 12 dk önce',
      source: 'AFAD',
      color: '#98FB98',
      severity: 'Düşük',
      minutesAgo: 192
    },
    {
      id: 6,
      magnitude: 2.8,
      location: 'Merkez - Samsun',
      distance: '4 sa 30 dk önce',
      time: '4 sa 30 dk önce',
      source: 'Meteoroloji',
      color: '#FFD60A',
      severity: 'Orta',
      minutesAgo: 270
    }
  ];

  const filteredData = earthquakeData.filter(item => {
    const matchesSearch = item.location.toLowerCase().includes(searchText.toLowerCase());

    // Büyüklük filtresi
    let matchesMagnitude = true;
    if (selectedFilter === 'high') matchesMagnitude = item.magnitude >= 5.0;
    else if (selectedFilter === 'medium') matchesMagnitude = item.magnitude >= 3.0 && item.magnitude < 5.0;
    else if (selectedFilter === 'low') matchesMagnitude = item.magnitude < 3.0;

    // Zaman filtresi
    let matchesTime = true;
    if (selectedTimeFilter === '1hour') matchesTime = item.minutesAgo <= 60;
    else if (selectedTimeFilter === '24hours') matchesTime = item.minutesAgo <= 1440;
    else if (selectedTimeFilter === '7days') matchesTime = item.minutesAgo <= 10080;
    else if (selectedTimeFilter === '30days') matchesTime = item.minutesAgo <= 43200;

    // Kaynak filtresi
    let matchesSource = true;
    if (selectedSourceFilter !== 'all') {
      matchesSource = item.source.toLowerCase() === selectedSourceFilter.toLowerCase();
    }

    return matchesSearch && matchesMagnitude && matchesTime && matchesSource;
  });

  const handleEarthquakePress = (earthquake) => {
    Alert.alert(
      `${earthquake.magnitude} Büyüklük Deprem`,
      `Konum: ${earthquake.location}\nUzaklık: ${earthquake.distance}\nZaman: ${earthquake.time}\nKaynak: ${earthquake.source}\nŞiddet: ${earthquake.severity}`,
      [
        { text: 'Tamam', style: 'default' },
        { text: 'Detaylar', style: 'default', onPress: () => showDetails(earthquake) }
      ]
    );
  };

  const showDetails = (earthquake) => {
    Alert.alert(
      'Deprem Detayları',
      `Büyüklük: ${earthquake.magnitude}\nKonum: ${earthquake.location}\nUzaklık: ${earthquake.distance}\nZaman: ${earthquake.time}\nVeri Kaynağı: ${earthquake.source}\nŞiddet Seviyesi: ${earthquake.severity}\n\nBu deprem hakkında daha fazla bilgi için yerel otoritelere başvurun.`,
      [{ text: 'Tamam' }]
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity style={styles.backButton}>
          <Ionicons name="chevron-back" size={24} color="#000" />
        </TouchableOpacity>
        <Text style={styles.headerTitle}>Rasathane</Text>
        <View style={styles.headerActions}>
          <TouchableOpacity 
            style={styles.filterButton}
            onPress={() => setIsFilterPanelOpen(!isFilterPanelOpen)}
          >
            <Ionicons name="options" size={24} color="#000" />
          </TouchableOpacity>
          <TouchableOpacity style={styles.searchButton}>
            <Ionicons name="search" size={24} color="#000" />
          </TouchableOpacity>
        </View>
      </View>

      {/* Search Bar */}
      <View style={styles.searchContainer}>
        <Ionicons name="search" size={20} color="#8E8E93" style={styles.searchIcon} />
        <TextInput
          style={styles.searchInput}
          placeholder="Konum ara..."
          value={searchText}
          onChangeText={setSearchText}
          placeholderTextColor="#8E8E93"
        />
      </View>

      {/* Filter Panel */}
      {isFilterPanelOpen && (
        <View style={styles.filterPanel}>
          <View style={styles.filterPanelHeader}>
            <Text style={styles.filterPanelTitle}>Filtreler</Text>
            <TouchableOpacity 
              onPress={() => setIsFilterPanelOpen(false)}
              style={styles.closeButton}
            >
              <Ionicons name="close" size={20} color="#8E8E93" />
            </TouchableOpacity>
          </View>

          <View style={styles.filterSection}>
            <Text style={styles.filterSectionTitle}>Büyüklük</Text>
            <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.filterContainer}>
              <TouchableOpacity 
                style={[styles.filterChip, selectedFilter === 'all' && styles.filterChipActive]}
                onPress={() => setSelectedFilter('all')}
              >
                <Text style={[styles.filterText, selectedFilter === 'all' && styles.filterTextActive]}>Tümü</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.filterChip, selectedFilter === 'high' && styles.filterChipActive]}
                onPress={() => setSelectedFilter('high')}
              >
                <Text style={[styles.filterText, selectedFilter === 'high' && styles.filterTextActive]}>Büyük (5.0+)</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.filterChip, selectedFilter === 'medium' && styles.filterChipActive]}
                onPress={() => setSelectedFilter('medium')}
              >
                <Text style={[styles.filterText, selectedFilter === 'medium' && styles.filterTextActive]}>Orta (3.0-4.9)</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.filterChip, selectedFilter === 'low' && styles.filterChipActive]}
                onPress={() => setSelectedFilter('low')}
              >
                <Text style={[styles.filterText, selectedFilter === 'low' && styles.filterTextActive]}>Düşük (&lt;3.0)</Text>
              </TouchableOpacity>
            </ScrollView>
          </View>

          <View style={styles.filterSection}>
            <Text style={styles.filterSectionTitle}>Zaman</Text>
            <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.filterContainer}>
              <TouchableOpacity 
                style={[styles.filterChip, selectedTimeFilter === 'all' && styles.filterChipActive]}
                onPress={() => setSelectedTimeFilter('all')}
              >
                <Text style={[styles.filterText, selectedTimeFilter === 'all' && styles.filterTextActive]}>Tümü</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.filterChip, selectedTimeFilter === '1hour' && styles.filterChipActive]}
                onPress={() => setSelectedTimeFilter('1hour')}
              >
                <Text style={[styles.filterText, selectedTimeFilter === '1hour' && styles.filterTextActive]}>Son 1 saat</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.filterChip, selectedTimeFilter === '24hours' && styles.filterChipActive]}
                onPress={() => setSelectedTimeFilter('24hours')}
              >
                <Text style={[styles.filterText, selectedTimeFilter === '24hours' && styles.filterTextActive]}>Son 24 saat</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.filterChip, selectedTimeFilter === '7days' && styles.filterChipActive]}
                onPress={() => setSelectedTimeFilter('7days')}
              >
                <Text style={[styles.filterText, selectedTimeFilter === '7days' && styles.filterTextActive]}>Son 7 gün</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.filterChip, selectedTimeFilter === '30days' && styles.filterChipActive]}
                onPress={() => setSelectedTimeFilter('30days')}
              >
                <Text style={[styles.filterText, selectedTimeFilter === '30days' && styles.filterTextActive]}>Son 30 gün</Text>
              </TouchableOpacity>
            </ScrollView>
          </View>

          <View style={styles.filterSection}>
            <Text style={styles.filterSectionTitle}>Kaynak</Text>
            <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.filterContainer}>
              <TouchableOpacity 
                style={[styles.filterChip, selectedSourceFilter === 'all' && styles.filterChipActive]}
                onPress={() => setSelectedSourceFilter('all')}
              >
                <Text style={[styles.filterText, selectedSourceFilter === 'all' && styles.filterTextActive]}>Tümü</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.filterChip, selectedSourceFilter === 'AFAD' && styles.filterChipActive]}
                onPress={() => setSelectedSourceFilter('AFAD')}
              >
                <Text style={[styles.filterText, selectedSourceFilter === 'AFAD' && styles.filterTextActive]}>AFAD</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.filterChip, selectedSourceFilter === 'Kandilli' && styles.filterChipActive]}
                onPress={() => setSelectedSourceFilter('Kandilli')}
              >
                <Text style={[styles.filterText, selectedSourceFilter === 'Kandilli' && styles.filterTextActive]}>Kandilli</Text>
              </TouchableOpacity>
              <TouchableOpacity 
                style={[styles.filterChip, selectedSourceFilter === 'Meteoroloji' && styles.filterChipActive]}
                onPress={() => setSelectedSourceFilter('Meteoroloji')}
              >
                <Text style={[styles.filterText, selectedSourceFilter === 'Meteoroloji' && styles.filterTextActive]}>Meteoroloji</Text>
              </TouchableOpacity>
            </ScrollView>
          </View>
        </View>
      )}

      {/* Earthquake List */}
      <ScrollView style={styles.listContainer} showsVerticalScrollIndicator={false}>
        {filteredData.map((earthquake) => (
          <TouchableOpacity 
            key={earthquake.id} 
            style={styles.earthquakeCard}
            onPress={() => handleEarthquakePress(earthquake)}
            activeOpacity={0.7}
          >
            <View style={styles.cardLeft}>
              <View style={[styles.magnitudeCircle, { backgroundColor: earthquake.color }]}>
                <Text style={styles.magnitudeText}>{earthquake.magnitude}</Text>
              </View>
              <View style={styles.cardInfo}>
                <Text style={styles.locationText}>{earthquake.location}</Text>
                <View style={styles.cardDetails}>
                  <Text style={styles.distanceText}>{earthquake.distance}</Text>
                  <Text style={styles.timeText}>{earthquake.time}</Text>
                  <Text style={styles.sourceText}>{earthquake.source}</Text>
                </View>
              </View>
            </View>
            <Ionicons name="chevron-forward" size={20} color="#C7C7CC" />
          </TouchableOpacity>
        ))}
      </ScrollView>
    </SafeAreaView>
  );
};

