import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  Image,
  TextInput,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';

const HomeScreen = ({ navigation }) => {
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');
  const { theme } = useTheme();
  const { t } = useLanguage();

  const [selectedCategory, setSelectedCategory] = useState('Travel');
  const [searchText, setSearchText] = useState('');

  const categories = ['Travel', 'Technology', 'Business'];

  const articles = [
    {
      id: 1,
      title: 'Uncovering the Hidden Gems of the Amazon Forest',
      author: 'Mr. Lara Kult',
      date: 'May 1, 2023',
      image: 'https://images.unsplash.com/photo-1441974231531-c6227db76b6e?w=400&h=200&fit=crop',
      category: 'Travel'
    },
    {
      id: 2,
      title: 'Experience the Serenity of Japan\'s Traditional...',
      author: 'Hilda Friesan',
      date: 'May 3, 2023',
      image: 'https://images.unsplash.com/photo-1522199755839-a2bacb67c546?w=400&h=200&fit=crop',
      category: 'Travel'
    },
    {
      id: 3,
      title: 'A Journey Through Time: Discovering the Nile river',
      author: 'Melissa White',
      date: 'May 5, 2023',
      image: 'https://images.unsplash.com/photo-1539650116574-75c0c6d73f6e?w=400&h=200&fit=crop',
      category: 'Travel'
    },
    {
      id: 4,
      title: 'Chasing the Northern Lights: A Winter Wonderland',
      author: 'Jeanne Conn',
      date: 'May 12, 2023',
      image: 'https://images.unsplash.com/photo-1531366936337-7c912a4589a7?w=400&h=200&fit=crop',
      category: 'Travel'
    }
  ];

  const filteredArticles = articles.filter(article => 
    article.category === selectedCategory &&
    (searchText === '' || article.title.toLowerCase().includes(searchText.toLowerCase()))
  );

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: theme.colors.background,
      paddingTop: insets.top,
    },
    header: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      paddingHorizontal: 20,
      paddingVertical: 16,
    },
    title: {
      fontSize: 28,
      fontWeight: '700',
      color: theme.colors.text,
    },
    searchButton: {
      padding: 8,
    },
    searchContainer: {
      flexDirection: 'row',
      alignItems: 'center',
      backgroundColor: theme.colors.surface,
      marginHorizontal: 20,
      marginBottom: 20,
      borderRadius: 12,
      paddingHorizontal: 16,
      paddingVertical: 12,
    },
    searchInput: {
      flex: 1,
      fontSize: 16,
      color: theme.colors.text,
      marginLeft: 8,
    },
    categoriesContainer: {
      paddingHorizontal: 20,
      marginBottom: 20,
    },
    categoriesRow: {
      flexDirection: 'row',
      justifyContent: 'space-around',
    },
    categoryButton: {
      paddingVertical: 12,
      paddingHorizontal: 24,
      borderRadius: 20,
      backgroundColor: 'transparent',
    },
    categoryButtonActive: {
      backgroundColor: theme.colors.primary,
    },
    categoryText: {
      fontSize: 16,
      fontWeight: '500',
      color: theme.colors.secondaryText,
    },
    categoryTextActive: {
      color: theme.colors.onPrimary,
      fontWeight: '600',
    },
    articlesContainer: {
      paddingHorizontal: 20,
    },
    articleCard: {
      backgroundColor: theme.colors.cardBackground,
      borderRadius: 16,
      marginBottom: 20,
      overflow: 'hidden',
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 2,
      },
      shadowOpacity: 0.1,
      shadowRadius: 8,
      elevation: 4,
    },
    articleImage: {
      width: '100%',
      height: 200,
      backgroundColor: theme.colors.surface,
    },
    articleContent: {
      padding: 16,
    },
    articleTitle: {
      fontSize: 18,
      fontWeight: '600',
      color: theme.colors.text,
      marginBottom: 12,
      lineHeight: 24,
    },
    articleMeta: {
      flexDirection: 'row',
      alignItems: 'center',
    },
    authorAvatar: {
      width: 24,
      height: 24,
      borderRadius: 12,
      backgroundColor: theme.colors.primary,
      marginRight: 8,
    },
    articleAuthor: {
      fontSize: 14,
      color: theme.colors.secondaryText,
      marginRight: 8,
    },
    articleDate: {
      fontSize: 14,
      color: theme.colors.secondaryText,
    },
    smallArticleCard: {
      flexDirection: 'row',
      backgroundColor: theme.colors.cardBackground,
      borderRadius: 12,
      marginBottom: 16,
      overflow: 'hidden',
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 1,
      },
      shadowOpacity: 0.05,
      shadowRadius: 4,
      elevation: 2,
    },
    smallArticleImage: {
      width: 80,
      height: 80,
      backgroundColor: theme.colors.surface,
    },
    smallArticleContent: {
      flex: 1,
      padding: 12,
      justifyContent: 'space-between',
    },
    smallArticleTitle: {
      fontSize: 14,
      fontWeight: '600',
      color: theme.colors.text,
      lineHeight: 18,
      marginBottom: 8,
    },
    smallArticleMeta: {
      flexDirection: 'row',
      alignItems: 'center',
    },
    smallAuthorAvatar: {
      width: 16,
      height: 16,
      borderRadius: 8,
      backgroundColor: theme.colors.primary,
      marginRight: 6,
    },
    smallArticleAuthor: {
      fontSize: 12,
      color: theme.colors.secondaryText,
      marginRight: 6,
    },
    smallArticleDate: {
      fontSize: 12,
      color: theme.colors.secondaryText,
    },
  });

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.title}>Explore</Text>
        <TouchableOpacity style={styles.searchButton}>
          <Ionicons name="search" size={24} color={theme.colors.text} />
        </TouchableOpacity>
      </View>

      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Search Bar */}
        <View style={styles.searchContainer}>
          <Ionicons name="search" size={20} color={theme.colors.secondaryText} />
          <TextInput
            style={styles.searchInput}
            placeholder="Search articles..."
            placeholderTextColor={theme.colors.secondaryText}
            value={searchText}
            onChangeText={setSearchText}
          />
        </View>

        {/* Categories */}
        <View style={styles.categoriesContainer}>
          <View style={styles.categoriesRow}>
            {categories.map((category) => (
              <TouchableOpacity
                key={category}
                style={[
                  styles.categoryButton,
                  selectedCategory === category && styles.categoryButtonActive
                ]}
                onPress={() => setSelectedCategory(category)}
              >
                <Text style={[
                  styles.categoryText,
                  selectedCategory === category && styles.categoryTextActive
                ]}>
                  {category}
                </Text>
              </TouchableOpacity>
            ))}
          </View>
        </View>

        {/* Articles */}
        <View style={styles.articlesContainer}>
          {filteredArticles.map((article, index) => {
            if (index === 0) {
              // Featured article (large card)
              return (
                <TouchableOpacity key={article.id} style={styles.articleCard}>
                  <View style={styles.articleImage} />
                  <View style={styles.articleContent}>
                    <Text style={styles.articleTitle}>{article.title}</Text>
                    <View style={styles.articleMeta}>
                      <View style={styles.authorAvatar} />
                      <Text style={styles.articleAuthor}>{article.author}</Text>
                      <Text style={styles.articleDate}>• {article.date}</Text>
                    </View>
                  </View>
                </TouchableOpacity>
              );
            } else {
              // Small articles
              return (
                <TouchableOpacity key={article.id} style={styles.smallArticleCard}>
                  <View style={styles.smallArticleImage} />
                  <View style={styles.smallArticleContent}>
                    <Text style={styles.smallArticleTitle} numberOfLines={2}>
                      {article.title}
                    </Text>
                    <View style={styles.smallArticleMeta}>
                      <View style={styles.smallAuthorAvatar} />
                      <Text style={styles.smallArticleAuthor}>{article.author}</Text>
                      <Text style={styles.smallArticleDate}>• {article.date}</Text>
                    </View>
                  </View>
                </TouchableOpacity>
              );
            }
          })}
        </View>

        <View style={{ height: 100 }} />
      </ScrollView>
    </View>
  );
};

export default HomeScreen;
