import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Alert,
  ScrollView,
  ActivityIndicator
} from 'react-native';
import { useAuth } from '../contexts/AuthContext';

const AuthorApplication = ({ onClose, onSuccess }) => {
  const { user, API_BASE_URL } = useAuth();
  const [formData, setFormData] = useState({
    organization: '',
    bio: ''
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};

    if (!formData.organization.trim()) {
      newErrors.organization = 'Kuruluş adı gereklidir';
    } else if (formData.organization.length < 2) {
      newErrors.organization = 'Kuruluş adı en az 2 karakter olmalıdır';
    }

    if (!formData.bio.trim()) {
      newErrors.bio = 'Biyografi gereklidir';
    } else if (formData.bio.length < 50) {
      newErrors.bio = 'Biyografi en az 50 karakter olmalıdır';
    } else if (formData.bio.length > 1000) {
      newErrors.bio = 'Biyografi en fazla 1000 karakter olabilir';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async () => {
    if (!validateForm()) {
      return;
    }

    setLoading(true);
    try {
      const token = localStorage.getItem('auth_token');
      const response = await fetch(`${API_BASE_URL}/users/apply-author`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(formData)
      });

      const data = await response.json();

      if (data.success) {
        Alert.alert(
          'Başvuru Gönderildi',
          'Yazar başvurunuz başarıyla gönderildi. Başvurunuz yöneticiler tarafından incelenecektir.',
          [
            {
              text: 'Tamam',
              onPress: () => {
                onSuccess && onSuccess();
                onClose && onClose();
              }
            }
          ]
        );
      } else {
        Alert.alert('Hata', data.error || 'Başvuru gönderilirken bir hata oluştu');
      }
    } catch (error) {
      console.error('Author application error:', error);
      Alert.alert('Hata', 'Başvuru gönderilirken bir hata oluştu');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Clear error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: null }));
    }
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Yazar Başvurusu</Text>
        <Text style={styles.subtitle}>
          Haber içerikleri oluşturmak için yazar olmaya başvurun
        </Text>
      </View>

      <View style={styles.form}>
        <View style={styles.inputGroup}>
          <Text style={styles.label}>Kuruluş Adı *</Text>
          <TextInput
            style={[styles.input, errors.organization && styles.inputError]}
            value={formData.organization}
            onChangeText={(value) => handleInputChange('organization', value)}
            placeholder="Örn: AFAD, TRT Haber, Kandilli Rasathanesi"
            placeholderTextColor="#999"
          />
          {errors.organization && (
            <Text style={styles.errorText}>{errors.organization}</Text>
          )}
          <Text style={styles.helpText}>
            Temsil ettiğiniz resmi kuruluşun adını yazın
          </Text>
        </View>

        <View style={styles.inputGroup}>
          <Text style={styles.label}>Biyografi *</Text>
          <TextInput
            style={[styles.textArea, errors.bio && styles.inputError]}
            value={formData.bio}
            onChangeText={(value) => handleInputChange('bio', value)}
            placeholder="Kendinizi ve temsil ettiğiniz kuruluştaki pozisyonunuzu tanıtın. Hangi konularda haber yazacağınızı ve uzmanlık alanlarınızı belirtin."
            placeholderTextColor="#999"
            multiline
            numberOfLines={6}
            textAlignVertical="top"
          />
          {errors.bio && (
            <Text style={styles.errorText}>{errors.bio}</Text>
          )}
          <Text style={styles.helpText}>
            {formData.bio.length}/1000 karakter (minimum 50 karakter)
          </Text>
        </View>

        <View style={styles.infoBox}>
          <Text style={styles.infoTitle}>Yazar Yetkileri:</Text>
          <Text style={styles.infoText}>• Haber içerikleri oluşturabilirsiniz</Text>
          <Text style={styles.infoText}>• Kendi yazılarınızı düzenleyebilirsiniz</Text>
          <Text style={styles.infoText}>• Yazılarınıza etiket ekleyebilirsiniz</Text>
          <Text style={styles.infoText}>• Resmi kuruluşunuzu temsil edebilirsiniz</Text>
        </View>

        <View style={styles.buttonContainer}>
          <TouchableOpacity
            style={styles.cancelButton}
            onPress={onClose}
            disabled={loading}
          >
            <Text style={styles.cancelButtonText}>İptal</Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={[styles.submitButton, loading && styles.submitButtonDisabled]}
            onPress={handleSubmit}
            disabled={loading}
          >
            {loading ? (
              <ActivityIndicator color="#fff" size="small" />
            ) : (
              <Text style={styles.submitButtonText}>Başvuru Gönder</Text>
            )}
          </TouchableOpacity>
        </View>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f5f5f5'
  },
  header: {
    backgroundColor: '#fff',
    padding: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#e0e0e0'
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#333',
    marginBottom: 8
  },
  subtitle: {
    fontSize: 16,
    color: '#666',
    lineHeight: 22
  },
  form: {
    padding: 20
  },
  inputGroup: {
    marginBottom: 20
  },
  label: {
    fontSize: 16,
    fontWeight: '600',
    color: '#333',
    marginBottom: 8
  },
  input: {
    backgroundColor: '#fff',
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 8,
    padding: 12,
    fontSize: 16,
    color: '#333'
  },
  textArea: {
    backgroundColor: '#fff',
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 8,
    padding: 12,
    fontSize: 16,
    color: '#333',
    minHeight: 120
  },
  inputError: {
    borderColor: '#e74c3c'
  },
  errorText: {
    color: '#e74c3c',
    fontSize: 14,
    marginTop: 4
  },
  helpText: {
    color: '#666',
    fontSize: 14,
    marginTop: 4
  },
  infoBox: {
    backgroundColor: '#e8f4fd',
    padding: 16,
    borderRadius: 8,
    marginBottom: 20
  },
  infoTitle: {
    fontSize: 16,
    fontWeight: '600',
    color: '#2c3e50',
    marginBottom: 8
  },
  infoText: {
    fontSize: 14,
    color: '#34495e',
    marginBottom: 4
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    gap: 12
  },
  cancelButton: {
    flex: 1,
    backgroundColor: '#fff',
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 8,
    padding: 16,
    alignItems: 'center'
  },
  cancelButtonText: {
    fontSize: 16,
    fontWeight: '600',
    color: '#666'
  },
  submitButton: {
    flex: 1,
    backgroundColor: '#3498db',
    borderRadius: 8,
    padding: 16,
    alignItems: 'center'
  },
  submitButtonDisabled: {
    backgroundColor: '#bdc3c7'
  },
  submitButtonText: {
    fontSize: 16,
    fontWeight: '600',
    color: '#fff'
  }
});

export default AuthorApplication;
