import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Dimensions,
  ScrollView,
  TouchableOpacity,
  Alert,
  Modal,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useLanguage } from '../contexts/LanguageContext';
import { useTheme } from '../contexts/ThemeContext';

const ProfileScreen = () => {
  const insets = useSafeAreaInsets();
  const { width: screenWidth } = Dimensions.get('window');
  const { t } = useLanguage();
  const { theme } = useTheme();

  const [userStats] = useState({
    totalShares: 12,
    familyMembers: 3
  });

  const [familyMembers, setFamilyMembers] = useState([
    {
      id: 1,
      name: 'Emine K.',
      relation: t('wife'),
      phone: '+90 532 XXX XX XX',
      status: t('safe'),
      lastSeen: `2 ${t('minutesAgo')}`,
      initial: 'E',
      statusColor: '#34C759'
    },
    {
      id: 2,
      name: 'Ali K.',
      relation: t('son'),
      phone: '+90 533 XXX XX XX',
      status: t('safe'),
      lastSeen: `5 ${t('minutesAgo')}`,
      initial: 'A',
      statusColor: '#34C759'
    },
    {
      id: 3,
      name: 'Fatma A.',
      relation: t('daughter'),
      phone: '+90 534 XXX XX XX',
      status: t('unknown'),
      lastSeen: `1 ${t('hoursAgo')}`,
      initial: 'F',
      statusColor: '#FF9500'
    }
  ]);

  const [showAllMembers, setShowAllMembers] = useState(false);

  const handleEmergencyReport = () => {
    Alert.alert(
      'Acil Durum Bildirimi',
      'Acil durum bildiriminiz yetkililere iletilecektir. Devam etmek istiyor musunuz?',
      [
        { text: 'İptal', style: 'cancel' },
        { text: 'Bildir', style: 'destructive', onPress: () => {
          Alert.alert('Başarılı', 'Acil durum bildiriminiz alındı.');
        }}
      ]
    );
  };

  const handleFamilyMemberPress = (member) => {
    setShowAllMembers(true);
  };

  const handleViewAllMembers = () => {
    setShowAllMembers(true);
  };

  const handleCallMember = (phone) => {
    Alert.alert('Arama', `${phone} numarası aranıyor...`);
  };

  const handleLocationRequest = (memberName) => {
    Alert.alert('Konum Sor', `${memberName} kişisine konum talebi gönderildi.`);
  };

  const handleEditMember = (member) => {
    Alert.alert('Düzenle', `${member.name} bilgileri düzenleniyor...`);
  };

  const handleDeleteMember = (memberId) => {
    Alert.alert(
      'Üyeyi Sil',
      'Bu aile üyesini silmek istediğinizden emin misiniz?',
      [
        { text: 'İptal', style: 'cancel' },
        { 
          text: 'Sil', 
          style: 'destructive', 
          onPress: () => {
            setFamilyMembers(prev => prev.filter(member => member.id !== memberId));
            Alert.alert('Başarılı', 'Aile üyesi silindi.');
          }
        }
      ]
    );
  };

  return (
    <View style={[styles.container, { paddingTop: insets.top }]}>
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.headerTitle}>{t('appName')}</Text>
        <TouchableOpacity style={styles.profileIcon}>
          <Ionicons name="person-circle-outline" size={24} color="#007AFF" />
        </TouchableOpacity>
      </View>

      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* User Profile Card */}
        <View style={styles.profileCard}>
          <View style={styles.centeredProfileContent}>
            <View style={styles.avatarContainer}>
              <View style={styles.avatar}>
                <Ionicons name="person" size={32} color="#007AFF" />
              </View>
              <View style={styles.statusBadge}>
                <Text style={styles.statusText}>Doğrulanmış</Text>
              </View>
            </View>

            <Text style={styles.userName}>Kullanıcı</Text>
            <Text style={styles.userLocation}>📍 İstanbul, Türkiye</Text>

            {/* Stats */}
            <View style={styles.statsContainer}>
              <View style={styles.statItem}>
                <Text style={styles.statNumber}>{userStats.totalShares}</Text>
                <Text style={styles.statLabel}>{t('totalShares')}</Text>
              </View>
              <View style={styles.statItem}>
                <Text style={styles.statNumber}>{userStats.familyMembers}</Text>
                <Text style={styles.statLabel}>{t('familyMembers')}</Text>
              </View>
            </View>
          </View>

          <TouchableOpacity style={styles.editButton}>
            <Ionicons name="create-outline" size={20} color="#8E8E93" />
          </TouchableOpacity>
        </View>

        {/* Family Members Section */}
