                <View style={[styles.statusIndicator, { backgroundColor: member.statusColor }]} />
                <Text style={[styles.memberStatusText, { color: member.statusColor }]}>
                  {member.status}
                </Text>
              </View>
              <TouchableOpacity style={styles.memberOptions}>
                <Ionicons name="ellipsis-horizontal" size={16} color="#8E8E93" />
              </TouchableOpacity>
            </TouchableOpacity>
          ))}

          <TouchableOpacity onPress={handleViewAllMembers} style={styles.viewAllButton}>
