          <View style={styles.safetyHeader}>
            <View style={styles.safetyIcon}>
              <Ionicons name="shield-checkmark" size={24} color="#34C759" />
            </View>
            <View style={styles.safetyInfo}>
              <Text style={styles.safetyTitle}>Durum: Güvenli</Text>
              <Text style={styles.safetySubtitle}>Son güncelleme: 5 dakika önce</Text>
            </View>
          </View>
        </View>

        {/* Emergency Report Button */}
        <TouchableOpacity style={styles.emergencyButton} onPress={handleEmergencyReport}>
          <Ionicons name="warning" size={20} color="#FFFFFF" />
          <Text style={styles.emergencyButtonText}>Acil Durum Bildir</Text>
        </TouchableOpacity>

        <View style={styles.bottomSpacing} />
      </ScrollView>

      {/* Family Members Detail Modal */}
      <Modal
        visible={showAllMembers}
        animationType="slide"
        presentationStyle="pageSheet"
      >
        <View style={[styles.modalContainer, { paddingTop: insets.top }]}>
          {/* Modal Header */}
          <View style={styles.modalHeader}>
            <Text style={styles.modalTitle}>Aile Üyeleri</Text>
            <TouchableOpacity 
              style={styles.modalCloseButton}
              onPress={() => setShowAllMembers(false)}
            >
              <Ionicons name="close" size={24} color="#8E8E93" />
            </TouchableOpacity>
          </View>

          <ScrollView style={styles.modalScrollView} showsVerticalScrollIndicator={false}>
            {familyMembers.map((member) => (
              <View key={member.id} style={styles.detailedMemberCard}>
                <View style={styles.detailedMemberHeader}>
                  <View style={styles.detailedMemberInfo}>
                    <View style={styles.detailedMemberAvatar}>
                      <Text style={styles.detailedMemberInitial}>{member.initial}</Text>
                    </View>
                    <View style={styles.detailedMemberDetails}>
                      <Text style={styles.detailedMemberName}>{member.name}</Text>
                      <Text style={styles.detailedMemberRelation}>{member.relation}</Text>
                      <Text style={styles.detailedMemberPhone}>{member.phone}</Text>
                      <Text style={styles.detailedMemberLastSeen}>Son görülme: {member.lastSeen}</Text>
                    </View>
                  </View>
                  <View style={styles.detailedMemberStatus}>
                    <View style={[styles.detailedStatusIndicator, { backgroundColor: member.statusColor }]} />
                    <Text style={[styles.detailedMemberStatusText, { color: member.statusColor }]}>
                      {member.status}
                    </Text>
                  </View>
                  <TouchableOpacity 
                    style={styles.detailedMemberOptions}
                    onPress={() => handleEditMember(member)}
                  >
                    <Ionicons name="create-outline" size={20} color="#8E8E93" />
                  </TouchableOpacity>
                  <TouchableOpacity 
                    style={styles.detailedMemberDelete}
                    onPress={() => handleDeleteMember(member.id)}
                  >
                    <Ionicons name="trash-outline" size={20} color="#FF3B30" />
                  </TouchableOpacity>
                </View>

                {/* Action Buttons */}
                <View style={styles.memberActionButtons}>
                  <TouchableOpacity 
                    style={styles.actionButton}
                    onPress={() => handleCallMember(member.phone)}
                  >
                    <Ionicons name="call" size={16} color="#007AFF" />
                    <Text style={styles.actionButtonText}>Ara</Text>
                  </TouchableOpacity>
                  <TouchableOpacity 
                    style={styles.actionButton}
                    onPress={() => handleLocationRequest(member.name)}
                  >
                    <Ionicons name="location" size={16} color="#007AFF" />
                    <Text style={styles.actionButtonText}>Konum Sor</Text>
                  </TouchableOpacity>
                </View>
              </View>
            ))}

            <TouchableOpacity style={styles.addMemberButton}>
              <Ionicons name="add" size={20} color="#007AFF" />
              <Text style={styles.addMemberText}>Yeni Aile Üyesi Ekle</Text>
            </TouchableOpacity>

            <View style={styles.modalBottomSpacing} />
          </ScrollView>
        </View>
      </Modal>
    </View>
  );
};

