import React from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity, Linking } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import Header from '../components/Header';

const AboutScreen = ({ navigation }) => {
  const { theme } = useTheme();
  const { t } = useLanguage();

  const appInfo = {
    name: 'Afet.net',
    version: '1.0.0',
    buildNumber: '100',
    releaseDate: '2024',
    developer: 'Afet.net Geliştirme Ekibi',
    description: 'Afet durumlarında hayat kurtaran mobil uygulama'
  };

  const features = [
    {
      icon: 'notifications',
      title: 'Anlık Bildirimler',
      description: 'Afet uyarıları ve güncellemeleri anında alın'
    },
    {
      icon: 'map',
      title: 'İnteraktif Harita',
      description: 'Afet bölgelerini harita üzerinde görüntüleyin'
    },
    {
      icon: 'call',
      title: 'Acil Durum Butonu',
      description: 'Tek dokunuşla yardım çağırın'
    },
    {
      icon: 'people',
      title: 'Aile Takibi',
      description: 'Sevdiklerinizin güvenliğini takip edin'
    },
    {
      icon: 'information-circle',
      title: 'Bilgi Merkezi',
      description: 'Afet öncesi ve sonrası bilgilere erişin'
    },
    {
      icon: 'settings',
      title: 'Kişiselleştirme',
      description: 'Uygulamayı ihtiyaçlarınıza göre ayarlayın'
    }
  ];

  const teamMembers = [
    {
      name: 'Geliştirme Ekibi',
      role: 'Mobil Uygulama Geliştirme',
      description: 'React Native ve modern teknolojiler kullanarak güvenilir çözümler üretiyoruz.'
    },
    {
      name: 'Afet Uzmanları',
      role: 'Afet Yönetimi Danışmanlığı',
      description: 'AFAD ve ilgili kurumlarla işbirliği halinde çalışıyoruz.'
    },
    {
      name: 'UX/UI Tasarım',
      role: 'Kullanıcı Deneyimi Tasarımı',
      description: 'Acil durumlarda kolay kullanılabilir arayüzler tasarlıyoruz.'
    }
  ];

  const socialLinks = [
    {
      name: 'Web Sitesi',
      icon: 'globe',
      url: 'https://www.afet.net',
      description: 'Resmi web sitemizi ziyaret edin'
    },
    {
      name: 'E-posta',
      icon: 'mail',
      url: 'mailto:info@afet.net',
      description: 'Bizimle iletişime geçin'
    },
    {
      name: 'Destek',
      icon: 'help-circle',
      url: 'mailto:destek@afet.net',
      description: 'Teknik destek alın'
    }
  ];

  const handleLinkPress = (url) => {
    Linking.openURL(url).catch(err => console.error('Link açılamadı:', err));
  };

  const renderFeature = (feature, index) => (
    <View key={index} style={[styles.featureItem, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}>
      <View style={[styles.featureIcon, { backgroundColor: theme.colors.primary + '20' }]}>
        <Ionicons name={feature.icon} size={24} color={theme.colors.primary} />
      </View>
      <View style={styles.featureContent}>
        <Text style={[styles.featureTitle, { color: theme.colors.text }]}>{feature.title}</Text>
        <Text style={[styles.featureDescription, { color: theme.colors.secondaryText }]}>{feature.description}</Text>
      </View>
    </View>
  );

  const renderTeamMember = (member, index) => (
    <View key={index} style={[styles.teamMember, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}>
      <Text style={[styles.memberName, { color: theme.colors.text }]}>{member.name}</Text>
      <Text style={[styles.memberRole, { color: theme.colors.primary }]}>{member.role}</Text>
      <Text style={[styles.memberDescription, { color: theme.colors.secondaryText }]}>{member.description}</Text>
    </View>
  );

  const renderSocialLink = (link, index) => (
    <TouchableOpacity
      key={index}
      style={[styles.socialLink, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}
      onPress={() => handleLinkPress(link.url)}
    >
      <View style={styles.socialLeft}>
        <Ionicons name={link.icon} size={24} color={theme.colors.primary} />
        <View style={styles.socialText}>
          <Text style={[styles.socialName, { color: theme.colors.text }]}>{link.name}</Text>
          <Text style={[styles.socialDescription, { color: theme.colors.secondaryText }]}>{link.description}</Text>
        </View>
      </View>
      <Ionicons name="chevron-forward" size={20} color={theme.colors.secondaryText} />
    </TouchableOpacity>
  );

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <Header title="Hakkında" navigation={navigation} />

      <ScrollView style={styles.content} showsVerticalScrollIndicator={false}>
        {/* Uygulama Bilgileri */}
        <View style={styles.section}>
          <View style={[styles.appHeader, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}>
            <View style={[styles.appLogo, { backgroundColor: theme.colors.primary }]}>
              <Text style={[styles.appLogoText, { color: theme.colors.onPrimary }]}>A</Text>
              <Ionicons name="home" size={16} color={theme.colors.onPrimary} style={styles.houseIcon} />
            </View>
            <View style={styles.appInfo}>
              <Text style={[styles.appName, { color: theme.colors.text }]}>{appInfo.name}</Text>
              <Text style={[styles.appDescription, { color: theme.colors.secondaryText }]}>{appInfo.description}</Text>
              <View style={styles.versionInfo}>
                <Text style={[styles.versionText, { color: theme.colors.secondaryText }]}>Versiyon {appInfo.version}</Text>
                <Text style={[styles.buildText, { color: theme.colors.tertiaryText }]}>Build {appInfo.buildNumber}</Text>
              </View>
            </View>
          </View>
        </View>

        {/* Özellikler */}
        <View style={styles.section}>
          <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>Özellikler</Text>
          {features.map(renderFeature)}
        </View>

        {/* Geliştirme Ekibi */}
        <View style={styles.section}>
          <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>Geliştirme Ekibi</Text>
          <Text style={[styles.sectionDescription, { color: theme.colors.secondaryText }]}>
            Afet.net, afet yönetimi alanında uzman bir ekip tarafından geliştirilmiştir.
          </Text>
          {teamMembers.map(renderTeamMember)}
        </View>

        {/* Misyon */}
        <View style={styles.section}>
          <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>Misyonumuz</Text>
          <View style={[styles.missionBox, { backgroundColor: theme.colors.cardBackground, borderColor: theme.colors.border }]}>
            <Text style={[styles.missionText, { color: theme.colors.text }]}>
              Afet durumlarında insanların güvenliğini sağlamak ve hayat kurtarmak için teknolojinin gücünden yararlanıyoruz. 
              Hızlı bilgi paylaşımı, etkili koordinasyon ve kullanıcı dostu tasarım ile afet yönetiminde yeni standartlar belirliyoruz.
            </Text>
          </View>
        </View>

        {/* İletişim */}
        <View style={[styles.section, styles.lastSection]}>
          <Text style={[styles.sectionTitle, { color: theme.colors.text }]}>İletişim</Text>
          {socialLinks.map(renderSocialLink)}
        </View>

        {/* Telif Hakkı */}
        <View style={styles.copyright}>
          <Text style={[styles.copyrightText, { color: theme.colors.tertiaryText }]}>
            © {appInfo.releaseDate} {appInfo.developer}
          </Text>
          <Text style={[styles.copyrightText, { color: theme.colors.tertiaryText }]}>
            Tüm hakları saklıdır.
          </Text>
        </View>
      </ScrollView>
    </View>
  );
};

