import React, { useEffect, useRef } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Animated,
  Dimensions,
  ActivityIndicator,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';

const { width, height } = Dimensions.get('window');

const SplashScreen = ({ onFinish }) => {
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const scaleAnim = useRef(new Animated.Value(0.8)).current;
  const slideAnim = useRef(new Animated.Value(50)).current;
  const rotateAnim = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    // Ana animasyon sekansı
    Animated.sequence([
      // Logo ve başlık animasyonu
      Animated.parallel([
        Animated.timing(fadeAnim, {
          toValue: 1,
          duration: 1000,
          useNativeDriver: true,
        }),
        Animated.timing(scaleAnim, {
          toValue: 1,
          duration: 1000,
          useNativeDriver: true,
        }),
        Animated.timing(slideAnim, {
          toValue: 0,
          duration: 1000,
          useNativeDriver: true,
        }),
      ]),
      // Dönen animasyon
      Animated.timing(rotateAnim, {
        toValue: 1,
        duration: 1500,
        useNativeDriver: true,
      }),
    ]).start(() => {
      // Animasyon tamamlandıktan sonra ana ekrana geç
      setTimeout(() => {
        onFinish();
      }, 500);
    });
  }, []);

  const spin = rotateAnim.interpolate({
    inputRange: [0, 1],
    outputRange: ['0deg', '360deg'],
  });

  return (
    <View style={styles.container}>
      <Animated.View
        style={[
          styles.logoContainer,
          {
            opacity: fadeAnim,
            transform: [
              { scale: scaleAnim },
              { translateY: slideAnim },
            ],
          },
        ]}
      >
        {/* İçişleri Bakanlığı Logosu */}
        <View style={styles.ministryLogoContainer}>
          <View style={styles.ministryLogo}>
            <View style={styles.ministryCircle}>
              <View style={styles.ministryStar}>
                <Text style={styles.ministryStarText}>★</Text>
              </View>
              <View style={styles.ministryBook}>
                <View style={styles.bookLine} />
                <View style={styles.bookLine} />
                <View style={styles.bookLine} />
              </View>
            </View>
          </View>
        </View>

        <Animated.View
          style={[
            styles.textContainer,
            {
              opacity: fadeAnim,
              transform: [{ translateY: slideAnim }],
            },
          ]}
        ><View style={styles.textContainer}>
          <Text style={styles.mainTitle}>TÜRKİYE CUMHURİYETİ</Text>
          <Text style={styles.subtitle}>İÇİŞLERİ BAKANLIĞI</Text>
        </View>

        {/* AFAD Logosu */}
        <View style={styles.afadContainer}>
          <Text style={styles.afadTitle}>AFAD</Text>
          <Text style={styles.afadSubtitle}>acil</Text>
          <Text style={styles.afadDescription}>AFET VE ACİL DURUM</Text>
          <Text style={styles.afadDescription}>YÖNETİMİ BAŞKANLIĞI</Text>
        </View></Animated.View>
      </Animated.View>

      <Animated.View
        style={[
          styles.loadingContainer,
          {
            opacity: fadeAnim,
          },
        ]}
      >
        <ActivityIndicator size="large" color="#ffffff" />
        <Text style={styles.loadingText}>Yükleniyor...</Text>
      </Animated.View>

      <Animated.View
        style={[
          styles.bottomContainer,
          {
            opacity: fadeAnim,
          },
        ]}
      >
        <Text style={styles.appName}>AfetNET</Text>
        <Text style={styles.version}>v1.0.0</Text>
      </Animated.View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E53E3E',
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },
  logoContainer: {
    alignItems: 'center',
    marginBottom: 40,
  },
  ministryLogoContainer: {
    alignItems: 'center',
    marginBottom: 30,
  },
  ministryLogo: {
    alignItems: 'center',
  },
  ministryCircle: {
    width: 100,
    height: 100,
    borderRadius: 50,
    backgroundColor: '#FFFFFF',
    justifyContent: 'center',
    alignItems: 'center',
    borderWidth: 3,
    borderColor: '#FFFFFF',
    position: 'relative',
  },
  ministryStar: {
    position: 'absolute',
    top: 15,
  },
  ministryStarText: {
    fontSize: 20,
    color: '#E53E3E',
    fontWeight: 'bold',
  },
  ministryBook: {
    marginTop: 25,
    alignItems: 'center',
  },
  bookLine: {
    width: 30,
    height: 2,
    backgroundColor: '#E53E3E',
    marginVertical: 2,
  },
  textContainer: {
    alignItems: 'center',
  },
  mainTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#ffffff',
    textAlign: 'center',
    marginBottom: 8,
    letterSpacing: 1,
  },
  subtitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#ffffff',
    textAlign: 'center',
    marginBottom: 40,
    letterSpacing: 1,
  },
  afadContainer: {
    alignItems: 'center',
    backgroundColor: '#F7F7F7',
    paddingVertical: 30,
    paddingHorizontal: 40,
    borderRadius: 10,
    marginTop: 20,
  },
  afadTitle: {
    fontSize: 48,
    fontWeight: 'bold',
    color: '#2B6CB0',
    textAlign: 'center',
    marginBottom: 5,
  },
  afadSubtitle: {
    fontSize: 32,
    fontWeight: 'normal',
    color: '#E53E3E',
    textAlign: 'center',
    fontStyle: 'italic',
    marginBottom: 20,
  },
  afadDescription: {
    fontSize: 14,
    fontWeight: '600',
    color: '#4A5568',
    textAlign: 'center',
    marginBottom: 2,
    letterSpacing: 0.5,
  },
  loadingContainer: {
    position: 'absolute',
    bottom: 120,
    alignItems: 'center',
  },
  loadingText: {
    fontSize: 16,
    color: 'rgba(255, 255, 255, 0.8)',
    marginTop: 10,
    fontWeight: '500',
  },
  bottomContainer: {
    position: 'absolute',
    bottom: 40,
    alignItems: 'center',
  },
  appName: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#ffffff',
    marginBottom: 5,
  },
  version: {
    fontSize: 14,
    color: 'rgba(255, 255, 255, 0.7)',
    fontWeight: '400',
  },
});

export default SplashScreen;
