import React, { useState, useEffect, useContext } from 'react';
import {
  View,
  Text,
  StyleSheet,
  Alert,
  TouchableOpacity,
  ActivityIndicator,
  Platform,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import * as Location from 'expo-location';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { ThemeContext } from '../context/ThemeContext';
import { LanguageContext } from '../context/LanguageContext';
import earthquakeService from '../services/earthquakeService';

// Web için Leaflet import'ları
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const MapScreen = () => {
  const { t } = useLanguage();
  const { theme } = useTheme();
  const insets = useSafeAreaInsets();
  const { width: screenWidth, height: screenHeight } = Dimensions.get('window');
  const mapRef = useRef(null);

  // State
  const [userLocation, setUserLocation] = useState(null);
  const [earthquakes, setEarthquakes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [mapRegion, setMapRegion] = useState({
    latitude: 39.9334, // Turkey center
    longitude: 32.8597,
    latitudeDelta: 8.0,
    longitudeDelta: 8.0,
  });

  useEffect(() => {
    requestLocationPermission();
    fetchEarthquakeData();

    // Set up interval to fetch earthquake data every minute
    const interval = setInterval(fetchEarthquakeData, 60000);

    return () => clearInterval(interval);
  }, []);


  const requestLocationPermission = async () => {
    try {
      const { status } = await Location.requestForegroundPermissionsAsync();
      if (status === 'granted') {
        getCurrentLocation();
      }
    } catch (error) {
      console.error('Location permission error:', error);
    }
  };

  const getCurrentLocation = async () => {
    try {
      const location = await Location.getCurrentPositionAsync({
        accuracy: Location.Accuracy.High,
      });
      setUserLocation({
        latitude: location.coords.latitude,
        longitude: location.coords.longitude,
      });

      // Update map region to user location
      setMapRegion({
        latitude: location.coords.latitude,
        longitude: location.coords.longitude,
        latitudeDelta: 0.05,
        longitudeDelta: 0.05,
      });
    } catch (error) {
      console.error('Get location error:', error);
    }
  };

  const fetchEarthquakeData = async () => {
    try {
      setIsLoading(true);

      // AFAD API'sinden deprem verilerini çek
      const earthquakeData = await earthquakeService.getLatestEarthquakes(50, 2.0); // Minimum 2.0 büyüklük

      // Veriyi harita için uygun formata dönüştür
      const formattedEarthquakes = earthquakeData.map(eq => ({
        id: eq.id,
        latitude: eq.coordinates.latitude,
        longitude: eq.coordinates.longitude,
        magnitude: eq.magnitude,
        depth: eq.depth,
        location: eq.location,
        time: eq.time.toISOString(),
        severity: eq.severity,
        timeAgo: eq.distance,
        color: eq.color
      }));

      setEarthquakes(formattedEarthquakes);
      setIsLoading(false);

      console.log(`${formattedEarthquakes.length} deprem verisi yüklendi`);
    } catch (error) {
      console.error('Deprem verileri yüklenirken hata:', error);

      // Hata durumunda fallback mock data kullan
      const fallbackData = [
        {
          id: 'fallback-1',
          latitude: 40.7589,
          longitude: 29.9133,
          magnitude: 4.2,
          depth: 8.5,
          location: 'Marmara Denizi',
          time: new Date().toISOString(),
          severity: 'Düşük',
          timeAgo: 'Az önce',
          color: '#34C759'
        },
        {
          id: 'fallback-2',
          latitude: 38.4237,
          longitude: 27.1428,
          magnitude: 3.8,
          depth: 12.3,
          location: 'İzmir',
          time: new Date(Date.now() - 1800000).toISOString(),
          severity: 'Çok Düşük',
          timeAgo: '30 dk önce',
          color: '#007AFF'
        }
      ];

      setEarthquakes(fallbackData);
      setIsLoading(false);

      Alert.alert(
        'Uyarı',
        'Deprem verileri yüklenirken bir sorun oluştu. Örnek veriler gösteriliyor.',
        [{ text: 'Tamam' }]
      );
    }
  };

  const getMarkerColor = (magnitude) => {
    if (magnitude >= 5.0) return '#FF3B30'; // Red for strong earthquakes
    if (magnitude >= 4.0) return '#FF9500'; // Orange for moderate earthquakes
    if (magnitude >= 3.0) return '#FFCC00'; // Yellow for light earthquakes
    return '#34C759'; // Green for very light earthquakes
  };

  const getMarkerSize = (magnitude) => {
    return Math.max(20, magnitude * 8);
  };

  const centerOnUserLocation = () => {
    if (userLocation && mapRef.current) {
      mapRef.current.animateToRegion({
        latitude: userLocation.latitude,
        longitude: userLocation.longitude,
        latitudeDelta: 0.05,
        longitudeDelta: 0.05,
      }, 1000);
    }
  };

  const showEarthquakeDetails = (earthquake) => {
    const timeAgo = earthquake.timeAgo || Math.floor((Date.now() - new Date(earthquake.time).getTime()) / 60000) + ' dk önce';
    const severity = earthquake.severity || 'Bilinmiyor';

    Alert.alert(
      'Deprem Detayları',
      `Konum: ${earthquake.location}\nBüyüklük: ${earthquake.magnitude}\nDerinlik: ${earthquake.depth} km\nŞiddet: ${severity}\nZaman: ${timeAgo}`,
      [
        { text: 'Tamam' },
        {
          text: 'Konuma Git',
          onPress: () => {
            if (mapRef.current) {
              mapRef.current.animateToRegion({
                latitude: earthquake.latitude,
                longitude: earthquake.longitude,
                latitudeDelta: 0.1,
                longitudeDelta: 0.1,
              }, 1000);
            }
          }
        }
      ]
    );
  };

  return (
    <View style={[styles.container, { paddingTop: insets.top }]}>
      {/* Header */}
      <View style={[styles.header, { backgroundColor: theme.colors.cardBackground }]}>
        <Text style={[styles.headerTitle, { color: theme.colors.text }]}>Deprem Haritası</Text>
        <TouchableOpacity 
          style={styles.refreshButton}
          onPress={fetchEarthquakeData}
        >
          <Ionicons name="refresh" size={20} color={theme.colors.text} />
        </TouchableOpacity>
      </View>

      {/* Map */}
      <View style={styles.mapContainer}>
        <MapView
          ref={mapRef}
          style={styles.map}
          provider={PROVIDER_DEFAULT}
          region={mapRegion}
          onRegionChangeComplete={setMapRegion}
          showsUserLocation={true}
          showsMyLocationButton={false}
          mapType="standard"
        >
          {/* User Location Marker */}
          {userLocation && (
            <Marker
              coordinate={userLocation}
              title="Konumunuz"
              description="Mevcut konumunuz"
            >
              <View style={styles.userLocationMarker}>
                <Ionicons name="person" size={16} color="#FFFFFF" />
              </View>
            </Marker>
          )}

          {/* Earthquake Markers */}
          {earthquakes.map((earthquake) => (
            <Marker
              key={earthquake.id}
              coordinate={{
                latitude: earthquake.latitude,
                longitude: earthquake.longitude,
              }}
              onPress={() => showEarthquakeDetails(earthquake)}
            >
              <View 
                 style={[
                   styles.earthquakeMarker,
                   {
                     backgroundColor: earthquake.color || getMarkerColor(earthquake.magnitude),
                     width: getMarkerSize(earthquake.magnitude),
                     height: getMarkerSize(earthquake.magnitude),
                   }
                 ]}
               >
                 <Text style={styles.magnitudeText}>{earthquake.magnitude}</Text>
               </View>
            </Marker>
          ))}
        </MapView>

        {/* Loading Overlay */}
        {isLoading && (
          <View style={styles.loadingOverlay}>
            <ActivityIndicator size="large" color="#007AFF" />
            <Text style={styles.loadingText}>Deprem verileri yükleniyor...</Text>
          </View>
        )}

        {/* Control Buttons */}
        <View style={styles.controlButtons}>
          <TouchableOpacity 
            style={[styles.controlButton, { backgroundColor: theme.colors.cardBackground }]}
            onPress={centerOnUserLocation}
          >
            <Ionicons name="locate" size={20} color={theme.colors.text} />
          </TouchableOpacity>
        </View>

        {/* Legend */}
        <View style={[styles.legend, { backgroundColor: theme.colors.cardBackground }]}>
          <Text style={[styles.legendTitle, { color: theme.colors.text }]}>Deprem Büyüklüğü</Text>
          <View style={styles.legendItems}>
            <View style={styles.legendItem}>
              <View style={[styles.legendColor, { backgroundColor: '#34C759' }]} />
              <Text style={[styles.legendText, { color: theme.colors.text }]}>{'< 3.0'}</Text>
            </View>
            <View style={styles.legendItem}>
              <View style={[styles.legendColor, { backgroundColor: '#FFCC00' }]} />
              <Text style={[styles.legendText, { color: theme.colors.text }]}>3.0-4.0</Text>
            </View>
            <View style={styles.legendItem}>
              <View style={[styles.legendColor, { backgroundColor: '#FF9500' }]} />
              <Text style={[styles.legendText, { color: theme.colors.text }]}>4.0-5.0</Text>
            </View>
            <View style={styles.legendItem}>
              <View style={[styles.legendColor, { backgroundColor: '#FF3B30' }]} />
              <Text style={[styles.legendText, { color: theme.colors.text }]}>≥ 5.0</Text>
            </View>
          </View>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
