import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  Modal
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useTheme } from '../contexts/ThemeContext';
import { useLanguage } from '../contexts/LanguageContext';
import { useNotification } from '../contexts/NotificationContext';
import Header from '../components/Header';
import DisasterVisualization from '../components/DisasterVisualization';
import NotificationBox from '../components/NotificationBox';

const { width: screenWidth } = Dimensions.get('window');

const NotificationsScreen = ({ navigation }) => {
  const insets = useSafeAreaInsets();
  const { theme } = useTheme();
  const { t } = useLanguage();
  const { 
    notifications, 
    unreadCount, 
    markAsRead, 
    markAllAsRead, 
    clearAllNotifications,
    removeNotification 
  } = useNotification();

  const [activeTab, setActiveTab] = useState('disasters'); // 'disasters' veya 'notifications'
  const [selectedNotification, setSelectedNotification] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);

  const handleNotificationPress = (notification) => {
    markAsRead(notification.id);
    setSelectedNotification(notification);
    setModalVisible(true);

    // Bildirim türüne göre özel işlemler
    if (notification.onPress) {
      notification.onPress(notification);
    }
  };

  const closeModal = () => {
    setModalVisible(false);
    setSelectedNotification(null);
  };

  const handleNotificationClose = (notificationId) => {
    removeNotification(notificationId);
  };

  const renderTabButton = (tabKey, title, icon) => {
    const isActive = activeTab === tabKey;
    return (
      <TouchableOpacity
        style={[
          styles.tabButton,
          isActive && styles.activeTab
        ]}
        onPress={() => setActiveTab(tabKey)}
      >
        <Ionicons
          name={icon}
          size={18}
          color={isActive ? theme.colors.primary : theme.colors.textSecondary}
        />
        <Text style={[
          styles.tabText,
          { color: isActive ? theme.colors.primary : theme.colors.textSecondary }
        ]}>
          {title}
        </Text>
        {tabKey === 'notifications' && unreadCount > 0 && (
          <View style={[styles.badge, { backgroundColor: theme.colors.primary }]}>
            <Text style={styles.badgeText}>
              {unreadCount > 9 ? '9+' : unreadCount}
            </Text>
          </View>
        )}
      </TouchableOpacity>
    );
  };

  const renderNotificationsList = () => {
    if (notifications.length === 0) {
      return (
        <View style={styles.emptyContainer}>
          <Ionicons
            name="notifications-off"
            size={48}
            color={theme.colors.textSecondary}
          />
          <Text style={[styles.emptyText, { color: theme.colors.text }]}>
            Henüz bildirim yok
          </Text>
        </View>
      );
    }

    return (
      <ScrollView style={styles.notificationsList} showsVerticalScrollIndicator={false}>
        {unreadCount > 0 && (
          <TouchableOpacity
            style={[styles.markAllButton, { backgroundColor: theme.colors.primary }]}
            onPress={markAllAsRead}
          >
            <Text style={styles.markAllText}>Tümünü Okundu İşaretle</Text>
          </TouchableOpacity>
        )}

        {notifications.map((notification, index) => (
          <TouchableOpacity
            key={notification.id}
            style={[
              styles.notificationItem,
              { backgroundColor: theme.colors.cardBackground },
              !notification.isRead && styles.unreadNotification
            ]}
            onPress={() => handleNotificationPress(notification)}
          >
            <View style={styles.notificationContent}>
              <Ionicons
                name={getNotificationIcon(notification.type)}
                size={18}
                color={getNotificationColor(notification.type)}
                style={styles.notificationIcon}
              />
              <View style={styles.textContent}>
                <Text style={[styles.notificationTitle, { color: theme.colors.text }]}>
                  {notification.title}
                </Text>
                <Text style={[styles.notificationMessage, { color: theme.colors.textSecondary }]}>
                  {notification.message}
                </Text>
                <Text style={[styles.notificationTime, { color: theme.colors.textSecondary }]}>
                  {formatTime(notification.timestamp)}
                </Text>
              </View>
            </View>
          </TouchableOpacity>
        ))}
      </ScrollView>
    );
  };

  // Yardımcı fonksiyonlar
  const getNotificationIcon = (type) => {
    switch (type) {
      case 'success': return 'checkmark-circle';
      case 'error': return 'close-circle';
      case 'warning': return 'warning';
      case 'earthquake': return 'earth';
      case 'fire': return 'flame';
      case 'flood': return 'water';
      case 'storm': return 'thunderstorm';
      case 'landslide': return 'triangle';
      case 'emergency': return 'alert-circle';
      default: return 'information-circle';
    }
  };

  const getNotificationColor = (type) => {
    switch (type) {
      case 'success': return theme.colors.success;
      case 'error': return theme.colors.danger;
      case 'warning': return theme.colors.warning;
      case 'earthquake': return '#FF6B35';
      case 'fire': return '#FF4500';
      case 'flood': return '#1E90FF';
      case 'storm': return '#9370DB';
      case 'landslide': return '#8B4513';
      case 'emergency': return '#DC143C';
      default: return theme.colors.primary;
    }
  };

  const formatTime = (timestamp) => {
    const now = new Date();
    const notificationTime = new Date(timestamp);
    const diffInMinutes = Math.floor((now - notificationTime) / (1000 * 60));

    if (diffInMinutes < 1) return 'Şimdi';
    if (diffInMinutes < 60) return `${diffInMinutes}dk önce`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}sa önce`;
    return notificationTime.toLocaleDateString('tr-TR');
  };

  const renderNotificationModal = () => {
    if (!selectedNotification) return null;

    return (
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={closeModal}
      >
        <View style={styles.modalOverlay}>
          <View style={[styles.modalContent, { backgroundColor: theme.colors.cardBackground }]}>
            <View style={styles.modalHeader}>
              <View style={styles.modalTitleContainer}>
                <Ionicons
                  name={getNotificationIcon(selectedNotification.type)}
                  size={24}
                  color={getNotificationColor(selectedNotification.type)}
                  style={styles.modalIcon}
                />
                <Text style={[styles.modalTitle, { color: theme.colors.text }]}>
                  {selectedNotification.title}
                </Text>
              </View>
              <TouchableOpacity onPress={closeModal} style={styles.closeButton}>
                <Ionicons name="close" size={24} color={theme.colors.textSecondary} />
              </TouchableOpacity>
            </View>

            <ScrollView style={styles.modalBody}>
              <Text style={[styles.modalMessage, { color: theme.colors.text }]}>
                {selectedNotification.message}
              </Text>

              <View style={styles.modalDetails}>
                <View style={styles.detailRow}>
                  <Text style={[styles.detailLabel, { color: theme.colors.textSecondary }]}>Tür:</Text>
                  <Text style={[styles.detailValue, { color: theme.colors.text }]}>
                    {getNotificationTypeText(selectedNotification.type)}
                  </Text>
                </View>

                <View style={styles.detailRow}>
                  <Text style={[styles.detailLabel, { color: theme.colors.textSecondary }]}>Zaman:</Text>
                  <Text style={[styles.detailValue, { color: theme.colors.text }]}>
                    {formatTime(selectedNotification.timestamp)}
                  </Text>
                </View>

                {selectedNotification.location && (
                  <View style={styles.detailRow}>
                    <Text style={[styles.detailLabel, { color: theme.colors.textSecondary }]}>Konum:</Text>
                    <Text style={[styles.detailValue, { color: theme.colors.text }]}>
                      {selectedNotification.location}
                    </Text>
                  </View>
                )}

                {selectedNotification.severity && (
                  <View style={styles.detailRow}>
                    <Text style={[styles.detailLabel, { color: theme.colors.textSecondary }]}>Önem Derecesi:</Text>
                    <Text style={[styles.detailValue, { color: theme.colors.text }]}>
                      {selectedNotification.severity}
                    </Text>
                  </View>
                )}
              </View>
            </ScrollView>
          </View>
        </View>
      </Modal>
    );
  };

  const getNotificationTypeText = (type) => {
    switch (type) {
      case 'earthquake': return 'Deprem';
      case 'fire': return 'Yangın';
      case 'flood': return 'Sel';
      case 'storm': return 'Fırtına';
      case 'landslide': return 'Heyelan';
      case 'emergency': return 'Acil Durum';
      case 'warning': return 'Uyarı';
      case 'success': return 'Başarılı';
      case 'error': return 'Hata';
      default: return 'Bilgi';
    }
  };

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: theme.colors.background,
    },
    tabContainer: {
      flexDirection: 'row',
      paddingHorizontal: 16,
      paddingVertical: 8,
      borderBottomWidth: 1,
      borderBottomColor: theme.colors.border || '#E5E5EA',
    },
    tabButton: {
      flex: 1,
      flexDirection: 'row',
      alignItems: 'center',
      justifyContent: 'center',
      paddingVertical: 12,
      paddingHorizontal: 8,
      position: 'relative',
    },
    activeTab: {
      borderBottomWidth: 2,
      borderBottomColor: theme.colors.primary,
    },
    tabText: {
      fontSize: 14,
      fontWeight: '500',
      marginLeft: 6,
    },
    badge: {
      position: 'absolute',
      top: 4,
      right: 8,
      borderRadius: 8,
      minWidth: 16,
      height: 16,
      alignItems: 'center',
      justifyContent: 'center',
      paddingHorizontal: 4,
    },
    badgeText: {
      color: '#FFFFFF',
      fontSize: 10,
      fontWeight: '600',
    },
    content: {
      flex: 1,
    },
    notificationsList: {
      flex: 1,
      paddingHorizontal: 16,
    },
    markAllButton: {
      paddingVertical: 10,
      paddingHorizontal: 16,
      borderRadius: 8,
      marginVertical: 12,
      alignItems: 'center',
    },
    markAllText: {
      color: '#FFFFFF',
      fontSize: 14,
      fontWeight: '500',
    },
    notificationItem: {
      paddingVertical: 12,
      paddingHorizontal: 16,
      marginVertical: 2,
      borderRadius: 8,
      borderLeftWidth: 3,
      borderLeftColor: 'transparent',
      minHeight: 80,
      maxHeight: 120,
    },
    unreadNotification: {
      borderLeftColor: theme.colors.primary,
    },
    notificationContent: {
